INSERT INTO `nn_sanpham` (`idSP`, `idLoai`, `TenSP`, `Gia`, `MoTa`, `ChiTiet`, `UrlHinh`, `NgayDang`, `TonKho`, `GhiChu`, `SoLanMua`, `SoLanXem`, `AnHien`) VALUES
(1, 1, 'Nokia N8', '11449000.0000', '    * Hệ điều hành Symbian\r\n    * Máy ảnh 12 MP, máy ảnh phụ VGA\r\n    * Mạng 3G với HSDPA, 7.2 Mbps\r\n    * HSUPA, 5.76 Mbps\r\n    * Wi-Fi 802.11 b/g, UPnP technology\r\n    * Document viewer (Word, Excel, PowerPoint, PDF)\r\n    * Đài FM, tích hợp máy phát FM\r\n    * Jack tai nghe 3.5mm, Out Tivi với HDMI and composite, Flash Lite v4.0 ', NULL, 'Nokia_N81.jpg', '0000-00-00', 100, NULL, 0, 18, 1),
(2, 1, 'Nokia N900', '9899000.0000', '<ul class="item_list" style="padding-left: 15px; margin: 0px;">\r\n<li>Điện thoại truyền th&ocirc;ng đa phương tiện</li>\r\n<li>Camera 5.0 MP, Camera phụ VGA</li>\r\n<li>Kết nối mạng 3.5G với tốc độ l&ecirc;n đến 10 Mbps</li>\r\n<li>Hỗ trợ A-GPS, OVI Maps</li>\r\n<li>Kết nối Wifi tốc độ cao, Bluetooth, USB</li>\r\n<li>Document viewer (Word, Excel, PowerPoint, PDF)</li>\r\n<li>Chỉnh sửa h&igrave;nh ảnh</li>\r\n<li>Adobe Flash Player 9.4</li>\r\n<li>Out Tivi, v&agrave; jack loa 3.5 mm </li>\r\n</ul>', NULL, 'N900.jpg', '0000-00-00', 100, NULL, 0, 6, 1),
(4, 1, 'Nokia 6700 classic gold', '9799000.0000', '<ul class="item_list" style="padding-left: 15px; margin: 0px;">\r\n<li>Điện thoại 3G với thiết kế thời trang với vỏ tr&aacute;ng 18 carat v&agrave;ng sang trọng</li>\r\n<li>Camera 5 MP, hỗ trợ flash, tự động lấy n&eacute;t</li>\r\n<li>Nghe nhạc/xem phim nhiều định dạng MP3/M4A/AAC/eAAC+/WMA</li>\r\n<li>Đ&agrave;i FM, Bluetooth, kết nối 3.5G với tốc độ truyền dữ liệu 10 Mbps</li>\r\n<li>Định vị GPS, Bản đồ Nokia</li>\r\n<li>Flash Lite 3 </li>\r\n</ul>', NULL, '6700_gold.jpg', '0000-00-00', 100, NULL, 0, 3, 1),
(5, 1, 'Nokia N97', '9549000.0000', '<ul class="item_list" style="padding-left: 15px; margin: 0px;">\r\n<li>Kiểu trượt cảm ứng s&agrave;nh điệu</li>\r\n<li>Camera 5 MP, camera phụ thực hiện videocall</li>\r\n<li>Bộ nhớ 32 GB, RAM 128 MB</li>\r\n<li>Chức năng định vị GPS</li>\r\n<li>Chức năng Out-Tivi, jack tai nghe 3.5mm</li>\r\n<li>Pocket Office (Word, Excel, PowerPoint, PDF viewer)</li>\r\n<li>Chỉnh sửa h&igrave;nh ảnh v&agrave; Video</li>\r\n<li>Flash Lite 3</li>\r\n<li>Kết nối 3G, Wifi, Bluetooth </li>\r\n</ul>', NULL, 'n97.jpg', '0000-00-00', 100, NULL, 0, 0, 1),
(6, 1, 'Nokia N97 mini Navi', '9049000.0000', '<ul class="item_list" style="padding-left: 15px; margin: 0px;">\r\n<li>Kiểu trượt cảm ứng s&agrave;nh điệu</li>\r\n<li>Camera 5 MP, camera phụ thực hiện videocall</li>\r\n<li>Bộ nhớ 32 GB, RAM 128 MB</li>\r\n<li>Chức năng định vị GPS</li>\r\n<li>Chức năng Out-Tivi, jack tai nghe 3.5mm</li>\r\n<li>Pocket Office (Word, Excel, PowerPoint, PDF viewer)</li>\r\n<li>Chỉnh sửa h&igrave;nh ảnh v&agrave; Video</li>\r\n<li>Flash Lite 3</li>\r\n<li>Kết nối 3G, Wifi, Bluetooth </li>\r\n</ul>', NULL, 'N97_mini.jpg', '0000-00-00', 100, NULL, 0, 0, 1),
(7, 1, 'Nokia C7', '8679000.0000', '<ul class="item_list" style="padding-left: 15px; margin: 0px;">\r\n<li>Hệ điều h&agrave;nh Symbian</li>\r\n<li>M&agrave;n h&igrave;nh cảm ứng điện dung</li>\r\n<li>M&aacute;y ảnh 8 MP với Flash k&eacute;p</li>\r\n<li>M&aacute;y nghe nhạc, xem phim nhiều định dạng</li>\r\n<li>Mạng 3G, kết nối Wifi, GPS, Bluetooth</li>\r\n<li>Xem văn bản (Word, Excel, PowerPoint, PDF)</li>\r\n<li>Kết nối Tivi v&agrave; Web Tivi </li>\r\n</ul>', NULL, 'nokia_C7.jpg', '0000-00-00', 100, NULL, 0, 0, 1),
