INSERT INTO `sanpham` VALUES (491, 44, 1, 'BlackBerry Torch 9800 ', '    * Thiết kế kiểu trượt thanh mảnh thời trang\r\n    * Máy ảnh 5.0 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc MP3/WMA/eAAC+/FlAC/OGG\r\n    * Mạng 3G với tốc độ truyền tải 384 Kbps\r\n    * Kết nối GPS, Wifi, Bluetooth, USB\r\n    * Chỉnh sửa văn bản (Word, Exce', '2009-02-02', 15359000, 'BlackBerry_Torch_9800_b.jpg', '<h2>BlackBerry Torch 9800 ra mắt</h2>\r\n<div class="news_description">H&ocirc;m qua, trong một sự kiện ri&ecirc;ng,  RIM v&agrave; nh&agrave; mạng AT&amp;T đ&atilde; tr&igrave;nh l&agrave;ng chiếc BlackBerry Torch 9800 với  m&agrave;n h&igrave;nh chạm v&agrave; b&agrave;n ph&iacute;m trượt.</div>\r\n<div class="new_pro_detail_contet">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="BlackBerry Torch 9800 ra mắt" src="http://images.thegioididong.com/Files/2010/08/05/15228/1_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="332" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&atilde;nh đạo của RIM c&ocirc;ng bố Torch tối qua. Ảnh: <em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">BlackBerry Torch 9800 l&agrave; chiếc smartphone đầu ti&ecirc;n chạy tr&ecirc;n hệ điều h&agrave;nh BlackBerry 6 với nhiều thay đổi lớn.</p>\r\n<p class="Normal">M&aacute;y được trang bị b&agrave;n ph&iacute;m QWERTY đầy đủ b&ecirc;n dưới m&agrave;n  h&igrave;nh cảm ứng HVGA rộng 3,2 inch, độ ph&acirc;n giải 480 x 360 pixel, thiết bị  n&agrave;y mỏng 14,6 mm. Torch chạy tr&ecirc;n 3 băng tần 3G, kết nối Wi-Fi chuẩn  b/g/n, Bluetooth, v&agrave; GPS.</p>\r\n<p class="Normal">Mặt sau của thiết bị l&agrave; camare 5 Megapixel, hỗ trợ chế  độ tự động lấy n&eacute;t, quay phim ph&acirc;n giải VGA. M&aacute;y c&oacute; bộ nhớ trong 4GB,  512 MB RAM, 512 MB ROM, khe cắm thẻ hỗ trợ cho ph&eacute;p mở rộng tới 32GB.</p>\r\n<p class="Normal">BlackBerry Torch 9800 sẽ được b&aacute;n đầu tiền tại Mỹ tr&ecirc;n  mạng AT&amp;T trong ng&agrave;y 12/8 tới đ&acirc;y đi k&egrave;m hợp đồng sử dụng hai năm  sử dụng với gi&aacute; 199 USD.</p>\r\n<p class="Normal">Cũng trong h&ocirc;m qua, một chiếc BlackBerry Tourch 9800  phi&ecirc;n bản d&ugrave;ng thử đ&atilde; xuất hiện tại cửa h&agrave;ng D&acirc;u Đen ở TP HCM. Thiết kế  đẹp, sang trọng l&agrave; những ấn tượng đầu ti&ecirc;n về model n&agrave;y.</p>\r\n<p class="Normal"><img src="http://images.thegioididong.com/Files/2010/08/05/15228/434_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="480" /></p>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal"><img src="http://images.thegioididong.com/Files/2010/08/05/15228/435_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="480" /></p>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal"><img src="http://images.thegioididong.com/Files/2010/08/05/15228/436_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="480" /></p>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal"><img src="http://images.thegioididong.com/Files/2010/08/05/15228/437_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="430" /></p>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal"><img src="http://images.thegioididong.com/Files/2010/08/05/15228/438_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="394" /></p>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal"><img src="http://images.thegioididong.com/Files/2010/08/05/15228/439_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="443" /></p>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal"><img src="http://images.thegioididong.com/Files/2010/08/05/15228/440_BlackBerry-Torch-9800-ra-mat.jpg" border="1" alt="BlackBerry Torch 9800 ra mắt" width="480" height="474" /></p>\r\n<p class="Normal"><strong>Quốc Kh&aacute;nh</strong></p>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (492, 44, 1, 'BlackBerry Bold 9700 ', '    * Máy ảnh 3.15 MP, hỗ trợ LED flash\r\n    * Máy nghe nhạc MP3/WMA/AAC+\r\n    * Xem phim DivX/WMV/XviD/3gp\r\n    * Document editor (Word, Excel, PowerPoint, PDF)\r\n    * Mạng 3G, kết nối Wifi \r\n\r\n', '2009-02-02', 12709000, '9700_b.jpg', '<h2>BlackBerry Bold 9700 v&agrave; Pearl 3G chạy OS 6</h2>\r\n<div class="news_description">Hai bản BlackBerry l&agrave; Bold 9700 v&agrave;  Pearl 3G 9100 c&oacute; thể chạy được hệ điều h&agrave;nh bản 6 của RIM.</div>\r\n<p class="Normal">Nền tảng mới được  nh&agrave; sản xuất Canada tr&igrave;nh l&agrave;ng lần đầu ti&ecirc;n tr&ecirc;n chiếc Torch 9800 trước  đ&acirc;y với m&agrave;n h&igrave;nh chạm. Hai đoạn video m&agrave; <em>Engadget </em>thử nghiệm cho thấy, OS 6 chạy tr&ecirc;n Bold 9700 v&agrave; Pearl 3G 9100 kh&aacute; tốt.</p>\r\n<p class="Normal">M&agrave;n h&igrave;nh Home với c&aacute;c n&uacute;t cuộn sang tr&aacute;i, phải, c&aacute;ch  l&agrave;m việc giống như Torch, nhưng người d&ugrave;ng cần điều khiển bằng trackpad  thay cho m&agrave;n h&igrave;nh chạm. Việc chuyển đổi c&aacute;c giao diện kh&aacute; mượt m&agrave;.</p>\r\n<p class="Normal">Hệ điều h&agrave;nh mới cũng mang tới tr&igrave;nh duyệt tr&ecirc;n nền  WebKit, cho ph&eacute;p lướt web tốt hơn với bản OS 5. D&ugrave; c&oacute; RAM k&eacute;m một nửa so  với Torch, nhưng 9100 v&agrave; 9700 đều chạy tốt hệ điều h&agrave;nh n&agrave;y.</p>\r\n<p class="Normal">Hiện chưa c&oacute; th&ocirc;ng tin về thời điểm RIM sẽ ra mắt bản  hệ điều h&agrave;nh mới cho Bold 9700 v&agrave; Pearl 3G 9100, nhiều kh&aacute; năng c&aacute;c thị  trường sẽ được cập nhật chọn lọc.</p>\r\n<p class="Normal"><em>Dưới đ&acirc;y l&agrave; hai đoạn video BlackBerry Bold 9700 v&agrave; Pearl 3G chạy OS 6.</em></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (493, 44, 1, 'BlackBerry Curve 9300 ', '    * Điện thoại 3G với bàn phím Qwerty mạnh mẽ\r\n    * Máy ảnh 2.0 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc, xem phim nhiều định dạng\r\n    * Kết nối Wifi, Bluetooth, GPRS \r\n\r\n', '2009-02-02', 8681000, 'black_9300_b.jpg', '<h2>BlackBerry Curve 3G 9300 tại TP HCM</h2>\r\n<div class="news_description">Những chiếc Curve 3G 9300 c&oacute; mặt tại  TP HCM trước khi RIM c&ocirc;ng bố phi&ecirc;n bản n&agrave;y, m&aacute;y c&oacute; kiểu d&aacute;ng kh&aacute; giống  với model 8520.</div>\r\n<div class="new_pro_detail_contet">\r\n<p class="Normal">Curve 3G 9300 được  RIM c&ocirc;ng bố c&aacute;ch đ&acirc;y v&agrave;i ng&agrave;y. Model c&oacute; mặt tại TP HCM c&ograve;n xuất hiện từ  kh&aacute; l&acirc;u trước đ&oacute;, đ&acirc;y l&agrave; bản n&acirc;ng cấp của Curve 8520 với c&aacute;c n&acirc;ng cấp  như kết nối 3G/HSDPA, Wi-Fi th&ecirc;m chuẩn n cũng như hỗ trợ GPS.</p>\r\n<p class="Normal">M&aacute;y c&oacute; thiết kế tương tự chiếc 8520, cầm gọn trong  tay, với k&iacute;ch thước 109 x 60 x 13,9 mm, nặng 104 gram, model n&agrave;y tr&ocirc;ng  kh&aacute; nhẹ. M&aacute;y chạy hệ điều h&agrave;nh BlackBerry OS 5, v&agrave; sẽ được n&acirc;ng cấp l&ecirc;n  bản 6 trong thời gian tới.</p>\r\n<p class="Normal">BlackBerry Curve 3G 9300 sẽ được b&aacute;n ch&iacute;nh thức tại  Việt Nam khoảng cuối th&aacute;ng 9, hiện chưa c&oacute; th&ocirc;ng tin về mức gi&aacute;. Dưới  đ&acirc;y l&agrave; model đang c&oacute; mặt tại cửa h&agrave;ng D&acirc;u Đen (đường L&ecirc; Hồng Phong - TP  HCM).</p>\r\n<table border="0" cellspacing="2" cellpadding="2" width="80%" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/1_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="465" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Mặt trước của m&aacute;y...</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/2_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="370" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">...với m&agrave;n h&igrave;nh rộng 2,46 inch, độ ph&acirc;n giải 320 x 240 pixel.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/3_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">M&aacute;y c&oacute; b&agrave;n ph&iacute;m của d&ograve;ng Curve truyền thống, b&ecirc;n tr&ecirc;n l&agrave; c&aacute;c n&uacute;t điều khiển v&agrave; trackpad cảm ứng của RIM.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/4_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="645" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Cạnh tr&aacute;i với cổng kết nối microUSB v&agrave; giắc cắm tai nghe 3,5 mm.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/5_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="570" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Ph&iacute;m tăng giảm &acirc;m lượng b&ecirc;n cạnh phải.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/6_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">C&aacute;c n&uacute;t bấm giải tr&iacute; đa phương tiện của Curve 3G 9300 nằm tr&ecirc;n đỉnh.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/7_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Ph&iacute;a dưới.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/8_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="495" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Mặt sau của m&aacute;y được l&agrave;m bằng lớp nhựa nh&aacute;m, camera của model n&agrave;y l&agrave; 2 Megapixel.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/9_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="376" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Thiết bị sử dụng pin 1.150 mAh.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/10_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="402" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Với khả năng kết nối mạnh mẽ, từ HSDPA đến Wi-Fi chuẩn n, Curve 3G 9300 cho ph&eacute;p lướt web nhanh.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="BlackBerry Curve 3G 9300 tại TP HCM" src="http://images.thegioididong.com/Files/2010/08/14/15591/11_BlackBerry-Curve-3G-9300-tai-TP-HCM.jpg" border="1" alt="BlackBerry Curve 3G 9300 tại TP HCM" width="480" height="375" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">B&ecirc;n cạnh Curve 8520, m&aacute;y hơn ở kết nối 3G.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style="text-align: right;"><strong>Quốc Huy</strong></p>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (494, 44, 1, 'BlackBerry Curve 8520 ', '    * Màn hình rộng và bàn phím QWERTY đầy đủ\r\n    * Camera 2.0 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc, xem phim nhiều định dạng\r\n    * Chức năng định vị GPS\r\n    * Kết nối Wifi tốc độ cao\r\n    * BlackBerry map \r\n\r\n', '2009-02-02', 6349000, 'Blackberry_8520_b.jpg', '<div class="box_right">\r\n<ul class="item_list">\r\n<li>M&agrave;n h&igrave;nh rộng v&agrave; b&agrave;n ph&iacute;m QWERTY đầy đủ</li>\r\n<li>Camera 2.0 MP, hỗ trợ quay phim</li>\r\n<li>M&aacute;y nghe nhạc, xem phim nhiều định dạng</li>\r\n<li>Chức năng định vị GPS</li>\r\n<li>Kết nối Wifi tốc độ cao</li>\r\n<li>BlackBerry map </li>\r\n</ul>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (495, 44, 1, 'BlackBerry', '(Hàng sắp về hoặc đã hết hàng) \r\n    * Màn hình TFT, 65.536 màu, rộng 2.4 inches\r\n    * Hỗ trợ đa Sim: Không\r\n    * Máy ảnh Không có\r\n    * Đài FM tích hợp: Không\r\n    * Nghe nhạc: AAC, MP3\r\n    * Xem Phim: 3GP, MP4\r\n    * Kết nối 3G: Không, Wifi Wi-Fi 80', '2009-02-02', 6349000, 'BlackBerry_1.jpg', '<div class="box_right">\r\n<ul class="item_list">\r\n<li>M&agrave;n h&igrave;nh TFT, 65.536 m&agrave;u, rộng 2.4 inches </li>\r\n<li>Hỗ trợ đa Sim: Kh&ocirc;ng </li>\r\n<li>M&aacute;y ảnh Kh&ocirc;ng c&oacute; </li>\r\n<li>Đ&agrave;i FM t&iacute;ch hợp: Kh&ocirc;ng </li>\r\n<li>Nghe nhạc: AAC, MP3 </li>\r\n<li>Xem Phim: 3GP, MP4 </li>\r\n<li>Kết nối 3G: Kh&ocirc;ng, Wifi Wi-Fi 802.11 b/g, GPS: A-GPS, Bluetooth: C&oacute; </li>\r\n<li>Ứng dụng văn ph&ograve;ng: Xem file văn bản </li>\r\n</ul>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (496, 44, 1, 'BlackBerry Dakota', '\r\n    * Hệ điều hành BlackBerry 6.1\r\n    * Máy ảnh 5.0 MP (2592 x 1944 pixels)\r\n    * Nghe nhạc: MP3, WAV, WMA, eAAC+\r\n    * Xem Phim: H.263, H.264(MPEG4-AVC), MP4, WMV\r\n    * Kết nối 3G, Wi-Fi 802.11 b/g/n, A-GPS, Bluetooth V2.1 với A2DP\r\n    * Ứng dụng ', '2009-02-02', 15000000, 'bb_dokato_b.jpg', '<h2>BlackBerry Dakota sẽ t&iacute;ch hợp c&ocirc;ng nghệ NFC</h2>\r\n<div class="news_description">BlackBerry Dakota sẽ l&agrave; smartphone  đầu ti&ecirc;n của RIM hỗ trợ t&iacute;nh năng quay video HD, c&ocirc;ng nghệ NFC v&agrave; c&oacute; thể  trở th&agrave;nh điểm ph&aacute;t 3G di động.</div>\r\n<div class="news_pro_link"><a href="http://thegioididong.com/tin-tuc-phan-mem-ung-dung-va-game-cho-dien-thoai,27543-blackberry-apps-snaptu---ung-dung-tat-ca-trong-mot.aspx">&nbsp;&gt;&nbsp;BlackBerry Apps: Snaptu - Ứng dụng tất cả trong một</a><a href="http://thegioididong.com/tin-tuc-phan-mem-ung-dung,27777-blackberry-bridge-phan-mem-dong-bo-giua-playbook-v.aspx">&nbsp;&gt;&nbsp;BlackBerry Bridge: phần mềm đồng bộ giữa Playbook v&agrave; điện thoại BB</a>,<a href="http://thegioididong.com/tin-tuc-dien-thoai-di-dong,28027-lo-dien-blackberry-curve-apollo.aspx">&nbsp;&gt;&nbsp;Lộ diện BlackBerry Curve Apollo</a>,</div>\r\n<div class="new_pro_detail_contet">\r\n<p><img class="img" title="BlackBerry Dakota sẽ t&iacute;ch hợp c&ocirc;ng nghệ NFC" src="http://images.thegioididong.com/Files/2011/01/14/28030/35_BlackBerry-Dakota-se-tich-hop-cong-nghe-NFC.jpg" border="1" alt="BlackBerry Dakota sẽ t&iacute;ch hợp c&ocirc;ng nghệ NFC" />&nbsp;<br /><span class="picture_more_detail">BlackBerry Dakota sẽ t&iacute;ch hợp c&ocirc;ng nghệ bị NFC. Ảnh: BGR</span></p>\r\nGiống như c&aacute;c mẫu <span style="text-decoration: underline;"><a href="http://thegioidientu.com/sieu-thi-dien-thoai-di-dong,trang-chu-42.aspx" target="_blank">điện thoại</a></span> d&ograve;ng Bold, BlackBerry Dakota sở hữu m&agrave;n h&igrave;nh 2.8-inch độ ph&acirc;n giải  640x480 px c&ugrave;ng camera 5MP hỗ trợ quay video HD. M&aacute;y c&oacute; khả năng kết nối  802.11n Wi-Fi tr&ecirc;n cả hai băng tần 2.4GHz/5GHz v&agrave; dung lượng lưu trữ  l&ecirc;n đến 4GB. <br /><br />Dakota sẽ được t&iacute;ch nhiều t&iacute;nh năng v&agrave; c&ocirc;ng nghệ  hiện đại, đ&aacute;ng ch&uacute; &yacute; nhất l&agrave; t&iacute;nh năng thanh to&aacute;n điện tử NFC v&agrave; 3G  hotspot - biến điện thoại th&agrave;nh điểm chia sẻ kết nối 3G với c&aacute;c thiết bị  kh&aacute;c. T&iacute;nh năng n&agrave;y sẽ gi&uacute;p Dakota cạnh tranh với Personal Hotspot  (biến điện thoại th&agrave;nh điểm ph&aacute;t Wi-fi di động) mới nhất tr&ecirc;n thiết bị  sử dụng phi&ecirc;n bản hệ điều h&agrave;nh iOS 4.3 beta.<br /><br />Ngo&agrave;i ra, Dakota c&ograve;n  trang bị bộ cảm biến chuyển động, la b&agrave;n số v&agrave; nhiều t&iacute;nh năng truyền  thống của d&ograve;ng m&aacute;y d&agrave;nh cho doanh nh&acirc;n. M&aacute;y chưa cập nhật hệ điều h&agrave;nh  QNX v&agrave; nhiều khả năng sẽ chạy phi&ecirc;n bản hệ điều h&agrave;nh BlackBerry OS 6.1.<br /><br />Nhiều  khả năng, RIM sẽ ch&iacute;nh thức ra mắt BlackBerry Dakota tại triển l&atilde;m CTIA  v&agrave;o th&aacute;ng 3/2011 hoặc triển l&atilde;m BlackBerry World v&agrave;o th&aacute;ng 5.<br /><br /><br />\r\n<div style="text-align: right;"><strong>Thu Hiền</strong></div>\r\n</div>', 0, 200, NULL, 0, 1);
