INSERT INTO `sanpham` VALUES (506, 37, 1, 'Nokia N8 ', 'Thân máy, Pin BL-4D(1200mAh),tai nghe WH-102, sạc AC-15, cáp USB(CA-101D), bao da, sách hướng dẫn\r\n    * Hệ điều hành Symbian\r\n    * Máy ảnh 12 MP, máy ảnh phụ VGA\r\n    * Mạng 3G với HSDPA, 7.2 Mbps\r\n    * HSUPA, 5.76 Mbps\r\n    * Wi-Fi 802.11 b/g, UPnP te', '2009-09-02', 11499000, 'Nokia_N8_bac.jpg', '<h2>Nokia ''đập hộp'' N8 theo 3 phong c&aacute;ch</h2>\r\n<div class="news_description">Trước thời điểm b&aacute;n N8, Nokia giới  thiệu c&aacute;c đoạn video "đập hộp" theo từng phong c&aacute;ch kh&aacute;c nhau.</div>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="Nokia ''đập hộp'' N8 theo 3 phong c&aacute;ch" src="http://images.thegioididong.com/Files/2010/09/09/20679/1_Nokia-dap-hop-N8-theo-3-phong-cach.jpg" border="1" alt="Nokia ''đập hộp'' N8 theo 3 phong c&aacute;ch" width="480" height="264" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hộp N8 được xếp h&agrave;nh chữ v&agrave; đổ Domino.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">N8 sẽ được b&aacute;n ra từ 30/9 v&agrave; c&oacute; mặt tại nhiều thị  trường hơn trong th&aacute;ng 10, gồm cả Việt Nam. Chiếc smartphone được nh&agrave;  sản xuất Phần Lan đặt nhiều kỳ vọng với thiết kế m&agrave;n h&igrave;nh chạm, m&aacute;y ảnh  12 Megapixel, chạy Symbian^3.</p>\r\n<p class="Normal">Nokia vừa giới thiệu bộ video đập hộp N8. Mở đầu, h&atilde;ng  tr&igrave;nh diễn c&aacute;c hộp N8 được treo l&ecirc;n d&acirc;y hoặc đổ sập domino. Sau đ&oacute; l&agrave;  t&ugrave;y chọn 3 người mẫu lựa chọn "đập hộp" chiếc di động n&agrave;y với c&aacute;c m&agrave;u  sắc kh&aacute;c nhau.</p>\r\n<p class="Normal"><span style="color: #2f2f2f;"><strong>Dưới đ&acirc;y l&agrave; 3 phong c&aacute;ch đập hộp N8 với c&aacute;c người mẫu của Nokia</strong>.</span></p>\r\n<table border="0" cellspacing="2" cellpadding="2" width="70%" align="center">\r\n<tbody>\r\n<tr>\r\n<td><br /></td>\r\n</tr>\r\n<tr>\r\n<td>Brenda mở hộp N8 xanh nước biển</td>\r\n</tr>\r\n<tr>\r\n<td><br /></td>\r\n</tr>\r\n<tr>\r\n<td>Ryan với chiếc N8 m&agrave;u x&aacute;m.</td>\r\n</tr>\r\n<tr>\r\n<td><br /></td>\r\n</tr>\r\n<tr>\r\n<td>Suzy ''sexy'' với N8 xanh l&aacute; c&acirc;y</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>&nbsp;</strong></p>', 0, 200, NULL, 0, 1);
