INSERT INTO `sanpham` VALUES (508, 37, 1, 'Nokia 6700 classic gold ', '    * Điện thoại 3G với thiết kế thời trang với vỏ tráng 18 carat vàng sang trọng\r\n    * Camera 5 MP, hỗ trợ flash, tự động lấy nét\r\n    * Nghe nhạc/xem phim nhiều định dạng MP3/M4A/AAC/eAAC+/WMA\r\n    * Đài FM, Bluetooth, kết nối 3.5G với tốc độ truyền dữ', '2009-09-02', 9799000, 'Nokia-6700gold_b.jpg', NULL, 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (509, 37, 1, 'Nokia N97 ', '    * Kiểu trượt cảm ứng sành điệu\r\n    * Camera 5 MP, camera phụ thực hiện videocall\r\n    * Bộ nhớ 32 GB, RAM 128 MB\r\n    * Chức năng định vị GPS\r\n    * Chức năng Out-Tivi, jack tai nghe 3.5mm\r\n    * Pocket Office (Word, Excel, PowerPoint, PDF viewer)\r\n ', '2009-09-02', 9549000, '0Nokia N97_1.jpg', NULL, 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (510, 37, 1, 'Nokia C7 ', '    * Hệ điều hành Symbian\r\n    * Màn hình cảm ứng điện dung\r\n    * Máy ảnh 8 MP với Flash kép\r\n    * Máy nghe nhạc, xem phim nhiều định dạng\r\n    * Mạng 3G, kết nối Wifi, GPS, Bluetooth\r\n    * Xem văn bản (Word, Excel, PowerPoint, PDF)\r\n    * Kết nối Tiv', '2009-09-02', 8249000, 'Nokia-C7-1000001042714.jpg', '<h2>3 smartphone chụp ảnh 8 ''chấm'' của Nokia ra mắt</h2>\r\n<div class="news_description">Tại sự kiện Nokia World diễn ra ở  London (Anh), h&atilde;ng điện thoại Phần Lan giới thiệu 3 model C6-01, C7 v&agrave;  E7 thuộc loại cao cấp với mức gi&aacute; từ 340 USD trở l&ecirc;n.</div>\r\n<p class="Normal">Nokia E7 sử dụng  m&agrave;n h&igrave;nh cảm ứng AMOLED 4 inch 640 x 360 pixel, được bổ sung c&ocirc;ng nghệ  ClearBlack Display độc quyền cho h&igrave;nh ảnh sống động như thật, kh&ocirc;ng thua  k&eacute;m g&igrave; Retina Display tr&ecirc;n iPhone 4. E7 d&agrave;y 13,8 mm, t&iacute;ch hợp m&aacute;y ảnh 8  megapixel, quay video HD, b&agrave;n ph&iacute;m Qwerty c&ugrave;ng 16 GB bộ nhớ trong. Sản  phẩm được Nokia nhắm đến đối tượng doanh nh&acirc;n, hỗ trợ ứng dụng Microsoft  Exchange, hệ thống Ovi v&agrave; c&oacute; gi&aacute; b&aacute;n l&agrave; 650 USD.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="3 smartphone chụp ảnh 8 ''chấm'' của Nokia ra mắt" src="http://images.thegioididong.com/Files/2010/09/15/21061/1_3-smartphone-chup-anh-8-cham-cua-Nokia-ra-mat.jpg" border="1" alt="3 smartphone chụp ảnh 8 ''chấm'' của Nokia ra mắt" width="450" height="288" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="middle">Ảnh: <em>Nokia.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&ograve;n model C7 sử dụng lớp vỏ bằng lớp th&eacute;p chống gỉ  c&ugrave;ng m&agrave;n h&igrave;nh cảm ứng AMOLED 3,5 inch kết hợp với ClearBlack Display  giống E7. M&aacute;y t&iacute;ch hợp camera 8 "chấm", hỗ trợ quay video HD v&agrave; được  xuất xưởng v&agrave;o cuối năm nay với mức gi&aacute; 430 USD.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="3 smartphone chụp ảnh 8 ''chấm'' của Nokia ra mắt" src="http://images.thegioididong.com/Files/2010/09/15/21061/2_3-smartphone-chup-anh-8-cham-cua-Nokia-ra-mat.jpg" border="1" alt="3 smartphone chụp ảnh 8 ''chấm'' của Nokia ra mắt" width="400" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle">Nokia C6-01 v&agrave; C7. Ảnh: <em>Nokia.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Cuối c&ugrave;ng l&agrave; Nokia C6-01 c&oacute; m&agrave;n h&igrave;nh giống với C7  nhưng b&eacute; hơn, chỉ 3,2 inch 640 x 360 pixel. Sản phẩm c&oacute; thiết kế giống  với model C6 nhưng kh&ocirc;ng c&oacute; b&agrave;n ph&iacute;m Qwerty. M&aacute;y hỗ trợ xem TV tr&ecirc;n web,  c&aacute;c mạng x&atilde; hội v&agrave; c&oacute; gi&aacute; 340 USD.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (511, 37, 1, 'Nokia N86 ', '    * Thiết kế trượt thời trang với màn hình AMOLED 2.6 inch\r\n    * Camera 8.0 MP, Camera phụ VGA, đèn LED hỗ trợ, kính chống xước\r\n    * Hỗ trợ định dạng: MP3, WMA, AAC, AAC+, eAAC, eAAC+, MPEG-4, AVC (H.264), WMV, Flash video, 3GPP (H.263), RealVideo\r\n ', '2009-09-02', 7890000, 'N86_b.jpg', '<h2>B&agrave;i viết về sản phẩm Nokia N86</h2>\r\n<div class="news_description">B&agrave;i viết về sản phẩm Nokia N86</div>\r\n<div class="new_pro_detail_contet">\r\n<ul>\r\n<li><strong><a href="http://thegioididong.com/DTDD/News/NewsDetail.aspx?NewsID=7003&amp;CategoryID=2" target="_blank">Nokia N86 vs. Sony Ericsson W995 </a></strong></li>\r\n<li><strong><a href="http://thegioididong.com/DTDD/News/NewsDetail.aspx?NewsID=6950&amp;CategoryID=30" target="_blank">''''Đập hộp'''' Nokia N86 </a></strong></li>\r\n<li><strong><a href="http://thegioididong.com/DTDD/News/NewsDetail.aspx?NewsID=6859&amp;CategoryID=2" target="_blank">Thử Nokia N86 </a></strong></li>\r\n<li><strong><a href="http://thegioididong.com/DTDD/News/NewsDetail.aspx?NewsID=6538&amp;CategoryID=11" target="_blank">Chụp ảnh thử bằng Nokia N86 8MP </a></strong></li>\r\n</ul>\r\n<p class="NewsSummary"><span style="font-size: medium;"><strong>N86 8MP: Dế 8 "chấm" đầu ti&ecirc;n của Nokia </strong></span></p>\r\n<p class="NewsSummary"><strong>Mặc d&ugrave; chậm hơn c&aacute;c đối thủ tới 1 năm nhưng cuối c&ugrave;ng Nokia đ&atilde; ch&iacute;nh thức gia nhập ph&acirc;n kh&uacute;c thị trường "dế" 8 megapixel.</strong></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">Cuối c&ugrave;ng th&igrave; sự tồn tại của một ch&uacute; dế trang bị camera 8 megapixel cũng đ&atilde; được Nokia x&aacute;c nhận. Tuy nhi&ecirc;n, cho tới thời điểm n&agrave;y, Nokia N86 8MP vẫn chưa được h&atilde;ng sản xuất di động số 1 thế giới n&agrave;y ra mắt tại Hội nghị di động thế giới MWC 2009 đang diễn ra ở Barcelona (T&acirc;y Ban Nha).</span></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">Năm 2008, ph&acirc;n kh&uacute;c dế 8 "chấm" trở n&ecirc;n đặc biệt s&ocirc;i động với sự tham gia của một loạt "đại gia" như Sony Ericsson với C905, Samsung với Innov8, hay LG với KC910. Mặc d&ugrave; vậy, Nokia vẫn "im hơi lặng tiếng" v&agrave; phải chờ cho tới tận thời điểm giữa th&aacute;ng 2 n&agrave;y, người d&ugrave;ng mới được chi&ecirc;m ngưỡng những h&igrave;nh ảnh của ch&uacute; dế 8 megapixel đầu ti&ecirc;n của Nokia. </span></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">B&ecirc;n cạnh camera 8 megapixel, t&iacute;nh năng chụp ảnh của N86 8 MP c&ograve;n được 2 đ&egrave;n LED với khả năng chiếu s&aacute;ng ở khoảng c&aacute;ch 3,5 m hỗ trợ. </span></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">Với bộ nhớ trong l&ecirc;n tới 8 GB v&agrave; khả năng mở rộng th&ecirc;m 16 GB bằng thẻ microSD, N86 cung cấp đủ kh&ocirc;ng gian d&agrave;nh cho những người th&iacute;ch nghe nhạc, xem phim. B&ecirc;n cạnh đ&oacute;, th&uacute; vui giải tr&iacute; của người d&ugrave;ng c&ograve;n được N86 8 MP thoả m&atilde;n bằng khả năng hỗ trợ một loạt c&aacute;c định dạng audio v&agrave; video. </span></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;"><img src="http://www.thegioididong.com/Dtdd/News/NewsImage/5739/tgdd_1234839726-nokia_n86_8mp_1.jpg" alt="" /></span></p>\r\n<p><em><span style="color: #0000ff; font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">N86 8MP: Dế 8 chấm đầu ti&ecirc;n của Nokia</span></em></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">Sử dụng pin BL-5K 1200 mAh mạnh mẽ, N86 8 MP c&oacute; thời gian thoại l&agrave; 6,3 giờ (GSM) hoặc 3,9 giờ (3G). Thời gian nghe nhạc l&ecirc;n tới 25 giờ v&agrave; xem video l&agrave; 7 giờ.</span></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">N86 c&oacute; m&agrave;n h&igrave;nh AMOLED rộng 2.6 inch nhưng đ&acirc;y kh&ocirc;ng phải l&agrave; m&agrave;n h&igrave;nh cảm ứng. M&aacute;y c&oacute; k&iacute;ch thước 103,4 x 51,4 x 16,5 mm v&agrave; trọng lượng l&agrave; 149 gram.</span></p>\r\n<p><span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">Th&ocirc;ng số của Nokia N86:</span></p>\r\n<span style="font-family: verdana,arial,helvetica,sans-serif; font-size: x-small;">- K&iacute;ch thước: 103,4 x 51,4 x 16,5 mm<br />- Trọng lượng: 149 gram<br />- M&agrave;n h&igrave;nh: AMOLED 2.6 inch, 240 x 320 pixel<br />- Camera: 8 megapixel với độ mở của ống k&iacute;nh l&agrave; f/2.4 (c&oacute; độ n&eacute;t cao ngay cả trong điều kiện &aacute;nh s&aacute;ng yếu)<br />- Tốc độ m&agrave;n trập 1/1000 gi&acirc;y (th&iacute;ch hợp cho cả việc chụp chuyển động)<br />- Quay video độ ph&acirc;n giải 640 x 480 pixel ở tốc độ 30 khung h&igrave;nh/gi&acirc;y<br />- Hỗ trợ định dạng: MP3, WMA, AAC, AAC+, eAAC, eAAC+, MPEG-4, AVC (H.264), WMV, Flash video, 3GPP (H.263), RealVideo<br />- K&iacute;nh chống xước<br />- Camera c&oacute; đ&egrave;n LED hỗ trợ<br />- Bộ nhớ trong 8 GB<br />- C&oacute; khe cắm mở rộng, hỗ trợ thẻ microSD 16 GB<br />- Thời gian thoại: 3,9 giờ (mạng 3G); 6,3 giờ (mạng GSM)<br />- Thời gian thực hiện video call: 2,5 giờ<br />- Thời gian xem video: 7 giờ (QVGA, 30 khung h&igrave;nh/gi&acirc;y)<br />- Thời gian nghe nhạc: 25 giờ<br />- Kết nối: microUSB, USB 2.0, TV-out, giắc audio 3.5 mm, Bluetooth 2.0, GPS</span></div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (512, 37, 1, 'Nokia E72 ', '    * Symbian OS 9.3, Series 60 v3.2 UI\r\n    * CPU: ARM 11 600 MHz processor, RAM 128MB\r\n    * Tính năng văn phòng (Word, Excel, PowerPoint, PDF)\r\n    * Camera 5.0 MP\r\n    * Camera phụ VGA thực hiện Videocall\r\n    * Máy nghe nhạc xem phim nhiều định dạng ', '2009-09-02', 7599000, 'Nokia E72_1.jpg', NULL, 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (513, 37, 1, 'Nokia X6 16GB ', '    * Màn hình cảm ứng rộng 3.2 inches\r\n    * CPU: ARM 11, 434 MHz processor, RAM 128 MB\r\n    * Camera 5 MP, 2592x1944 pixels, ống kính Carl Zeiss\r\n    * Camera phụ VGA hỗ trợ thực hiện VideoCall\r\n    * Máy nghe nhạc xem phim nhiều định dạng\r\n    * Mạng 3', '2009-09-02', 7199000, 'Nokia_X6_16GB_b.jpg', NULL, 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (514, 37, 1, 'Nokia C6 ', '    * Hệ điều hành Symbian OS v9.4\r\n    * Máy ảnh 5.0 MP, máy ảnh phụ thực hiện Videocall\r\n    * Máy nghe nhạc MP3/WMA/WAV/eAAC+\r\n    * Đài FM tích hợp, jack tai nghe 3.5mm\r\n    * Mạng 3G với HSDPA 7.2 Mbps\r\n    * HSUPA 5.76 Mbps\r\n    * Yahoo! Messenger, ', '2009-09-02', 6099000, 'Nokia_C6_b.jpg', NULL, 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (515, 37, 1, 'Nokia 5530 illuvial pink ', '    * Điện thoại nghe nhạc cá tính\r\n    * Hệ điều hành Symbian OS v9.4, Series 60 rel. 5\r\n    * CPU: ARM 11, 434 MHz processor\r\n    * RAM 128MB\r\n    * Xem file văn phòng (Word, Excel, PowerPoint, PDF)\r\n    * Camera 3.15 MP, hỗ trợ quay phim, Flash\r\n    * ', '2009-09-02', 4499000, 'Nokia_5530pink_b.jpg', '<h2>B&agrave;i viết về sản phẩm Nokia 5530 illuvial pink</h2>\r\n<div class="news_description">B&agrave;i viết về sản phẩm Nokia 5530 illuvial pink</div>\r\n<div class="new_pro_detail_contet">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://thegioididong.com/dtdd/News/BaivietSP/Nokia/5530-illuvial.jpg" border="1" alt="5530 XpressMusic với m&agrave;n h&igrave;nh cảm   ứng. Ảnh: Cnet." width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">5530 XpressMusic với m&agrave;n h&igrave;nh cảm ứng. Ảnh: <em>Cnet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Sau sự ra mắt của bộ đ&ocirc;i di động cảm ứng l&agrave; 5800 XpressMusic v&agrave; N97 chạy tr&ecirc;n hệ điều h&agrave;nh S60 phi&ecirc;n bản 5, 5530 l&agrave; model tiếp theo c&oacute; m&agrave;n h&igrave;nh chạm với c&aacute;c ứng dụng giải tr&iacute;.</p>\r\n<p class="Normal">Chiếc smartphone cảm ứng n&agrave;y được n&acirc;ng cấp thanh bar Contact (Danh bạ) với 20 số điện thoại được c&agrave;i đặt sẵn trong khi 5800 chỉ l&agrave; 4. Kh&aacute;c với lớp vỏ nhựa của 5800, điện thoại mới của Nokia c&oacute; lớp vỏ bằng th&eacute;p cao cấp v&agrave; chắc chắn hơn.</p>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (516, 39, 1, 'Samsung S3500 ', '    * Thiết kế trượt thời trang\r\n    * Màn hình 16 triệu màu\r\n    * Camera 2.0 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc nhiều định dạng\r\n    * Chế độ giả lập cuộc gọi ản\r\n    * Kết nối Bluetooth, USB, đài FM \r\n\r\n', '2011-10-02', 1999000, 'SamsungS3500_1.jpg', '<h1 class="features_subject"><span style="font-size: small;">Phong c&aacute;ch b&ecirc;n ngo&agrave;i lẫn b&ecirc;n trong </span></h1>\r\n<p class="features_text">Samsung S3500 l&agrave; chiếc điện thoại trượt thời trang hỗ trợ M&agrave;n h&igrave;nh 2.2" QVGA 16 triệu m&agrave;u TFT LCD với giao diện menu sử dụng t&ugrave;y biến Camera 2.0 Megapixel, hỗ trợ nhạc đa dịnh dạng MP3 / AAC / AAC+ / MIDIplayback, lưu trữ thật lớn với hỗ trợ thẻ nhớ microSD 8G (bộ nhớ trong 40MB). Theo d&otilde;i điện thoại 2.0 gi&uacute;p bạn bảo mật th&ocirc;ng tin c&aacute; nh&acirc;n trong trường hợp bạn bị thất lạc.</p>\r\n<div class="contentlt">\r\n<div class="content_block">\r\n<div class="title_para">\r\n<h2><span style="font-size: small;">Thiết kế trượt sang trọng thuộc d&ograve;ng Soul</span></h2>\r\n</div>\r\n<img class="img" src="http://www.thegioididong.com/dtdd/news/BaivietSP/Samsung/S3500_A_02.jpg" alt="thiết kế trượt sang trọng thuộc d&ograve;ng Soul" width="344" /><br />\r\n<div class="memo">&bull; Thiết kế trượt v&agrave; mỏng 99.9 x 48 x 13.9mm.<br />&bull; Vỏ được l&agrave;m bằng kim loại mờ xước cho cảm gi&aacute;c thật sang trọng. <br />&bull; Phần vỏ được gia c&ocirc;ng kỹ thuật cắt liền (kh&ocirc;ng c&oacute; vết h&agrave;n) gi&uacute;p tr&ocirc;ng thật liền lạc.</div>\r\n</div>\r\n<div class="content_block">\r\n<div class="title_para">\r\n<h2><span style="font-size: small;">T&iacute;nh năng kh&aacute;c tr&ecirc;n S3500</span></h2>\r\n</div>\r\n<img class="img" src="http://www.thegioididong.com/dtdd/news/BaivietSP/Samsung/S3500_A_01.jpg" alt="t&iacute;nh năng kh&aacute;c tr&ecirc;n S3500" width="344" /><br />\r\n<div class="memo">&bull; Gửi v&agrave; nhận SMS, EMS, MMS v&agrave; tin nhắn video.<br />&bull; Danh bạ lưu được 1000 số gi&uacute;p bạn lưu trữ tất cả danh một nơi v&agrave; dễ d&agrave;ng quản l&yacute; ch&uacute;ng.<br />&bull; Tin nhắn Bluetooth gi&uacute;p bạn gửi tin đến những người ban xung quanh m&agrave; kh&ocirc;ng tốn bất kỳ một chi ph&iacute; n&agrave;o.<br />&bull; Theo d&otilde;i điện thoại 2.0 (Utrack 2.0) gi&uacute;p bạn bảo mật th&ocirc;ng tin c&aacute; nh&acirc;n trong trường hợp bạn bị thất lạc.<br />&bull; Tin nhắn khẩn cấp sẽ b&aacute;o động bằng 1 tin nhắn cần trợ gi&uacute;p khi bạn cần.</div>\r\n</div>\r\n</div>\r\n<!-- Left Content END --><!-- Right Content START -->\r\n<div class="content_block2">\r\n<div class="title_para">\r\n<h2><span style="font-size: small;">Giải tr&iacute; với Music &amp; FM Radio</span></h2>\r\n</div>\r\n<div class="memo">&bull; Hỗ trợ nhạc đa dịnh dạng MP3 / AAC / AAC+ / MIDIplayback. <br />&bull; Quản l&yacute; nhạc theo thư viện gi&uacute;p bạn dễ d&agrave;ng lựa chọn b&agrave;i nhạc. <br />&bull; Chế độ nghe nhạc nền gi&uacute;p bạn c&oacute; thể vừa thuởng thức &acirc;m nhạc vừ a sử dụng c&aacute;c chức năng kh&aacute;c.<br />&bull; Ghi &acirc;m một đoạn FM v&agrave; chuyển th&agrave;nh nhạc chu&ocirc;ng.</div>\r\n</div>\r\n<div class="title_para">\r\n<h2><span style="font-size: small;">Chụp h&igrave;nh v&agrave; quay phim s&agrave;nh điệu</span></h2>\r\n</div>\r\n<p><img class="img" src="http://www.thegioididong.com/dtdd/news/BaivietSP/Samsung/S3500_A_03.jpg" alt="chụp h&igrave;nh v&agrave; quay phim s&agrave;nh điệu" width="344" /><br />&bull; Camera 2 Megapixel cho những tấm ảnh chất lượng cao v&agrave; dễ d&agrave;ng chia sẻ, in ấn.<br />&bull; Quay video với 15 f/s QVGA định dạng MPEG4 v&agrave; H.263 (xem lại với 25 f/s QVGA).<br /><em><strong><br />Theo Samsung</strong></em></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (517, 39, 1, 'Samsung C3200 Monte Bar ', '    * Thiết kế thời trang với kiểu dáng lạ mắt\r\n    * Máy ảnh 2.0 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc MP3/WMA/eAAC+, xem phim MP4, 3GP\r\n    * Đài FM tích hợp\r\n    * Kết nối Bluetooth, USB \r\n\r\n', '2011-10-02', 1699000, '0Samsung_C3200_1.jpg', '<p><span style="font-size: large;"><a href="http://www.thegioikts.com/Dien-thoai-di-dong/105-Samsung.html"><span style="color: #084b8b;">XEM TH&Ocirc;NG TIN CHI TIẾT TẠI:</span></a><a href="http://www.thegioikts.com/Dien-thoai-di-dong/105-Samsung.html"><span style="color: #084b8b;"> </span></a><a href="http://www.thegioikts.com/Dien-thoai-di-dong/105-Samsung.html">http://www.thegioikts.com/Dien-thoai-di-dong/105-Samsung.html</a></span>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size: small;">H&agrave;ng ch&iacute;nh h&atilde;ng của Samsung Việt Nam. Bảo h&agrave;nh 12 th&aacute;ng tr&ecirc;n to&agrave;n quốc.</span><br /><br /><br /><span style="font-size: small;">T&iacute;nh năng nổi bật:</span><br /><br /><span style="font-size: small;">- Thiết kế thời trang với kiểu d&aacute;ng lạ mắt</span><br /><span style="font-size: small;">- M&aacute;y ảnh 2.0 MP, hỗ trợ quay phim</span><br /><span style="font-size: small;">- M&aacute;y nghe nhạc MP3/WMA/eAAC+, xem phim MP4, 3GP</span><br /><span style="font-size: small;">- Đ&agrave;i FM t&iacute;ch hợp</span><br /><span style="font-size: small;">- Kết nối Bluetooth, USB</span><br /><br /><span style="font-size: small;">Th&ocirc;ng tin chi tiết:</span><br /><br /><span style="font-size: small;">+ Băng tần GSM 900/1800/1900 </span><br /><span style="font-size: small;">+ Hỗ trợ mạng tại Việt Nam MobiFone, VinaPhone, Viettel, Vietnamobile </span><br /><span style="font-size: small;">+ K&iacute;ch thước 96.3 x 53.8 x 13 mm </span><br /><span style="font-size: small;">+ Trọng lượng 80g </span><br /><span style="font-size: small;">+ Dung lượng Pin 1000 mAh </span><br /><br /><span style="font-size: small;">+ Thời gian đ&agrave;m thoại 12 (giờ) </span><br /><span style="font-size: small;">+ Thời gian chờ 666 (giờ)</span><br /><span style="font-size: small;">+ Chụp h&igrave;nh 2 MP, 1600x1200 pixels </span><br /><span style="font-size: small;">+ Quay phim Tuỳ thuộc bộ nhớ</span><br /><span style="font-size: small;">+ Video call C&oacute; </span><br /><span style="font-size: small;">+ Nghe nhạc MP3/WMA/eAAC+ </span><br /><span style="font-size: small;">+ Xem phim MP4, 3GP </span><br /><span style="font-size: small;">+ Đ&agrave;i FM C&oacute; </span><br /><span style="font-size: small;">+ Ghi &acirc;m Tuỳ thuộc bộ nhớ </span><br /><span style="font-size: small;">+ Ghi &acirc;m cuộc gọi C&oacute;</span><br /><span style="font-size: small;">+ Xem Tivi Kh&ocirc;ng</span><br /><span style="font-size: small;">+ Bộ nhớ trong 40MB </span><br /><span style="font-size: small;">+ Thẻ nhớ ngo&agrave;i TranFlash-Hỗ trợ l&ecirc;n đến 8Gb</span><br /><span style="font-size: small;">+ Kết nối USB 2.0 </span><br /><span style="font-size: small;">+ Bluetooth C&oacute; </span><br /><span style="font-size: small;">+ Hồng ngoại Kh&ocirc;ng </span><br /><span style="font-size: small;">+ Tr&igrave;nh duyệt WAP 2.0/xHTML, HTML </span><br /><span style="font-size: small;">+ Java C&oacute; </span><br /><span style="font-size: small;">+ GPRS C&oacute; </span><br /><span style="font-size: small;">+ HSCSD Kh&ocirc;ng </span><br /><span style="font-size: small;">+ EDGE 236.8(kbps) </span><br /><span style="font-size: small;">+ 3G Kh&ocirc;ng</span><br /><span style="font-size: small;">+ UMTS Kh&ocirc;ng</span><br /><span style="font-size: small;">+ WCDMA Kh&ocirc;ng</span><br /><span style="font-size: small;">+ Wifi Kh&ocirc;ng</span><br /><span style="font-size: small;">+ GPS Kh&ocirc;ng</span><br /><span style="font-size: small;">+ Loại m&agrave;n h&igrave;nh TFT 262.144 triệu m&agrave;u </span><br /><span style="font-size: small;">+ K&iacute;ch thước m&agrave;n h&igrave;nh 2.0 inches 240 x 320 Pixels </span><br /><span style="font-size: small;">+ Nhạc chu&ocirc;ng 64 &acirc;m sắc MIDI, MP3, WAV </span><br /><span style="font-size: small;">+ B&aacute;o rung C&oacute; </span><br /><span style="font-size: small;">+ Tin nhắn SMS/MMS/Email </span><br /><span style="font-size: small;">+ Danh bạ 1000 số </span><br /><span style="font-size: small;">+ Loa ngo&agrave;i C&oacute; </span><br /><span style="font-size: small;">+ Ứng dụng văn ph&ograve;ng C&oacute; </span><br /><span style="font-size: small;">+ Hệ điều h&agrave;nh --- </span><br /><span style="font-size: small;">+ Tr&ograve; chơi C&agrave;i sẵn trong m&aacute;y </span><br /><span style="font-size: small;">+ Đồng hồ C&oacute; </span><br /><span style="font-size: small;">+ B&aacute;o thức C&oacute; </span><br /><span style="font-size: small;">+ Ng&ocirc;n ngữ Tiếng Anh/ Việt </span><br /><span style="font-size: small;">+ Kiểu d&aacute;ng Thanh </span><br /><span style="font-size: small;">+ M&agrave;u sắc: Đen v&agrave;ng</span><br /><span style="font-size: small;">+ Phong c&aacute;ch</span></p>\r\n</p>\r\n<p><span style="font-size: large;"></span></p>\r\n<p>&nbsp;</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (518, 39, 1, 'Samsung C3303 Champ ', '    * Màn hình cảm ứng rộng 2.4 inches\r\n    * Máy ảnh 1.3 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc MP3/WMA/eAAC+\r\n    * Đài FM tích hợp, jack tai nghe 3.5mm\r\n    * Kết nối Bluetooth, USB, GPRS \r\n\r\n', '2011-10-02', 1679000, 'Samsung_C3300_b.jpg', '<div class="new_pro_detail_contet"><strong>H&atilde;ng điện thoại H&agrave;n Quốc vừa giới thiệu sản phẩm mới của m&igrave;nh đ&oacute; l&agrave; Samsung C3303 Champ gi&aacute; rẻ với hy vọng sẽ phổ biến tr&ecirc;n thị trường giống Samsung Star trước đ&oacute;.</strong><br /><br />\r\n<div><img title="Điện thoại di động: samsung Samsung C3300K Champ" src="http://thegioididong.com/dtdd/news/BaivietSP/Samsung/C3300K.jpg" alt="" /></div>\r\n<br />Samsung C3300K Champ c&oacute; cấu h&igrave;nh giống như Corby nhưng được đơn giản h&oacute;a hơn, với m&agrave;n h&igrave;nh cảm ứng điện trở 2.4 inches, nhỏ gọn v&agrave; nhẹ hơn Sony Ericsson XPERIA X10 mini với giao diện TouchWiz Lite với c&aacute;c biểu tượng được tổ chức dạng lưới. M&aacute;y c&oacute; t&iacute;ch hợp mạng x&atilde; hội, hỗ trợ Samsung Apps v&agrave; Java.<br /><br />Sản phẩm hỗ trợ c&aacute;c kết nối cơ bản cần thiết như GSM/GPRS/EDGE, Bluetooth 2.1 v&agrave; USB, camera 1.3 MP, hỗ trợ quay video QCIF 15fps, sử dụng thẻ nhớ MicroSD hỗ trợ l&ecirc;n đến 8GB v&agrave; jack cắm 3.5mm với hiệu ứng 3D. Samsung C3300K sử dụng pin 1000 mAh, cho thời gian chờ l&ecirc;n đến 666 giờ v&agrave; thời gian đ&agrave;m thoại l&ecirc;n đến 12 giờ.<br /><br />C3300K Champ sẽ được b&aacute;n v&agrave;o th&aacute;ng 6 ở c&aacute;c nước Trung Đ&ocirc;ng, Ch&acirc;u Mỹ Latin, Ch&acirc;u &Aacute; v&agrave; Ch&acirc;u &Acirc;u với c&aacute;c m&agrave;u Đen, N&acirc;u cafe, Hồng v&agrave; m&agrave;u trắng. Hiện Samsung vẫn chưa c&ocirc;ng bố gi&aacute; b&aacute;n sẽ l&agrave; bao nhi&ecirc;u nhưng chắc chắn sản phẩm sẽ c&oacute; gi&aacute; thấp.<br /><br /><em><strong>Sưu tầm</strong></em></div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (519, 39, 1, 'Samsung E2530 ', '    * Thiết kế nắp gập thời trang\r\n    * Máy ảnh 1.3 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc MP3\r\n    * Kết nối Bluetooth, USB, GPRS \r\n\r\n', '2011-10-02', 1589000, 'samsung_e2530_b.jpg', '<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/samsung_e2530-3665.php">Announced</a></td>\r\n<td class="nfo">2010, November</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/samsung_e2530-3665.php">Status</a></td>\r\n<td class="nfo">Coming soon. Exp. release 2010, November</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="2" scope="row">Size</th>\r\n<td class="ttl"><a href="http://www.gsmarena.com/samsung_e2530-3665.php">Dimensions</a></td>\r\n<td class="nfo">94.4 x 47.2 x 17.4 mm</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/samsung_e2530-3665.php">Weight</a></td>\r\n<td class="nfo">86 g</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="3" scope="row">Display</th>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=display-type">Type</a></td>\r\n<td class="nfo">TFT, 65K colors</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/samsung_e2530-3665.php">Size</a></td>\r\n<td class="nfo">128 x 160 pixels, 2.0 inches</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl">&nbsp;</td>\r\n<td class="nfo">- hidden 1" CSTN 96 x 96 pixels external display</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="3" scope="row">Sound</th>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=call-alerts">Alert types</a></td>\r\n<td class="nfo">Vibration, MP3 ringtones</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=loudspeaker">Speakerphone</a></td>\r\n<td class="nfo">Yes</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl">&nbsp;</td>\r\n<td class="nfo">- 3.5 mm audio jack</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="3" scope="row">Memory</th>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=phonebook">Phonebook</a></td>\r\n<td class="nfo">1000 entries, Photocall</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/samsung_e2530-3665.php">Call records</a></td>\r\n<td class="nfo">30 dialed, 30 received, 30 missed calls</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=memory-card-slot">Card slot</a></td>\r\n<td class="nfo">microSD, up to 8GB</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="8" scope="row">Data</th>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=gprs">GPRS</a></td>\r\n<td class="nfo">Class 12 (4+1/3+2/2+3/1+4 slots), 32 - 48 kbps</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=edge">EDGE</a></td>\r\n<td class="nfo">Class 12</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=3g">3G</a></td>\r\n<td class="nfo">No</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=wi-fi">WLAN</a></td>\r\n<td class="nfo">No</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=bluetooth">Bluetooth</a></td>\r\n<td class="nfo">Yes, v2.1 with A2DP</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=irda">Infrared port</a></td>\r\n<td class="nfo">No</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=usb">USB</a></td>\r\n<td class="nfo">Yes, v2.0</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="3" scope="row">Camera</th>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=camera">Primary</a></td>\r\n<td class="nfo">1.3 MP, 1280 x 1024 pixels</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=camera">Video</a></td>\r\n<td class="nfo">Yes</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=video-call">Secondary</a></td>\r\n<td class="nfo">No</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="10" scope="row">Features</th>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=messaging">Messaging</a></td>\r\n<td class="nfo">SMS, MMS</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=browser">Browser</a></td>\r\n<td class="nfo">WAP 2.0/xHTML</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=fm-radio">Radio</a></td>\r\n<td class="nfo">FM radio; FM recording</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=mobile-games">Games</a></td>\r\n<td class="nfo">Yes</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/samsung_e2530-3665.php">Colors</a></td>\r\n<td class="nfo">Black</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=gps">GPS</a></td>\r\n<td class="nfo">No</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=java">Java</a></td>\r\n<td class="nfo">Yes, MIDP 2.0</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl">&nbsp;</td>\r\n<td class="nfo">- SNS integration<br />- MP3/MP4 player<br />- Organizer<br />- Voice memo<br />- Predictive text input</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<th rowspan="3" scope="row">Battery</th>\r\n<td class="ttl">&nbsp;</td>\r\n<td class="nfo">Standard battery, Li-Ion 800 mAh</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=stand-by-time">Stand-by</a></td>\r\n<td class="nfo">Up to 530 h</td>\r\n</tr>\r\n<tr>\r\n<td class="ttl"><a href="http://www.gsmarena.com/glossary.php3?term=talk-time">Talk time</a></td>\r\n<td class="nfo">Up to 11 h 20 min</td>\r\n</tr>\r\n</tbody>\r\n</table>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (520, 39, 1, 'Samsung E2550 Monte Slider ', '    * Kiểu dáng trượt thời trang\r\n    * Máy ảnh 1.3 MP, hỗ trợ quay phim\r\n    * Máy nghe nhạc MP3/WMA/eAAC+\r\n    * Đài FM tích hợp\r\n    * Kết nối Bluetooth, USB \r\n\r\n', '2011-10-02', 1519000, 'Samsung_E2550_b.jpg', '<p>&nbsp;&nbsp;&nbsp; * Kiểu d&aacute;ng trượt thời trang<br />&nbsp;&nbsp;&nbsp; * M&aacute;y ảnh 1.3 MP, hỗ trợ quay phim<br />&nbsp;&nbsp;&nbsp; * M&aacute;y nghe nhạc MP3/WMA/eAAC+<br />&nbsp;&nbsp;&nbsp; * Đ&agrave;i FM t&iacute;ch hợp<br />&nbsp;&nbsp;&nbsp; * Kết nối Bluetooth, USB</p>\r\n<p>&nbsp;</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (521, 39, 1, 'Samsung C3053 ', '    * Kiểu trượt thời trang\r\n    * Camera VGA, có chế độ chụp panorama\r\n    * Hỗ trợ thẻ nhớ ngoài\r\n    * Máy nghe nhạc MP3, với công nghệ âm thanh 3D\r\n    * Kết nối USB, Bluetooth\r\n    * Đài FM với chức năng ghi âm \r\n\r\n', '2011-10-02', 1359000, 'Samsung_C3053_1.jpg', '<div class="new_pro_detail_contet">\r\n<h3>Thiết kế vừa tay</h3>\r\nThiết kế nắp trượt hiện đại, kiểu d&aacute;ng nhỏ gọn năng động vừa tay bạn. M&agrave;n h&igrave;nh LCD 2.0&rdquo; 65K m&agrave;u cho h&igrave;nh ảnh sinh động sắc n&eacute;t\r\n<div class="overview_con">\r\n<div class="overviewImg_con">\r\n<div class="overviewImg_btm"><img src="http://vn.samsungmobile.com/contents/products/phone/02_Advanced%20Camera%20Feature.jpg" alt="M&aacute;y ảnh tiện dụng" /></div>\r\n</div>\r\n<h3>M&aacute;y ảnh tiện dụng</h3>\r\nM&aacute;y ảnh VGA cho bạn lưu những khoảng khắc đ&aacute;ng nhớ.<br />Chụp panorama (to&agrave;n cảnh) cho bạn những khung cảnh rộng thật ấn tượng</div>\r\n<div class="overview_con">\r\n<div class="overviewImg_con">\r\n<div class="overviewImg_btm"><img src="http://vn.samsungmobile.com/contents/products/phone/03_Dynamic%20Music%20Experience.jpg" alt="Nghe nhạc bất cứ đ&acirc;u" /></div>\r\n</div>\r\n<h3>Nghe nhạc bất cứ đ&acirc;u</h3>\r\nSamsung C3053 l&agrave; chiếc m&aacute;y nghe nhạc MP3 với thẻ nhớ Micro SD hỗ trợ đến 8GB, thật sự hỗ trợ đắc lực cho nhu cầu giải tr&iacute; của bạn</div>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (522, 41, 1, 'HTC TOUCH PRO2', 'Công năng với Windows Mobile 6.1® Professional.\r\nHSDPA/WCDMA cùng với kết nối GSM đa băng tầng, GPRS, EDGE .\r\nMàn hình TFT-LCD cảm ứng chạm 2.8 inch với độ phân giải VGA.\r\nGiao diện TouchFlo™ 3D thân thiện cho các thao tác trên màn hình cảm ứng.\r\nCamera 3', '2011-11-02', 9989000, 'HTC Touch pro2_1.jpg', '<p class="Normal" style="text-align: justify;">Touch Pro 2 l&agrave; sản phẩm  đỉnh cao nhất của d&ograve;ng PDA  phone chạy Windows Mobile c&oacute; b&agrave;n ph&iacute;m QWERTY  của HTC. Model n&acirc;ng cấp từ  phi&ecirc;n bản ra mắt năm ngo&aacute;i, sở hữu c&aacute;c t&iacute;nh  năng mới, cho ph&eacute;p người  d&ugrave;ng doanh nh&acirc;n l&agrave;m việc tốt hơn.</p>\r\n<table style="width: 1px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img style="border: 1px solid black;" src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/05/22/touch-pro-1.jpg" alt="Touch Pro với k&iacute;ch thước lớn. Ảnh: Quốc Huy." width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="text-align: center;"><em>Touch Pro với k&iacute;ch thước lớn. Ảnh: <em>Quốc Huy.</em></em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: justify;"><span style="color: #2f2f2f;"><strong>Đơn giản nhất khi nh&igrave;n  v&agrave;o bảng c&aacute;c th&ocirc;ng số, c&oacute; thể thấy Touch Pro 2 tr&ocirc;ng rất lớn</strong>.</span> K&iacute;ch thước của m&aacute;y l&agrave; 116 x 59,2 x 17,3 mm v&agrave; nặng tới 179 gram. Trong   khi đ&oacute;, nhiều chiếc smartphone tr&ecirc;n thị trường hiện c&oacute; k&iacute;ch thước chỉ   tr&ecirc;n dưới 100 gram. V&igrave; thế, model n&agrave;y kh&ocirc;ng phải d&agrave;nh cho tất cả người   d&ugrave;ng, m&aacute;y cũng kh&ocirc;ng dễ d&agrave;ng để bỏ v&agrave;o t&uacute;i quần. Touch Pro 2 được bọc   xung quanh bằng lớp crom s&aacute;ng b&oacute;ng, mặt trước v&agrave; ph&iacute;a sau của m&aacute;y l&agrave;m từ   nhựa c&oacute; m&agrave;u x&aacute;m.</p>\r\n<p class="Normal" style="text-align: justify;">M&agrave;n h&igrave;nh của m&aacute;y rất lớn,  l&ecirc;n tới 3,6 inch v&agrave; độ ph&acirc;n  giải 800 x 480 pixel (chuẩn WVGA). Khả  năng hiển thị s&aacute;ng v&agrave; sắc n&eacute;t l&agrave;  những ưu điểm dễ nhận ra. M&agrave;n h&igrave;nh của  Pro 2 chiếm gần như to&agrave;n bộ diện  t&iacute;ch mặt trước v&agrave; chỉ chừa một &iacute;t  kh&ocirc;ng gian cho thanh zoom cảm ứng v&agrave;  một v&agrave;i ph&iacute;m bấm như gọi v&agrave; ngắt  điện thoại, Start v&agrave; Back nằm ở b&ecirc;n  dưới.</p>\r\n<p class="Normal" style="text-align: justify;">Khi đẩy m&agrave;n h&igrave;nh sang b&ecirc;n  phải, b&agrave;n ph&iacute;m QWERTY của  m&aacute;y lộ ra, với 5 h&agrave;ng ph&iacute;m, trong đ&oacute; c&aacute;c n&uacute;t  bấm số nằm ri&ecirc;ng ở g&oacute;c  tr&ecirc;n. C&aacute;ch bố tr&iacute; c&aacute;c lớp rất tốt, giống như  b&agrave;n ph&iacute;m của m&aacute;y t&iacute;nh. Giữa  c&aacute;c n&uacute;t bấm c&oacute; kh&ocirc;ng gian, t&aacute;c động trở lại  khi ấn ph&iacute;m. C&oacute; thể n&oacute;i,  đ&acirc;y l&agrave; một trong những thiết bị c&oacute; b&agrave;n ph&iacute;m  trượt dễ sử dụng, thuận tay  nhất hiện nay.</p>\r\n<p class="Normal" style="text-align: justify;">Giống như TyTN II, m&agrave;n  h&igrave;nh của Pro 2 cũng cho ph&eacute;p  dựng chếch l&ecirc;n, cho ph&eacute;p mang tới g&oacute;c nh&igrave;n  thuận tiện hơn khi soạn thảo.  Ngo&agrave;i ra, với phương nh&igrave;nh chếch l&ecirc;n,  người d&ugrave;ng cũng c&oacute; thể dựng m&aacute;y  v&agrave; xem phim hay nhận điện thoại cuộc  gọi video tốt hơn.</p>\r\n<p class="Normal" style="text-align: justify;">Giống Touch Diamond 2,  model n&agrave;y kh&ocirc;ng c&oacute; ph&iacute;m điều  hướng. Mọi điều khiển gần như th&ocirc;ng qua  m&agrave;n h&igrave;nh cảm ứng rộng. Pro 2  trang bị cổng giao tiếp mini-USD, cho ph&eacute;p  sạc, đồng bộ m&aacute;y t&iacute;nh v&agrave; kết  nối với tai nghe nhạc.</p>\r\n<table style="width: 1px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img style="border: 1px solid black;" src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/05/22/touch-pro-2.jpg" alt="M&aacute;y c&oacute; b&agrave;n ph&iacute;m QWERTY với c&aacute;c n&uacute;t bấm tiện dụng. Ảnh:  Quốc Huy." width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="text-align: center;"><em>M&aacute;y c&oacute; b&agrave;n ph&iacute;m QWERTY với c&aacute;c n&uacute;t bấm tiện dụng. Ảnh:  <em>Quốc Huy.</em></em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: justify;"><span style="color: #2f2f2f;"><strong>L&agrave; chiếc di động cao cấp  d&agrave;nh cho doanh nh&acirc;n, Touch Pro 2 sỡ hữu một loạt c&aacute;c t&iacute;nh năng</strong>.</span> M&aacute;y cho ph&eacute;p người d&ugrave;ng lựa chọn c&aacute;c kết nối HSDPA, Wi-Fi, Bluetoot v&agrave;  định vị to&agrave;n cầu GPS.</p>\r\n<p class="Normal" style="text-align: justify;">Giao diện TouchFLO 3D  tr&ecirc;n model n&agrave;y đ&atilde; xuất hiện tr&ecirc;n  Diamond 2, nhưng c&oacute; th&ecirc;m nhiều cải  tiến. Đầu ti&ecirc;n l&agrave; ở m&agrave;n h&igrave;nh Home,  c&oacute; th&ecirc;m c&aacute;c nh&atilde;n lớn cho ph&eacute;p người  d&ugrave;ng gắn ứng dụng thứ ba v&agrave;o. Danh  bạ tr&ecirc;n TouchFLO 3D cũng hỗ trợ khả  năng tương t&aacute;c qua lại tốt hơn với  cuộc gọi, tin nhắn.</p>\r\n<p class="Normal" style="text-align: justify;">Xa hơn nữa, Pro 2 c&ograve;n hỗ  trợ t&ugrave;y chọn cho ph&eacute;p người  d&ugrave;ng t&iacute;ch hợp account Facebook với sổ địa  chỉ, cho ph&eacute;p cập nhật status  h&agrave;ng ng&agrave;y. Kh&aacute;c với Touch Pro phi&ecirc;n bản  đầu, m&agrave;n h&igrave;nh Home c&oacute; thể hiển  thị ở chế độ nằm ngang khi trượt b&agrave;n  ph&iacute;m ra.</p>\r\n<p class="Normal" style="text-align: justify;">Tr&igrave;nh duyệt nguy&ecirc;n bản  tr&ecirc;n Pro 2 l&agrave; Opera Mobile, ứng  dụng n&agrave;y được c&agrave;i trực tiếp tr&ecirc;n bản  ROM theo m&aacute;y. Theo đ&oacute;, người d&ugrave;ng  c&oacute; thể tải trang web, hiển thị h&igrave;nh  ảnh nhanh hơn.</p>\r\n<p class="Normal" style="text-align: justify;">Mặt sau của Pro 2 l&agrave; ph&iacute;m  bấm speakerphone kh&aacute; lớn,  cho ph&eacute;p người d&ugrave;ng nghe điện với &acirc;m thanh  lớn hơn v&agrave; hỗ trợ giảm tiếng  ồn. HTC gọi to&agrave;n bộ g&oacute;i ứng dụng của m&igrave;nh  l&agrave; c&ocirc;ng nghệ Straight Talk.  Thử nghiệm thực tế cho thấy, t&iacute;nh năng n&agrave;y  dễ sử dụng, cho ph&eacute;p bật, tắt  nhanh.</p>\r\n<p class="Normal" style="text-align: justify;">Mặt sau của m&aacute;y, camera  3,2 Megapixel hỗ trợ chế độ tự  động canh n&eacute;t. C&aacute;c bức ảnh c&oacute; chất lượng  kh&aacute; ổn, tuy nhi&ecirc;n lấy n&eacute;t  chậm. Do đ&oacute;, h&igrave;nh ảnh hoạt động rất kh&oacute; để  chụp đẹp.</p>\r\n<table style="width: 1px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img style="border: 1px solid black;" src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/05/22/touch-pro-3.jpg" alt="Ph&iacute;m bấm s" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="text-align: center;"><em>Ph&iacute;m bấm speakerphone nằm sau cho &acirc;m thanh tốt với  c&ocirc;ng nghệ Straight Talk. Ảnh: <em>Quốc Huy</em>.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: justify;"><span style="color: #2f2f2f;"><strong>HTC Touch Pro 2 trang bị  pin c&oacute; dung lượng lớn, tới 1.500 mAh</strong>.</span> Sử dụng khoảng 2  ng&agrave;y với phải sạc lại, đ&acirc;y l&agrave; thời gian d&ugrave;ng tốt hơn  nhiều so với c&aacute;c  d&ograve;ng điện thoại th&ocirc;ng minh kh&aacute;c. Chất lượng cuộc gọi  sạch sẽ v&agrave; kh&ocirc;ng hề  bị "rớt" mạng.</p>\r\n<p class="Normal" style="text-align: justify;">Một trong những điểm đ&aacute;ng  biểu dương Pro 2 ch&iacute;nh l&agrave;  khả năng đ&agrave;m thoại video. Chất lượng thoại  tuyệt vời nhờ t&iacute;nh năng  Straight Talk, tuy nhi&ecirc;n h&igrave;nh ảnh thu được chưa  r&otilde; n&eacute;t.</p>\r\n<p class="Normal" style="text-align: justify;">Chiếc PDA phone d&agrave;nh cho  doanh nh&acirc;n n&agrave;y c&oacute; bộ nhớ RAM  288 MB, tốc độ bộ vi xử l&yacute; 528 MHz, chạy  c&aacute;c chương tr&igrave;nh mượt m&agrave;. Pro 2  sử dụng hệ điều h&agrave;nh Windows Mobile  6.1, HTC sẽ cho ph&eacute;p người d&ugrave;ng cập  nhật miễn ph&iacute; l&ecirc;n bản 6.5 khi nền  tảng n&agrave;y ra mắt cuối năm nay.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (523, 40, 1, 'SONY ERICSSON SATIO U1', 'Điện thoại cảm ứng màn hình rộng\r\nMáy ảnh12 MP, camera phụ VGA thực hiện Videocall\r\nMáy nghe nhạc MP3/WMA/WAV/RA/AAC/M4A\r\nXem phim WMV/RV/MP4/3GP\r\nKết nối 3G, GPS, Wifi, Bluetooth\r\nĐài FM\r\nOut-Tivi, Google map', '2011-11-02', 9989000, 'U1_satio_b.jpg', '<div class="newsintro">\r\n<p style="text-align: justify;">Ấn tượng với khả  năng chia sẻ v&agrave; cấu h&igrave;nh "khủng", nhưng Sony Ericsson  Satio bị ch&ecirc; v&igrave;  nắp đậy pin d&agrave;y v&agrave; kh&ocirc;ng c&oacute; giắc cắm 3,5 mm.</p>\r\n</div>\r\n<table style="width: 1px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img style="border: 1px solid black;" src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/07/1F/s1.jpg" alt="Satio với m&agrave;n h&igrave;nh cảm ứng rộng ra  tới tận bi&ecirc;n. Ảnh: Cnet." width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="text-align: center;"><em>Satio với m&agrave;n h&igrave;nh cảm ứng rộng ra tới tận bi&ecirc;n. Ảnh: <em>Cnet.</em></em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: justify;">Khi Sony Ericsson giới  thiệu phi&ecirc;n bản demo chiếc di  động giải tr&iacute; đa phương tiện với t&ecirc;n m&atilde;  Idou tại MWC th&aacute;ng 2 năm nay,  trọng t&acirc;m của model n&agrave;y được h&atilde;ng giới  thiệu kh&ocirc;ng chỉ l&agrave; c&aacute;c trang bị  phần cứng "khủng".</p>\r\n<p class="Normal" style="text-align: justify;">Chủ đề m&agrave; li&ecirc;n minh Nhật  Bản - Thụy Điển giới thiệu  ch&iacute;nh l&agrave; việc h&atilde;ng khai trương d&ograve;ng  Entertainment Unlimited, nhắm v&agrave;o  c&aacute;c t&iacute;nh năng chia sẻ nội dung giải  tr&iacute; đa phương tiện qua nhiều nền  tảng một c&aacute;ch dễ d&agrave;ng, như từ di động  qua PC hay TV. Một phần quan trọng  trong đ&oacute; l&agrave; Media Go, chương tr&igrave;nh  xuất hiện tr&ecirc;n Satio v&agrave; W995 Walkman  cho ph&eacute;p nguời d&ugrave;ng truyền tải v&agrave;  đồng bộ c&aacute;c file giải tr&iacute;.</p>\r\n<p class="Normal" style="text-align: justify;">Bước đi của Sony Ericsson  c&oacute; thể được coi l&agrave; chậm hơn  c&aacute;c đối thủ, khi m&agrave; Nokia đ&atilde; giới thiệu  Ovi, hay Apple iPhone với một  loạt c&aacute;c ứng dụng rất th&agrave;nh c&ocirc;ng th&ocirc;ng  qua phần mềm iTunes. Satio được  chờ mong l&agrave; chiếc di động mang lại cho  h&atilde;ng những th&agrave;nh c&ocirc;ng mới, vực  dậy tiếng tăm. Model n&agrave;y chạy tr&ecirc;n giao  diện S60 phi&ecirc;n bản 5, đ&acirc;y l&agrave;  si&ecirc;u phẩm đ&aacute;ng ch&uacute; &yacute; nhất của Sony  Ericsson năm nay giống như Xperia X1  chạy Windows Mobile năm ngo&aacute;i.</p>\r\n<table style="width: 1px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img style="border: 1px solid black;" src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/07/1F/s2.jpg" alt="Cạnh của m&aacute;y. Ảnh: Cnet." width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="text-align: center;"><em>Cạnh tr&aacute;i của m&aacute;y. Ảnh: <em>Cnet.</em></em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: justify;">M&agrave;n h&igrave;nh lớn v&agrave; hiển thị  r&otilde; r&agrave;ng l&agrave; một trong những  t&iacute;nh năng được ch&uacute; &yacute; nhất ở Satio, model  mang nhiều dấu ấn từ Xperia X1  để lại. Tấm nền của m&aacute;y c&oacute; k&iacute;ch thước  3,5 inch, độ ph&acirc;n giải l&ecirc;n tới 640  x 360 pixel. M&agrave;n h&igrave;nh của m&aacute;y rộng  tương đương với Nokia N97, nhưng nhỏ  hơn 0,2 inch so với Samsung Omnia  HD.</p>\r\n<p class="Normal" style="text-align: justify;">M&agrave;n h&igrave;nh cảm ứng của  Satio rộng ra tới tận bi&ecirc;n v&agrave; l&agrave;  chuẩn cảm ứng điện trở, điều đ&oacute; c&oacute;  nghĩa m&aacute;y sẽ kh&ocirc;ng nhạy như iPhone,  nhưng cho ph&eacute;p d&ugrave;ng b&uacute;t, viết chữ  một c&aacute;ch ch&iacute;nh x&aacute;c. M&aacute;y cũng trang bị  t&iacute;nh năng rung phản hồi kh&aacute; ấn  tượng.</p>\r\n<p class="Normal" style="text-align: justify;">Trong lần ra mắt đầu ti&ecirc;n  v&agrave;o th&aacute;ng 2, Sony Ericsson  cho biết, Satio (Idou) chạy tr&ecirc;n phần mềm  Symbian Foundation, tuy nhi&ecirc;n  hiện tại, h&atilde;ng đ&atilde; x&aacute;c nhận model n&agrave;y sẽ  sử dụng hệ điều h&agrave;nh Symbian S60  phi&ecirc;n bản 5. Nền tảng n&agrave;y đ&atilde; được sử  dụng tr&ecirc;n c&aacute;c model n&agrave;y 5800  XpressMusic, N97 của Nokia đ&atilde; xuất hiện  tr&ecirc;n thị trường v&agrave; sắp tới l&agrave;  5530 XpressMusic v&agrave; Samsung Omnia HD.</p>\r\n<p class="Normal" style="text-align: justify;">Sony Ericsson Satio sẽ  xuất hiện tr&ecirc;n thị trường v&agrave;o  đầu qu&yacute; IV năm nay, thời điểm trước kỳ  mua sắm lớn nhất trong năm. M&aacute;y  chạy tr&ecirc;n giao diện S60 5th editio,  người d&ugrave;ng c&oacute; thể đặt c&aacute;c tab tr&ecirc;n  m&agrave;n h&igrave;nh Home, th&ecirc;m danh bạ y&ecirc;u  thich, c&aacute;c trang web, h&igrave;nh ảnh v&agrave; ứng  dụng. Giao diện mới về cơ bản  kh&ocirc;ng kh&aacute;c với c&aacute;c thiết bị c&ugrave;ng hệ điều  h&agrave;nh, tuy nhi&ecirc;n điểm nổi bật  l&agrave; Sony Ericsson trang bị đồ họa đẹp v&agrave;  long lanh hơn.</p>\r\n<table style="width: 1px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img style="border: 1px solid black;" src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/07/1F/s3.jpg" alt="Cạnh phải của Sony Ericsson Satio. Ảnh: Cnet." width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="text-align: center;"><em>Cạnh phải của Sony Ericsson Satio. Ảnh: <em>Cnet.</em></em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: justify;">Satio l&agrave; chiếc điện thoại  12 Megapixel đầu ti&ecirc;n được  c&ocirc;ng bố, tuy nhi&ecirc;n c&oacute; thể kh&ocirc;ng phải l&agrave;  model đầu ti&ecirc;n được b&aacute;n ra tr&ecirc;n  thị trường. Samsung Pixon12 sẽ c&oacute; mặt  sớm hơn, m&aacute;y được b&aacute;n v&agrave;o cuối  th&aacute;ng 6 ở ch&acirc;u &Acirc;u v&agrave; đến qu&yacute; III sẽ đến  ch&acirc;u &Aacute;.</p>\r\n<p class="Normal" style="text-align: justify;">Hai năm trước, Sony  Ericsson đ&atilde; b&aacute;n K850i Cyber-shot,  model được trang bị cả đ&egrave;n flash  Xenon lẫn LED, điều n&agrave;y cũng được lặp  lại tr&ecirc;n Satio. Ngo&agrave;i ra, m&aacute;y c&ograve;n  hỗ trợ th&ecirc;m c&aacute;c t&iacute;nh năng như nhận  dạng khu&ocirc;n mặt, nụ cười, chạm v&agrave;o  m&agrave;n h&igrave;nh lấy n&eacute;t.</p>\r\n<p class="Normal" style="text-align: justify;">C&aacute;c trang bị kh&aacute;c của  Satio cũng n&ecirc;n cho điểm cộng l&agrave;  m&aacute;y chạy tr&ecirc;n 4 băng tần GSM với kết  nối UMTS/HSPA, Bluetooth stereo, hỗ  trợ chế độ tự động xoay m&agrave;n h&igrave;nh.  M&aacute;y cũng trang bị khe cắm thẻ nhớ  microSD, GPS v&agrave; Microsoft Exchange  ActiveSync.</p>\r\n<table style="width: 1px;" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img style="border: 1px solid black;" src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/07/1F/s4.jpg" alt="Nắp đậy camera của Satio qu&aacute; d&agrave;y. Ảnh: Cnet." width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="text-align: center;"><em>Nắp đậy camera của Satio qu&aacute; d&agrave;y. Ảnh: <em>Cnet.</em></em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: justify;">Giống C905 Cyber-shot của  h&atilde;ng ra mắt năm ngo&aacute;i, Satio  cũng c&oacute; nắp đậy ph&iacute;a sau camera nổi cao  v&agrave; rất lớn, thiết kế l&agrave;m cho  m&aacute;y trở n&ecirc;n d&agrave;y hơn v&agrave; tr&ocirc;ng xấu. Trong  khi W995 trang bị giắc cắm tai  nghe 3,5 mm thuận tiện th&igrave; Satio lại  kh&ocirc;ng, m&agrave; đi chung đường kết nối  USB với m&aacute;y tinh. Bộ nhớ trong của m&aacute;y  chỉ 128 MB, so s&aacute;nh với c&aacute;c si&ecirc;u  phẩm kh&aacute;c như N97 c&oacute; dung lượng 32 GB  hay Omnia HD l&agrave; 8 GB.</p>\r\n<p class="Normal" style="text-align: justify;">Một điều kh&aacute; kh&oacute; hiểu l&agrave;  đến nay, Sony Ericsson vẫn  chưa đưa ra th&ocirc;ng số về thời gian sử dụng  pin, mặc d&ugrave; m&aacute;y trang bị pin  dung lượng 1.000 mAh.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (524, 41, 1, 'HTC ARIA', 'Hệ điều hành Android 2.1\r\nMáy ảnh 5.0 MP, lấy nét tự động\r\nMạng 3G với tốc độ truyền tải 7.2 Mbps\r\nKết nối Wifi, GPS, Bluetooth\r\nGoogle Search, Maps, Gmail\r\nYouTube, Google Talk, Picasa integration', '2011-11-02', 9949000, 'aria_b.jpg', '<h2 style="color: #ff9501; padding: 5px 0px 3px 10px; margin: 0px; font-weight: bolder;">HTC Aria - ng&ocirc;i sao mới của smartphone Android tầm trung</h2>\r\n<div class="news_description" style="color: #606060; padding: 0px 0px 0px 10px; margin-bottom: 10px;">Thiết kế đơn giản, chắc chắn m&agrave; tinh tế của HTC Aria được nhiều người đ&aacute;nh gi&aacute; cao ở ph&acirc;n kh&uacute;c điện thoại th&ocirc;ng minh nền tảng Android tầm trung.</div>\r\n<div class="new_pro_detail_contet" style="padding: 5px 3px 5px 10px;">\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">Về ngoại h&igrave;nh, HTC Aria chỉ kh&aacute;c HD Mini ở c&aacute;c n&uacute;t bấm b&ecirc;n dưới m&agrave;n h&igrave;nh. C&aacute;c n&uacute;t bấm tr&ecirc;n HD Mini được thay bằng c&aacute;c ph&iacute;m cảm ứng Home, Menu, Back, v&agrave; Search theo đ&uacute;ng phong c&aacute;ch của hệ điều h&agrave;nh Google Android. Ph&iacute;a giữa c&aacute;c ph&iacute;m tr&ecirc;n l&agrave; n&uacute;m điều hướng quang học giống như ở &ldquo;h&agrave;ng khủng&rdquo; Motorola Droid Incredible.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">HTC Aria c&oacute; m&agrave;n h&igrave;nh HVGA rộng 3,2 inch, độ ph&acirc;n giải 480 x 320 pixel hỗ trợ cảm ứng đa điểm. Trong ph&ograve;ng, m&agrave;n h&igrave;nh hiển thị s&aacute;ng r&otilde;, h&igrave;nh ảnh v&agrave; video đều sắc n&eacute;t, m&agrave;u sống động nhưng chưa thể so s&aacute;nh với c&aacute;c m&agrave;n h&igrave;nh độ ph&acirc;n giải cao như ở Google Nexus One. Ngo&agrave;i trời, dưới &aacute;nh s&aacute;ng trực tiếp, m&agrave;n h&igrave;nh gần như &ldquo;v&ocirc; dụng&rdquo; v&igrave; l&oacute;a do lớp gương phủ qu&aacute; b&oacute;ng.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;"><img title="HTC Aria - ng&ocirc;i sao mới của smartphone Android tầm trung" src="http://images.thegioididong.com/Files/2010/07/20/14392/2_HTC-Aria---ngoi-sao-moi-cua-smartphone-Android-tam.jpg" alt="HTC Aria - ng&ocirc;i sao mới của smartphone Android tầm trung" /></span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">Với k&iacute;ch thước vừa xinh 11,7 x 5,8cm, mỏng 12mm v&agrave; nặng gần 250mg, Aria nằm gọn g&agrave;ng trong t&uacute;i &aacute;o của người d&ugrave;ng v&agrave; vừa tay khi sử dụng. Thiết kế mới được t&ocirc; điểm bởi đường viền cr&ocirc;m ở cạnh tr&ecirc;n v&agrave; n&uacute;t bấm tăng giảm &acirc;m lượng. C&aacute;c ốc v&iacute;t kim loại s&aacute;ng lo&aacute;ng ở mặt sau tăng th&ecirc;m vẻ chắc chắn v&agrave; cao cấp của smartphone xinh xắn n&agrave;y. Một điểm nhấn kh&aacute;c, ruột m&aacute;y được sơn một m&agrave;u v&agrave;ng tươi trẻ lại ở ph&iacute;a trong &iacute;t lộ ra. B&ecirc;n cạnh tr&aacute;i l&agrave; n&uacute;t tăng giảm &acirc;m lượng, tr&ecirc;n đỉnh l&agrave; ph&iacute;m nguồn v&agrave; giắc cắm headphone 3,5 mm. Micro t&iacute;ch hợp v&agrave; ch&acirc;n kết nối USB được đặt ở cạnh dưới của điện thoại.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;"><img title="HTC Aria - ng&ocirc;i sao mới của smartphone Android tầm trung" src="http://images.thegioididong.com/Files/2010/07/20/14392/3_HTC-Aria---ngoi-sao-moi-cua-smartphone-Android-tam.jpg" alt="HTC Aria - ng&ocirc;i sao mới của smartphone Android tầm trung" /></span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">Thực sự giao diện Sense tr&ecirc;n nền hệ điều h&agrave;nh Android 2.1 của HTC Aria d&ugrave;ng rất tuyệt, c&aacute;c chuy&ecirc;n gia của LaptopMag đ&aacute;nh gi&aacute;. Ngo&agrave;i ra, giao diện phần mềm chơi nhạc Sense Music App cũng kh&aacute; bắt mắt, hơi giống của Apple iPod. Nghe nhạc v&agrave; xem video trực tuyến từ YouTube mượt m&agrave; qua mạng 3G, chỉ c&oacute; điều loa của Aria khi mở tối đa sẽ hơi bị m&eacute;o tiếng.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">HTC Aria, thuộc ph&acirc;n kh&uacute;c smartphone tầm trung, với cấu h&igrave;nh vừa phải sử dụng bộ vi xử l&yacute; Qualcomm MSM 7227 tốc độ 600 MHz, bộ nhớ RAM 384MB, ROM 512MB. Điện thoại hỗ trợ đầy đủ c&aacute;c kết nối như Bluetooth, Wi-Fi v&agrave; 3G HSPA 7,2 Mbs, GPS, t&iacute;ch hợp sẵn la b&agrave;n số v&agrave; trang bị cả cảm biến &aacute;nh s&aacute;ng.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">Phần cứng kh&ocirc;ng qu&aacute; mạnh nhưng khả năng xử l&yacute;, chuyển đổi giữa c&aacute;c ứng dụng phần mềm rất nhanh v&agrave; thuyết phục. Bản đồ Google Maps chạy mượt ngay cả khi đột ngột chuyển sang d&ugrave;ng email khi c&oacute; mail mới đến, hay c&ugrave;ng l&uacute;c lướt web v&agrave; tải phần mềm. Thời gian truy cập c&aacute;c trang web tr&ecirc;n mạng 3G của AT&amp;T rất nhanh trung b&igrave;nh khoảng 8 gi&acirc;y. V&agrave; để download file dữ liệu 1,22 MB về m&aacute;y chỉ cần c&oacute; 8,8 gi&acirc;y.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;"><img title="HTC Aria - ng&ocirc;i sao mới của smartphone Android tầm trung" src="http://images.thegioididong.com/Files/2010/07/20/14392/4_HTC-Aria---ngoi-sao-moi-cua-smartphone-Android-tam.jpg" alt="HTC Aria - ng&ocirc;i sao mới của smartphone Android tầm trung" /></span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">Camera 5 megapixel chụp ảnh với tốc độ lấy n&eacute;t nhanh, trong điều kiện &aacute;nh s&aacute;ng tốt cho ảnh chất lượng v&agrave; chi tiết. Với c&aacute;c cảnh chuyển động nhanh như người đi bộ tr&ecirc;n vỉa h&egrave; hay d&ograve;ng xe qua lại tấp nập tr&ecirc;n đường phố, quay video bằng điện thoại ở độ ph&acirc;n giải 640 x 480 (VGA), tốc độ 28 khung h&igrave;nh/gi&acirc;y, h&igrave;nh ảnh kh&aacute; r&otilde; n&eacute;t v&agrave; kh&ocirc;ng bị rung. Nhưng v&igrave; kh&ocirc;ng được trang bị đ&egrave;n flash, chụp ảnh buổi tối l&agrave; kh&ocirc;ng khả thi ngoại trừ h&igrave;nh chụp thu về chỉ l&agrave; những vệt đ&egrave;n đường s&aacute;ng l&oacute;a.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">Khả năng tiếp nhận s&oacute;ng di động của HTC Aria kh&ocirc;ng gặp trục trặc g&igrave; v&agrave; chất lượng thoại rất tốt, ngay cả khi nhận c&aacute;c cuộc gọi đường d&agrave;i hay quốc tế. Song nếu để &acirc;m lượng qu&aacute; lớn, tiếng đ&agrave;m thoại sẽ bị m&eacute;o. Điểm cần cải tiến hơn nữa ở điện thoại n&agrave;y l&agrave; hệ thống loa ngo&agrave;i. HTC Aria chạy tr&ecirc;n mạng GSM của AT&amp;T, hoạt động tốt tr&ecirc;n tất cả c&aacute;c tần số 850/900/1800 hay 1900MHz. HTC Aria c&oacute; thời lượng đ&agrave;m thoại li&ecirc;n tục l&ecirc;n tới 6 giờ. Pin của m&aacute;y c&oacute; thể lướt web tr&ecirc;n mạng 3G trong 4 giờ 32 ph&uacute;t, đủ để người d&ugrave;ng phổ th&ocirc;ng y&ecirc;n t&acirc;m sử dụng cả ng&agrave;y.</span></p>\r\n<p style="margin: 14px 0px; line-height: 18px;"><span class="PBody" style="font-size: 11px;">Tại Mỹ, HTC Aria được nh&agrave; mạng AT&amp;T cung cấp với gi&aacute; chỉ 129,99 USD nhưng k&egrave;m hợp đồng 2 năm sử dụng bắt buộc. C&ograve;n ở Việt Nam, phi&ecirc;n bản smartphone Android xinh xắn với ng&ocirc;n ngữ tiếng Việt sẽ được ch&iacute;nh thức b&aacute;n ra v&agrave;o th&aacute;ng 8 với mức gi&aacute; tương đương HTC HD mini, tr&ecirc;n 10 triệu đồng.</span></p>\r\n</div>\r\n<p><br class="Apple-interchange-newline" /></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (525, 43, 1, 'ACER LIQUID E SMART S100', 'Hệ điều hành Android v2.1\r\n256MB RAM, 512MB ROM\r\nCPU: Qualcomm Snapdragon 8250, 768 MHz processor\r\nMáy ảnh 5.0 MP, hỗ trợ lấy nét tự động\r\nMạng 3G với HSDPA, 7.2 Mbps\r\nHSUPA, 2.0 Mbps\r\nXem văn bản (Word, Excel, PowerPoint, PDF)', '2011-11-02', 9799000, '0acer_liquid-e_b.jpg', '<div class="newstitle">\r\n<h1>Liquid E - Điện thoại Acer đầu ti&ecirc;n sử dụng Android 2.1</h1>\r\n</div>\r\n<div class="newsintro">\r\n<p style="text-align: justify;">Nh&agrave; sản xuất m&aacute;y  t&iacute;nh Acer vừa th&ocirc;ng b&aacute;o cho ra mắt sản phẩm điện thoại  đầu ti&ecirc;n của  h&atilde;ng chạy tr&ecirc;n hệ điều h&agrave;nh Android 2.1. Thiết bị n&agrave;y c&oacute;  t&ecirc;n gọi Liquid  E.</p>\r\n</div>\r\n<p style="text-align: center;"><img style="border: 1px solid black;" src="http://www.mainguyen.vn/img/inposts/1003/Acer-Liquid.jpg" alt="" /></p>\r\n<p style="text-align: justify;">L&atilde;nh đạo của Acer cho biết, Liquid E l&agrave;  bản cập nhật của chiếc Liquid A1 đ&atilde; xuất hiện tr&ecirc;n thị trường v&agrave;o nửa  cuối năm 2009. Đ&acirc;y l&agrave; thiết bị đầu ti&ecirc;n m&agrave; Acer sử dụng hệ điều h&agrave;nh mới  nhất hiện nay, Android 2.1.<br /><br />C&aacute;c th&ocirc;ng tin ban đầu cho thấy,  Liquid E được trang bị m&agrave;n h&igrave;nh cảm ứng 3,5 inch với độ ph&acirc;n giải 480 x  800 pixel, camera 5 MP hỗ trợ tự động lấy n&eacute;t tự động, RAM 256MB, ROM  512MB.<br /><br />Ngo&agrave;i ra, Liquid E t&iacute;ch hợp c&ocirc;ng nghệ định vị A-GPS, kết  nối bluetooth, hỗ trợ thẻ nhớ ngo&agrave;i l&ecirc;n tới 32 GB, giắc cắm tay nghe  chuẩn 3,5 mm. Sản phẩm mới của Acer sử dụng bộ xử l&yacute; Qualcomm Snapdragon  8250 với tốc độ 768 MHz.<br /><br />B&ecirc;n cạnh đ&oacute;, Liquid E sở hữu một loạt  c&aacute;c t&iacute;nh năng của hệ điều h&agrave;nh Android 2.1, MMS, Email v&agrave; IM, hỗ trợ  tr&igrave;nh duyệt web HTML5. K&iacute;ch thước của m&aacute;y kh&aacute; nhỏ gọn: 115 x 62.5 x 12.5  mm, nặng 135g.<br /><br />Theo kế hoạch, điện thoại Liquid E sẽ c&oacute; mặt tr&ecirc;n  thị trường trong nửa đầu năm nay với gi&aacute; 500 USD th&ocirc;ng qua nh&agrave; ph&acirc;n  phối Rogers. Sản phẩm sẽ đến tay người d&ugrave;ng với 3 m&agrave;u: đen, trắng v&agrave; đỏ.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (526, 39, 1, 'SAMSUNG S8530 WAVE II', 'Hệ điều hành Bada 1.2\r\nMáy ảnh 5.0 MP, hỗ trợ máy ảnh phụ\r\nQuay phim chuẩn HD\r\nMạng 3G với tốc độ truyền tải 7.2 Mbps\r\nCông nghệ âm thanh DNSe (Digital Natural Sound Engine)\r\nKết nối Wifi, Tivi, Bluetooth,..', '2011-11-02', 9479000, 'samsung_s8530_b.jpg', '<h2 style="color: #ff9501; padding: 5px 0px 3px 10px; margin: 0px; font-weight: bolder;">Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch</h2>\r\n<div class="news_description" style="color: #606060; padding: 0px 0px 0px 10px; margin-bottom: 10px;">Samsung vừa ra mắt phi&ecirc;n bản nối tiếp Wave S8500 mang t&ecirc;n S8530 với m&agrave;n h&igrave;nh Super Clear LCD rộng 3,7 inch, vi xử l&yacute; 1GHz, quay video HD.</div>\r\n<div class="new_pro_detail_contet" style="padding: 5px 3px 5px 10px;">\r\n<table style="border-collapse: collapse; background-color: #ffffff;" border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" src="http://images.thegioididong.com/Files/2010/10/05/22202/41_Samsung-gioi-thieu-Wave-II-man-hinh-37-inch.jpg" border="1" alt="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" width="480" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Wave II với m&agrave;n h&igrave;nh rộng hơn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="margin: 14px 0px; line-height: 18px;">Wave II cao cấp hơn c&aacute;c bản Wave 2, Wave 2 Pro v&agrave; Wave 723 đ&atilde; được giới thiệu. Đ&acirc;y l&agrave; chiếc smartphone n&agrave;y l&agrave; thiết bị thứ 5 chạy Bada được c&ocirc;ng bố, m&aacute;y n&acirc;ng cấp c&aacute;c t&iacute;nh năng của Wave S8500.</p>\r\n<p class="Normal" style="margin: 14px 0px; line-height: 18px;">Thay đổi với phi&ecirc;n bản đ&agrave;n anh l&agrave; model n&agrave;y sử dụng m&agrave;n h&igrave;nh Super Clear LCD thay cho Super AMOLED. M&agrave;n h&igrave;nh của m&aacute;y rộng 3,7 inch, độ ph&acirc;n giải WVGA.</p>\r\n<p class="Normal" style="margin: 14px 0px; line-height: 18px;">Chiếc Bada mới c&oacute; vi xử l&yacute; 1GHz, camera 5 Megapixel với đ&egrave;n flash LED, quay video HD 720p. Wave II hỗ trợ kết nối Bluetooth 3.0, Wi-Fi chuẩn b/g/n, cho ph&eacute;p c&agrave;i hơn 1.000 ứng dụng từ Samsung Apps, trong đ&oacute; phần lớn l&agrave; miễn ph&iacute;.</p>\r\n<p class="Normal" style="margin: 14px 0px; line-height: 18px;">Wave II sẽ c&oacute; mặt tại ch&acirc;u &Acirc;u trong th&aacute;ng 11 với gi&aacute; khoảng 590 USD (gần 12 triệu đồng).</p>\r\n<p class="Normal" style="margin: 14px 0px; line-height: 18px;">&nbsp;</p>\r\n<h1 class="Title">Samsung Wave II S8530</h1>\r\n<table style="border-collapse: collapse; background-color: #ffffff;" border="0" cellspacing="2" cellpadding="2" width="80%" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" src="http://images.thegioididong.com/Files/2010/10/05/22202/42_Samsung-gioi-thieu-Wave-II-man-hinh-37-inch.jpg" border="1" alt="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" width="480" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" src="http://images.thegioididong.com/Files/2010/10/05/22202/43_Samsung-gioi-thieu-Wave-II-man-hinh-37-inch.jpg" border="1" alt="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" width="480" height="612" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" src="http://images.thegioididong.com/Files/2010/10/05/22202/44_Samsung-gioi-thieu-Wave-II-man-hinh-37-inch.jpg" border="1" alt="Samsung giới thiệu Wave II m&agrave;n h&igrave;nh 3.7 inch" width="480" height="430" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (527, 41, 1, 'HTC HERO', 'Kết nối 3.5G tốc độ cao và công nghệ Wi-Fi\r\nTăng khả năng duyệt web với việc mở nhiều cửa sổ cùng lúc và tìm từ khoá ngay trên trang\r\nLuôn giữ liên lạc với văn phòng cùng Microsoft Exchange ActiveSync®\r\nTrình quản lý ứng dụng - SlideME với khả năng truy c', '2011-11-02', 8999000, 'HTC Hero_1.jpg', '<h1 class="review_title_7">HTC Hero: Cho &ldquo;t&iacute;n đồ&rdquo; Internet</h1>\r\n<p class="review_teaser_4">HTC  Hero chạy hệ điều h&agrave;nh Android, c&oacute; nhiều ứng dụng Internet phổ biến;  giao diện người d&ugrave;ng c&oacute; thể sẽ l&agrave;m bạn ngạc nhi&ecirc;n v&agrave; ưa th&iacute;ch.</p>\r\n<div><img src="http://www.vatgia.com/review_pictures/lxn1257479067.jpg" alt="" /></div>\r\n<p>&nbsp;<br /><a href="http://vatgia.com/438/400332/thong_so_ky_thuat/htc-hero-white-htc-hero-130.html">HTC Hero</a> l&agrave; smartphone thứ 2 của HTC chạy hệ điều h&agrave;nh (HĐH) Android được b&aacute;n tại VN.<br /><br />Hero  c&oacute; thiết kế gợi cho ch&uacute;ng ta nhớ về chiếc G1. Đu&ocirc;i m&aacute;y được uốn cong về  ph&iacute;a trước. Dĩ nhi&ecirc;n, k&iacute;ch thước của Hero nhỏ hơn G1, với &ldquo;số đo&rdquo; l&agrave;  112x56,2x14,4mm. Ch&uacute;ng ta cũng kh&ocirc;ng thấy b&agrave;n ph&iacute;m QWERTY vật l&yacute; tr&ecirc;n  Hero. Được biết, đ&acirc;y cũng l&agrave; sản phẩm c&oacute; kh&aacute; nhiều m&agrave;u sắc, gồm: đen,  trắng, n&acirc;u, hồng v&agrave; than ch&igrave;.<br /><br />Ph&iacute;a trước của m&aacute;y c&oacute; m&agrave;n h&igrave;nh TFT,  65.000, k&iacute;ch thước 3,2&rdquo;. Chất lượng hiển thị h&igrave;nh ảnh khiến cho ch&uacute;ng  t&ocirc;i phải th&aacute;n phục bởi chất lượng v&agrave; độ s&aacute;ng m&agrave; kh&ocirc;ng phải model  smartphone n&agrave;o hiện c&oacute; tr&ecirc;n thị trường cũng c&oacute; thể c&oacute; được. N&oacute; cũng &ldquo;bất  chấp&rdquo; cả &aacute;nh nắng mặt trời ch&oacute;i chang để hiển thị h&igrave;nh ảnh 1 c&aacute;ch tuyệt  vời. Tuy chưa biết HTC đ&atilde; sử dụng kỹ thuật g&igrave; trong việc chế tạo m&agrave;n  h&igrave;nh n&agrave;y nhưng c&oacute; lẽ th&ocirc;ng số độ ph&acirc;n giải 320x480pixel cũng phần n&agrave;o l&yacute;  giải cho chất lượng hiển thị h&igrave;nh ảnh của Hero. Theo GSMArena, hiện  tr&ecirc;n to&agrave;n cầu chỉ c&oacute; khoảng 17 model smartphone sử dụng m&agrave;n h&igrave;nh c&oacute; độ  ph&acirc;n giải n&oacute;i tr&ecirc;n, trong đ&oacute; c&oacute; cả c&aacute;c sản phẩm iPhone.<br /><br />Đ&acirc;y cũng  l&agrave; loại m&agrave;n h&igrave;nh cảm ứng, sử dụng c&ocirc;ng nghệ đa điểm v&agrave; đa chiều. Đặc  biệt, hầu hết ứng dụng quen thuộc đều được HTC đưa ra m&agrave;n h&igrave;nh chờ, với  nhiều &ldquo;trang&rdquo; kh&aacute;c nhau. Với HTC, người d&ugrave;ng &iacute;t phải để &yacute; đến &ldquo;Menu&rdquo;  ph&iacute;a trong. Bạn cứ chạm tay v&agrave;o m&agrave;n h&igrave;nh chờ v&agrave; k&eacute;o, như c&aacute;ch ch&uacute;ng ta  lật trang s&aacute;ch l&agrave; c&oacute; thể đi tới hầu hết c&aacute;c ứng dụng thường d&ugrave;ng. Thậm  ch&iacute;, c&oacute; nhiều ứng dụng bạn c&ograve;n c&oacute; thể chạm v&agrave; k&eacute;o từ tr&ecirc;n xuống như  k&eacute;o... r&egrave;m cửa số. C&ograve;n nhiều điều về giao diện m&agrave;n h&igrave;nh chờ của Hero để  bạn kh&aacute;m ph&aacute;. V&agrave; ch&uacute;ng dễ d&agrave;ng sử dụng cũng như c&oacute; nhiều tuỳ biến.<br /><br /></p>\r\n<div style="text-align: center;"><img src="http://www.vatgia.com/upload_images/Image/2009/Thang11/06/ht1.jpg" alt="" width="479" height="334" /></div>\r\n<p><br />Chạy  Andoird, điều trước ti&ecirc;n c&oacute; thể n&oacute;i, nếu bạn kh&ocirc;ng th&iacute;ch c&ocirc;ng nghệ th&igrave;  ban đầu c&oacute; thể bạn kh&oacute; chịu bởi n&oacute; c&oacute; vẻ hơi phức tạp, rối rắm, &iacute;t quen  thuộc như c&aacute;ch d&ugrave;ng Windows Mobile hay Symbian. Nhưng đổi lại bạn sẽ c&oacute;  những ứng dụng Internet phổ dụng nhất hiện nay. Gần như tất cả c&aacute;c ứng  dụng Internet đều được thể hiện dưới dạng widget. Ph&iacute;a dưới m&agrave;n h&igrave;nh  ch&iacute;nh l&agrave; hệ thống một v&agrave;i ph&iacute;m bấm vật l&yacute; để điều khiển m&aacute;y, c&aacute;c ph&iacute;m  n&agrave;y kh&aacute; tiện dụng nhưng tiếc l&agrave; k&iacute;ch thước lại hơi b&eacute;, điều n&agrave;y c&oacute; thể  l&agrave;m cho một số người d&ugrave;ng kh&ocirc;ng th&iacute;ch. D&ugrave; m&aacute;y ảnh số 5 megapixel với chế  độ tự động lấy n&eacute;t cho h&igrave;nh ảnh chụp chất lượng tốt lại vẫn (như hầu  hết smartphone kh&aacute;c của HTC) kh&ocirc;ng c&oacute; đ&egrave;n flash. Th&ecirc;m nữa, Hero kh&ocirc;ng c&oacute;  radio FM t&iacute;ch hợp.<br /><br /><br style="font-weight: bold;" /> <span style="font-weight: bold;">Ưu điểm: </span>Thiết  kế chắc chắn; chất lượng hiển thị h&igrave;nh ảnh tr&ecirc;n m&agrave;n h&igrave;nh tuyệt vời, sử  dụng c&ocirc;ng nghệ cảm ứng đa chạm, đa điểm; giao diện m&agrave;n h&igrave;nh chờ nhiều  tuỳ biến; c&aacute;c ứng dụng Internet phong ph&uacute;, hữu dụng.<br /> <br /> <span style="font-weight: bold;">Khuyết điểm: </span>kh&ocirc;ng c&oacute; đ&egrave;n flash; kh&ocirc;ng c&oacute; radio FM; c&aacute;c ph&iacute;m bấm vật l&yacute; hơi nhỏ.<br /> <br /> <span style="font-weight: bold;">Nhận x&eacute;t: </span>HTC  Hero chạy hệ điều h&agrave;nh Android, c&oacute; nhiều ứng dụng Internet phổ biến;  giao diện người d&ugrave;ng c&oacute; thể sẽ l&agrave;m bạn ngạc nhi&ecirc;n v&agrave; ưa th&iacute;ch.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (528, 41, 1, 'HTC HD7 ', '    * Hệ điều hành Microsoft Windows Phone 7\r\n    * Màn hình Super Clear LCD 16 triệu màu, rộng 4.3 inches\r\n    * Máy ảnh 5.0 MP (2592 x 1944 pixels)\r\n    * Nghe nhạc: MP3, WMA, WAV, eAAC+\r\n    * Xem Phim: 3GP, MP4, H.264(MPEG4-AVC), H.263, WMV\r\n    * Kết', '2011-11-02', 14990000, 'htc-hd7_b.jpg', '<p class="Normal">HTC h&ocirc;m qua đ&atilde; x&aacute;c nhận, h&atilde;ng sẽ ra mắt điện thoại chạy Windows Phone 7 trong th&aacute;ng 10 tới. Trong v&agrave;i ng&agrave;y gần đ&acirc;y, th&ocirc;ng tin v&agrave; c&aacute;c h&igrave;nh ảnh r&ograve; rỉ sản phẩm n&agrave;y li&ecirc;n tục xuất hiện tr&ecirc;n Internet.</p>\r\n<p class="Normal">Chiếc HTC HD7 đ&atilde; xuất hiện những h&igrave;nh ảnh đầu ti&ecirc;n với giao diện HTC Hub, được xem như Sense cho Windows Phone 7. Trang <em>HTCinside</em> cho biết, model n&agrave;y c&oacute; m&agrave;n h&igrave;nh WVGA 4,3 inch, vi xử l&yacute; Snapdragon SOC 1GHz, camera 5 Megapixel với hai đ&egrave;n flash LED, quay phim HD v&agrave; bộ nhớ 8GB.</p>\r\n<p class="Normal">Ngay sau đ&oacute;, h&igrave;nh ảnh về bản HD3 cũng xuất hiện tr&ecirc;n một trang web Trung Quốc với thiết kế dạng bar, ph&iacute;a sau c&oacute; ch&acirc;n chống v&agrave; k&yacute; hiệu Windows Phone 7.</p>\r\n<p class="Normal"><em>Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh c&aacute;c model n&agrave;y.</em></p>\r\n<table border="0" cellspacing="2" cellpadding="2" width="70%" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink">\r\n<p><img title="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" src="http://images.thegioididong.com/Files/2010/09/24/21622/1_Loat-hinh-anh-sieu-pham-HTC-HD7-va-HD3-ro-ri.jpg" border="1" alt="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" width="300" height="490" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">H&igrave;nh ảnh chiếc HD7 với giao diện HTC Hubs, model n&agrave;y sở ữu m&agrave;n h&igrave;nh 4,3 inch, tương đương HD2 hay Desire HD.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">\r\n<p><img title="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" src="http://images.thegioididong.com/Files/2010/09/24/21622/2_Loat-hinh-anh-sieu-pham-HTC-HD7-va-HD3-ro-ri.jpg" border="1" alt="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" width="438" height="345" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">H&igrave;nh ảnh chiếc HD3.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img title="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" src="http://images.thegioididong.com/Files/2010/09/24/21622/3_Loat-hinh-anh-sieu-pham-HTC-HD7-va-HD3-ro-ri.jpg" border="1" alt="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" width="449" height="345" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">M&aacute;y c&oacute; ch&acirc;n chống ph&iacute;a sau.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">\r\n<p><img title="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" src="http://images.thegioididong.com/Files/2010/09/24/21622/4_Loat-hinh-anh-sieu-pham-HTC-HD7-va-HD3-ro-ri.jpg" border="1" alt="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" width="500" height="375" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Giao diện Windows Phone 7.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">\r\n<p><img title="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" src="http://images.thegioididong.com/Files/2010/09/24/21622/5_Loat-hinh-anh-sieu-pham-HTC-HD7-va-HD3-ro-ri.jpg" border="1" alt="Loạt h&igrave;nh ảnh si&ecirc;u phẩm HTC HD7 v&agrave; HD3 r&ograve; rỉ" width="162" height="290" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Điện thoại chạy Windows Phone của HTC sẽ xuất hiện trong th&aacute;ng sau.</td>\r\n</tr>\r\n</tbody>\r\n</table>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (529, 41, 1, 'HTC TOUCH CRUISE T4242', 'Giao diện sử dụng HTC TouchFLO 3D trực quan và thân thiện với đầu ngón tay\r\nPocket Office (Word, Excel, PowerPoint, PDF viewer) và Push Email\r\nLướt web trên tốc độ băng thông rộng với WiFi hoặc HSDPA lên đến 7.2Mbps\r\nChức năng định vị GPS\r\nChế độ NaviPane', '2011-11-02', 8979000, 'HTC Touch Cruise 09_1.jpg', '<p><span style="font-size: medium;"><strong>Lưu lại từng khoảnh khắc đặc biệt của cuộc sống.</strong></span><br /><br />L&agrave;m cho những kỷ niệm sống m&atilde;i với&nbsp; HTC Touch Cruise&trade; t&iacute;ch hợp chức năng GPS. Nổi bật với sự đột ph&aacute; trong chức năng HTC Footprints&trade; mang t&iacute;nh c&aacute;ch mạng của HTC, người bạn đồng h&agrave;nh tr&ecirc;n tay n&agrave;y sẽ gi&uacute;p bạn lưu lại mỗi chuyến phi&ecirc;u lưu với c&aacute;c th&ocirc;ng tin ch&iacute;nh x&aacute;c v&agrave; đầy sống động của nơi bạn đặt ch&acirc;n đến c&ugrave;ng toạ độ cụ thể. Tự động lưu toạ độ n&agrave;y v&agrave;o mỗi bức ảnh bạn chụp, v&agrave; th&ecirc;m v&agrave;o đ&oacute; một ghi ch&uacute; hoặc đoạn &acirc;m thanh ngắn cho những kỷ niệm như thật để c&oacute; thể xem qua v&agrave; hồi tưởng lại trong tương lai. Viết n&ecirc;n một quyển tập san về những c&acirc;u chuyện th&uacute; vị của từng kỷ niệm đẹp nhất trong đời, gi&uacute;p bạn dễ d&agrave;ng t&igrave;m đường trở lại với những qu&aacute;n caf&eacute; &iacute;t được biết đến. hay khung cảnh xưa của lần cầu h&ocirc;n l&atilde;ng mạn của bạn.<br /><br />T&igrave;m đường quanh c&aacute;c th&agrave;nh phố lớn nhất hoặc những địa điểm xa nhất với việc dễ d&agrave;ng sử dụng của phần mềm bản đồ trực quan b&ecirc;n trong Touch Cruise&trade;. Khi đặt chiếc điện thoại th&ocirc;ng minh n&agrave;y v&agrave;o bộ gi&aacute; đỡ tr&ecirc;n xe hơi, bản điều khiển th&ocirc;ng minh (NaviPanel) sẽ tự động k&iacute;ch hoạt, một bảng giao diện một-chạm đầy tiện lợi thể hiện tất cả c&aacute;c c&ocirc;ng cụ cho việc di chuyển m&agrave; bạn cần trong c&aacute;ch tiếp cận dễ d&agrave;ng. T&iacute;nh năng &ldquo;Go-Home&rdquo; gi&uacute;p bạn t&igrave;m ra lộ tr&igrave;nh su&ocirc;n sẻ v&agrave; nhanh nhất để về nh&agrave;, thể hiện cả những dịch vụ dọc tr&ecirc;n đường đi.<br />Với một c&ocirc;ng nghệ th&ocirc;ng minh như &yacute;, việc đi lại sẽ dễ d&agrave;ng hơn bao giờ!<span class="Apple-converted-space">&nbsp;</span><br /><br />Tạo ra những dấu ch&acirc;n của cuộc sống v&agrave; vẽ lại từng bước đi đến những kỹ niệm tuyệt vời nhất của bạn &ndash; với Touch Cruise&trade; ho&agrave;n to&agrave;n mới.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (530, 41, 1, 'HTC Desire HD ', '    * Hệ điều hành Android 2.2 Froyo\r\n    * Màn hình cảm ứng đa điểm, rộng 4.3 inches\r\n    * Máy ảnh 8.0 MP, hỗ trợ LED flash kép\r\n    * Mạng 3G với HSDPA, 14.4 Mbps\r\n    * HSUPA, 5.76 Mbps\r\n    * Kết nối Wifi, A-GPS, Bluetooth \r\n\r\n', '2011-11-02', 14440000, 'htc_desire_hd_b.jpg', NULL, 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (531, 42, 1, 'MOTOROLA MILESTONE', 'Hệ điều hành Android 2.1 với màn hình cảm ứng rộng\r\nMáy ảnh 5.0 MP, LED Flash kép\r\nQuay phim với chuẩn D1 (720x480 pixels)@24fps\r\nMạng 3 với HSDPA, 10.2 Mbps\r\nHSUPA, 5.76 Mbps\r\nKết nối Internet với Wifi tốc độ cao\r\nBluetooth 2.1+ EDR\r\nGoogle Search, Maps,', '2011-11-02', 8888000, 'Motorola_Droid_b.jpg', '<p style="text-align: justify;">Chiếc Milestone 2 sẽ kh&ocirc;ng kh&aacute;c nhiều so với người anh em CDMA của m&igrave;nh với cấu h&igrave;nh cơ bản như:</p>\r\n<ul style="text-align: justify;">\r\n<li>Hệ điều h&agrave;nh Android 2.2 hỗ trợ full Flash 10.1.</li>\r\n<li> CPU TI OMAP xung nhịp 1GHz kết hợp GPU PowerSVG gi&uacute;p xử l&iacute; đồ họa mượt m&agrave;.</li>\r\n<li> RAM 512MB.</li>\r\n<li> M&agrave;n h&igrave;nh k&iacute;ch thước 3,7" độ ph&acirc;n giải 854x480 chuẩn 16:9.</li>\r\n<li> B&agrave;n ph&iacute;m QWERTY trượt ngang được thiết kế lại cho hợp l&iacute; hơn.</li>\r\n<li> Camera 5 megapixel, hỗ trợ lấy n&eacute;t tự động, quay phim chuẩn HD (c&oacute; lẽ l&agrave; HD 720p).</li>\r\n</ul>\r\n<p style="text-align: justify;">Điểm đặc biệt nhất đ&oacute; l&agrave; Milestone 2 sẽ  d&ugrave;ng giao diện MotoBlur hướng tới c&aacute;c mạng x&atilde; hội, điều m&agrave; Motorola  kh&ocirc;ng l&agrave;m với chiếc Droid 2 v&agrave; cả Droid X. Giao diện n&agrave;y rất hữu &iacute;ch v&agrave;  đẹp mắt, nhưng trước đ&acirc;y chỉ xuất hiện tr&ecirc;n c&aacute;c d&ograve;ng m&aacute;y trung v&agrave; cấp  thấp của Motorola m&agrave; th&ocirc;i. Tuy nhi&ecirc;n, giao diện MotoBlur lần n&agrave;y được  cải tiến hơn với những thay đổi như: khả năng chỉnh sửa k&iacute;ch thước của  widget, c&aacute;c icon kh&aacute;c đi, m&agrave;u sắc tươi tắn v&agrave; sinh động hơn, t&iacute;ch hợp  "bộ lọc Widget" (Widget Filter) cũng như thiết kế giao diện ứng dụng  Dock Station mới (hoạt động khi gắm m&aacute;y v&agrave;o đế đa phương tiện).<br /><br />Hi  vọng Motorola sẽ sớm giới thiệu chiếc Milestone 2 ở Việt Nam với mức  gi&aacute; cực k&igrave; hợp l&iacute; (khoảng 10,5 triệu đồng) như h&atilde;ng đ&atilde; từng l&agrave;m với  chiếc Milestone.</p>\r\n<p style="text-align: center;"><img style="border: 1px solid black;" src="http://www.mainguyen.vn/img/inposts/1009/Motorola-MILESTONE-2_3.jpg" alt="" width="580" height="600" /><br /><br /><img style="border: 1px solid black;" src="http://www.mainguyen.vn/img/inposts/1009/Motorola-MILESTONE-2_4.jpg" alt="" width="580" height="679" /><br /><br /><img style="border: 1px solid black;" src="http://www.mainguyen.vn/img/inposts/1009/Motorola-MILESTONE-2_5.jpg" alt="" width="580" height="350" /><br /><br /><img style="border: 1px solid black;" src="http://www.mainguyen.vn/img/inposts/1009/Motorola-MILESTONE-2_6.jpg" alt="" width="580" height="470" /></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (532, 39, 1, 'Samsung Galaxy Tab P1000 ', '    * Hệ điều hành Android v2.2 (Froyo)\r\n    * Màn hình cảm ứng đa điểm cực rộng 7 inches\r\n    * Xem phim Full HD\r\n    * Thinkfree Office (Word, Excel, PowerPoint, PDF)\r\n    * Mạng 3G với HSDPA, 7.2 Mbps\r\n    * HSUPA, 5.76 Mbps\r\n    * Bộ nhớ trong 16/32 G', '2011-11-02', 14189000, 'galaxy_tab_1.png', '<h2>Samsung ch&iacute;nh thức ra Galaxy Tab</h2>\r\n<div class="news_description">Đ&uacute;ng như đ&atilde; hẹn, h&ocirc;m nay tại Đức, Samsung đ&atilde; tr&igrave;nh l&agrave;ng chiếc tablet t&iacute;ch hợp chức năng phone Galaxy Tab với m&agrave;n h&igrave;nh 7 inch.</div>\r\n<div class="new_pro_detail_contet">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/721_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tab xuất hiện sau thời gian d&agrave;i r&ograve; rỉ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Samsung P1000 Galaxy Tab xuất hiện sau một thời gian d&agrave;i đồn thổi, thiết bị được h&atilde;ng ra mắt tại triển l&atilde;m thương mại IFA 2010 đang diễn ra ở Đức. Chiếc tablet của thương hiệu <span id="cpms_link2" class="cmps_it_intextLink" onclick="cpms_Intext.aj(2);" onmouseover="cpms_Intext.f(2);" onmouseout="cpms_Intext.Z();">H&agrave;n Quốc</span> c&oacute; m&agrave;n h&igrave;nh 7 inch, t&iacute;ch hợp đầy đủ chức năng phone như một chiếc điện thoại th&ocirc;ng minh. M&aacute;y cho ph&eacute;p xem video 7 giờ li&ecirc;n tục, hỗ trợ DivX v&agrave; Flash tr&ecirc;n nền Android, m&agrave;n h&igrave;nh của Tab c&oacute; độ ph&acirc;n giải cao WSVGA.</p>\r\n<p class="Normal">Galaxy Tab sử dụng <span id="cpms_link1" class="cmps_it_intextLink" onclick="cpms_Intext.aj(1);" onmouseover="cpms_Intext.f(1);" onmouseout="cpms_Intext.Z();">c&ocirc;ng nghệ</span> m&agrave;n h&igrave;nh TFT, độ ph&acirc;n giải 1.024 x 600 pixel, vi xử l&yacute; Cortex-A8 tốc độ 1GHz, RAM 512MB, chip đồ họa PowerVR SGX540.</p>\r\n<p class="Normal">Chiếc tablet n&agrave;y chạy tr&ecirc;n hệ điều h&agrave;nh Android 2.2 Froyo, h&atilde;ng kh&ocirc;ng mang giao diện TouchWiz l&ecirc;n m&agrave; tập trung v&agrave;o c&aacute;c g&oacute;i ứng dụng thay thế.</p>\r\n<p>&nbsp;</p>\r\n<table border="0" cellspacing="2" cellpadding="2" width="324" align="center" bgcolor="#408080">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink" align="center">\r\n<p><strong><span style="color: #ffffff;">Xem những h&igrave;nh ảnh đẹp của Galaxy Tab</span></strong></p>\r\n<p>&nbsp;</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/722_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/723_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/724_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/725_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/726_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/727_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/728_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="333" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tab chạy tr&ecirc;n mạng GSM/GPRS/EDGE với kết nối 3G/HSPA, hỗ trợ SMS, MMS. Camera ph&iacute;a trước l&agrave; 1,3 Megapixel, hỗ trợ đ&agrave;m thoại video. Ph&iacute;a sau m&aacute;y l&agrave; camera 3 Megapixel với đ&egrave;n flash LED.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/729_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="480" height="342" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Samsung Galaxy Tab c&oacute; cấu h&igrave;nh mạnh mẽ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Samsung P1000 Galaxy Tab c&oacute; k&iacute;ch thước đầy đủ l&agrave; 190.1 x 120.5 x 11.98 mm, nặng 380 gram, ngoại cỡ so với một chiếc di động. M&aacute;y nhỏ hơn <span id="cpms_link0" class="cmps_it_intextLink" onclick="cpms_Intext.aj(0);" onmouseover="cpms_Intext.f(0);" onmouseout="cpms_Intext.Z();">iPad</span>, vỏ nhựa.</p>\r\n<p class="Normal">Samsung sẽ đưa ra hai t&ugrave;y chọn về bộ nhớ l&agrave; 16 v&agrave; 32GB, ngo&agrave;i ra thiết bị c&ograve;n sở hữu khe cắm thẻ microSD cho ph&eacute;p mở rộng 32GB. M&aacute;y hỗ trợ th&ecirc;m c&aacute;c kết nối gồm Bluetooth 3.0, Wi-Fi chuẩn n, v&agrave; cổng giao tiếp chuẩn giống Apple.</p>\r\n<p class="Normal">Model n&agrave;y cũng c&oacute; một ng&otilde; v&agrave;o HDMI, cho ph&eacute;p chuyễn m&atilde; c&aacute;c định dạng video như DivX, XviD, H.264, Flash 10.1, pin theo m&aacute;y dung lượng 4.000 mAh.</p>\r\n<p class="Normal">Samsung cũng th&ecirc;m phần mềm Readers Hub, hỗ trợ hai triệu cuốn s&aacute;ch v&agrave; h&agrave;ng ngh&igrave;n đầu b&aacute;o, tạp ch&iacute;. M&aacute;y cũng cho ph&eacute;p c&agrave;i đặt ứng dụng từ Android Market. Hiện chưa r&otilde; c&oacute; bao nhiều phần mềm hỗ trợ chuẩn ph&acirc;n giải 1.024 x 600 pixel.</p>\r\n<span style="color: #000000;">&nbsp;</span> \r\n<table border="0" cellspacing="2" cellpadding="2" width="296" align="center" bgcolor="#408080">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" align="center"><strong><span style="color: #ffffff;">Bộ ảnh ch&iacute;nh thức của Galaxy Tab</span></strong></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/730_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="637" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/731_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="401" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/732_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="356" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/733_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="762" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/734_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="1039" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/735_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="768" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/736_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="726" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/737_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="335" /></td>\r\n</tr>\r\n<tr>\r\n<td><img title="Samsung ch&iacute;nh thức ra Galaxy Tab" src="http://images.thegioididong.com/Files/2010/09/03/20130/738_Samsung-chinh-thuc-ra-Galaxy-Tab.jpg" border="1" alt="Samsung ch&iacute;nh thức ra Galaxy Tab" width="500" height="197" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">\r\n<p><strong><span style="color: #ffffff;">Video giới thiệu Galaxy T</span></strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (533, 45, 1, 'Dell Streak ', '    * Hệ điều hành Android 1.6 có thể nâng cấp lên 2.2\r\n    * Mạng 3G với HSDPA, 7.2 Mbps\r\n    * HSUPA, 5.76 Mbps\r\n    * Chức năng văn phòng với QuickOffice\r\n    * Google Search, Maps, Gmail, YouTube, Google Talk\r\n    * Chỉnh sửa hình ảnh, Adobe Flash 10.', '2011-11-02', 13990000, 'dellstreak_bb.jpg', '<h2>smarphone khổng lồ Dell Streak</h2>\r\n<div class="news_description">iFixit vừa tiến h&agrave;nh mở chiếc smartphone khổng lồ Streak của Dell, model sở hữu m&agrave;n h&igrave;nh rộng 5 inch.</div>\r\n<div class="news_pro_link"><a tabindex="0" href="http://thegioididong.com/tin-tuc-dien-thoai-di-dong,14966-dell-streak-quoc-te-175-trieu-o-ha-noi.aspx">&nbsp;&gt;&nbsp;Dell Streak quốc tế 17.5 triệu ở H&agrave; Nội</a></div>\r\n<div class="new_pro_detail_contet">\r\n<p>&nbsp;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/70_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="To&agrave;n bộ nội tạng của Dell Streak. Ảnh: iFixit." width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">To&agrave;n bộ nội tạng của Dell Streak. Ảnh: <em>iFixit.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Model n&agrave;y c&oacute; m&agrave;n h&igrave;nh cảm ứng LCD rộng 5 inch, độ ph&acirc;n giải 800 x 480 pixel, m&aacute;y sở hữu camera 5 Megaoixel, m&aacute;y ảnh ph&iacute;a trước, vi xử l&yacute; Qualcomm 8250 Snapdragon tốc độ 1GHz.</p>\r\n<p class="Normal">Giống như c&aacute;c mẫu di động kh&aacute;c, việc thao Dell Streak kh&ocirc;ng qu&aacute; kh&oacute; khăn, đầu ti&ecirc;n, cần mở rời pin, sau đ&oacute;, b&oacute;c mặt sau, v&agrave; th&aacute;o mở c&aacute;c chi tiết b&ecirc;n trong, bo mạch, cũng như mảng m&agrave;n h&igrave;nh ph&iacute;a trước.</p>\r\n<p class="Normal">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<table border="0" cellspacing="2" cellpadding="2" width="80%" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/71_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="359" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/72_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/73_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/74_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="359" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/75_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/76_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/77_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/78_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/79_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="359" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/80_Mo-bung-smarphone-khong-lo-Dell-Streak.jpg" border="1" alt="" width="480" height="345" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src="http://images.thegioididong.com/Files/2010/08/21/15919/8