INSERT INTO `sanpham` VALUES (655, 40, 1, 'SONY ERICSSON J20I HAZEL', 'Điện thoại thân thiện với môi trường\r\nMáy ảnh 5 MP, hỗ trợ LED Flash\r\nKết nối mạng với Wifi tốc độ cao\r\nMạng 3G với tốc độ truyền tải 7.2 Mbps\r\nChức năng định vị toàn cầu GPS\r\nChức năng lọc tiếng ồn với mic chuyên dụng\r\nKết nối mạng xã hội YouTube, Facebo', '0000-00-00', 5089000, 'j20i_b.jpg', '<h2 style="color: #ff9501; padding: 5px 0px 3px 10px; margin: 0px; font-weight: bolder;">B&agrave;i viết về sản phẩm Sony Ericsson Hazel</h2>\r\n<div class="news_description" style="color: #606060; padding: 0px 0px 0px 10px; margin-bottom: 10px;">B&agrave;i viết về sản phẩm Sony Ericsson Hazel</div>\r\n<div class="new_pro_detail_contet" style="padding: 5px 3px 5px 10px;"><strong>Ng&agrave;y h&ocirc;m qua, 14/12, Sony Ericsson ra mắt 2 model mới thuộc d&ograve;ng dế th&acirc;n thiện với m&ocirc;i trường Greenheart c&oacute; t&ecirc;n Elm v&agrave; Hazel.</strong>\r\n<div class="content-noidung">\r\n<p style="margin: 14px 0px; line-height: 18px; text-align: justify;">Cả 2 model mới đều được trang bị khả năng kết nối Wifi, GPS, camera 5 megapixel tự động canh n&eacute;t, đồng thời t&iacute;ch hợp 1 số mạng x&atilde; hội - đặc điểm thường thấy của những ch&uacute; dế ra đời trong thời gian gần đ&acirc;y trước sự ph&aacute;t triển như vũ b&atilde;o của c&aacute;c mạng x&atilde; hội.</p>\r\n<p style="margin: 14px 0px; line-height: 18px; text-align: justify;">Ngo&agrave;i ra, với những đường cong được thiết kế 1 c&aacute;ch th&ocirc;ng minh, Elm v&agrave; Hazel rất vừa vặn với tay người d&ugrave;ng.</p>\r\n<p style="margin: 14px 0px; line-height: 18px; text-align: justify;">Sony Ericsson Elm c&oacute; độ d&agrave;y khoảng 14 mm v&agrave; nặng 90 gram. Tuy nhi&ecirc;n, m&agrave;n h&igrave;nh của Elm chỉ rộng 2.2 inch, hỗ trợ hiển thị 262k m&agrave;u v&agrave; độ ph&acirc;n giải QVGA.</p>\r\n<p style="margin: 14px 0px; line-height: 18px; text-align: justify;">Cả Elm v&agrave; Hazel đều được trang bị 1 loạt t&iacute;nh năng đa phương tiện như phần mềm SensMe, TrackID v&agrave; MegaBass. 2 ch&uacute; dế n&agrave;y cũng c&oacute; khả năng xoay m&agrave;n h&igrave;nh theo chiều sử dụng.</p>\r\n<p style="margin: 14px 0px; line-height: 18px; text-align: justify;">Điều th&uacute; vị l&agrave; Elm v&agrave; Hazel sử dụng bộ sạc c&oacute; chuẩn kết nối l&agrave; microUSB. Như vậy, sau Xperia X10, Elm v&agrave; Hazel ch&iacute;nh l&agrave; ch&uacute; dế thứ 2 v&agrave; thứ 3 của Sony Ericsson c&oacute; đặc điểm n&agrave;y.</p>\r\n<p style="margin: 14px 0px; line-height: 18px; text-align: justify;">Elm sẽ c&oacute; 2 phi&ecirc;n bản m&agrave;u l&agrave; Metal Black (đen) v&agrave; Pearly Rose (đỏ), tr&igrave;nh l&agrave;ng v&agrave;o qu&yacute; I/2010. Trong khi đ&oacute;, Hazel sẽ ra đời muộn hơn - qu&yacute; II/2010 v&agrave; cũng c&oacute; 2 phi&ecirc;n bản m&agrave;u l&agrave; Superious Black (đen) v&agrave; Passionate Rouge (hồng).</p>\r\n<br class="Apple-interchange-newline" /></div>\r\n</div>', 0, 200, NULL, 0, 1);
