INSERT INTO `sanpham` VALUES (657, 40, 1, 'SONY ERICSSON W980', 'Tận hưởng âm nhạc\r\nThiết kế nắp gập thời trang\r\nBộ nhớ trong 8 GB\r\nMáy nghe nhạc Walkman với các phím chơi nhạc chuyên dụng\r\nĐài FM với RDS\r\nMáy phát FM\r\nBộ điều khiển rung\r\nChức năng xem và chỉnh sửa hình ảnh', '0000-00-00', 4989000, 'bsonyericsson-w9800.jpg', '<p class="Title" style="color: #810004; padding-left: 10px; font: bold 20px Arial;">Walkman gập bộ nhớ ''khủng'' của Sony Ericsson</p>\r\n<p class="Lead" style="margin-left: 10px; color: #343434; font: bold 12px Arial;"><a class="Lead" style="text-decoration: none; color: #343434; font: bold 12px Arial;" href="http://sohoa.vnexpress.net/Topic/?ID=211">Sony Ericsson</a><span class="Apple-converted-space">&nbsp;</span>W980<span class="Apple-converted-space">&nbsp;</span><a class="Lead" style="text-decoration: none; color: #343434; font: bold 12px Arial;" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2008/02/3B9AF101/">ra mắt th&aacute;ng 2 năm nay</a><span class="Apple-converted-space">&nbsp;</span>với d&aacute;ng gập, hai m&agrave;n h&igrave;nh, c&aacute;c ph&iacute;m bấm ch&igrave;m c&ugrave;ng bộ nhớ lưu trữ l&ecirc;n tới 8 GB. Dự kiến th&aacute;ng sau sản phẩm sẽ ra mắt.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Sony Ericsson W980 vẫn chưa c&oacute; mặt tr&ecirc;n thị trường, model n&agrave;y sẽ ra mắt trong th&aacute;ng 8 n&agrave;y. M&aacute;y trang bị đầy đủ kết nối mạnh của một chiếc smartphone như 3G/HSDPA, Bluetooth hỗ trợ A2DP (nghe nhạc qua tai nghe kh&ocirc;ng d&acirc;y với &acirc;m thanh stereo).</p>\r\n<table border="0" cellspacing="3" cellpadding="3" width="405" align="center" bgcolor="#fff7d9">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/1.jpg" border="1" alt="" width="400" height="291" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" style="vertical-align: top; font: 12px Arial; color: #1f1f1f; padding-left: 10px;" colspan="2" align="center">Khi nắp lại, W980i vu&ocirc;ng vắn.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/2.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" style="vertical-align: top; font: 12px Arial; color: #1f1f1f; padding-left: 10px;" colspan="2" align="center">C&aacute;c ph&iacute;m bấm nghe nhạc nổi bật, lớn v&agrave; dễ sử dụng.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/3.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" style="vertical-align: top; font: 12px Arial; color: #1f1f1f; padding-left: 10px;" colspan="2" align="center">Giắc cắm tai nghe vẫn theo chuẩn ri&ecirc;ng của Sony Ericsson.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/4.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" style="vertical-align: top; font: 12px Arial; color: #1f1f1f; padding-left: 10px;" colspan="2" align="center">Trong khi c&aacute;c ph&iacute;m số được thiết kế tr&ograve;n trịa v&agrave; bao bọc c&aacute;c đường viền s&aacute;ng.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/5.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">&nbsp;Bộ phận điều khiển cũng c&aacute;ch điều bằng c&aacute;c đường tr&ograve;n.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/6.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">&nbsp;M&agrave;n h&igrave;nh của m&aacute;y s&aacute;ng, W980 hỗ trợ kết nối 3G.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/8.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">&nbsp;Mặt sau, camera 3,2 Megapixel.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/9.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">&nbsp;C&oacute; thể thấy c&aacute;c ph&iacute;m bấm, kết cả ph&iacute;m điều khiển nằm rất ch&iacute;m. Thiết kế n&agrave;y l&agrave;m cho b&agrave;n ph&iacute;m kh&ocirc;ng t&aacute;c động đến m&agrave;n h&igrave;nh khi gập lại.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/11.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">&nbsp;Nắp gập của W980.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">\r\n<p style="font-size: 12px; margin-bottom: 10px;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F5/50/12.jpg" border="1" alt="" width="400" height="244" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" colspan="2" align="middle">&nbsp;So s&aacute;nh W980 với điện thoại Walkman mới W902.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><br class="Apple-interchange-newline" /></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (658, 48, 1, 'Vertu Ascent 2010 ', ' Vertu Ascent 2010  \r\n- Vỏ titanium, nhôm, mặt kính saphia, da và cao su cao cấp\r\n- Máy ảnh 5.0 MP, hỗ trợ quay phim\r\n- Mạng 3G với HSDPA; HSUPA\r\n- Chức năng định vị toàn cầu GPS\r\n- Kết nối Bluetooth, USB, GPRS', '0000-00-00', 85745000, 'Ascent_2010_b.jpg', '<div class="new_pro_detail_contet"><span style="font-family: Arial; font-size: x-small;"><strong>Vertu  l&agrave; thương hiệu điện thoại cao cấp v&agrave; &ldquo;si&ecirc;u&rdquo; đắt tiền của Nokia. Trải qua thời kỳ khủng hoảng kinh tế trầm trọng nhưng Vertu vẫn đứng vững, v&agrave; điều nghịch l&yacute; l&agrave; nhiều người vẫn sẵn s&agrave;ng bỏ ra một đống tiền để tậu về một &ldquo;con&rdquo; Vertu vốn rất &iacute;t t&iacute;nh năng n&agrave;y. Vertu vẫn đắt đỏ một c&aacute;ch phi l&yacute; trong khi những g&igrave; m&agrave; d&ograve;ng điện thoại n&agrave;y mang lại chỉ l&agrave; c&aacute;i tiếng được sử dụng m&oacute;n đồ chơi đắt tiền. <br /><br /></strong></span>\r\n<div><span style="font-family: Arial; font-size: x-small;"><strong><img title="Điện thoại di động: Vertu Ascent 2010" src="http://www.thegioididong.com/dtdd/news/BaivietSP/vertu/2010.jpg" alt="" /></strong></span></div>\r\n<span style="font-family: Arial; font-size: x-small;"> <br /> Phi&ecirc;n bản Vertu Ascent X sẽ c&oacute; m&agrave;n h&igrave;nh 2-inch, độ ph&acirc;n giải QVGA, định vị vệ tinh A GPS, hỗ trợ e-mail v&agrave; được t&iacute;ch hợp m&aacute;y ảnh 5 megapixel (c&oacute; đ&egrave;n LED). Đ&acirc;y cũng l&agrave; phi&ecirc;n bản Vertu đầu ti&ecirc;n được trang bị nhiều t&iacute;nh năng như thế n&agrave;y. Vertu Ascent X sẽ c&oacute; bộ nhớ khoảng 32GB. <br /> <br /> Vẫn l&agrave; thế mạnh của d&ograve;ng Vertu, phi&ecirc;n bản Ascent X vẫn sử dụng 4 băng tần GSM (850/ 900/ 1800/ 1900 MHz) v&agrave; 4 băng tần UMTS/HSUPA (800 / 850 / 1900 / 2100 MHz), đảm bảo cho điện thoại c&oacute; thể kết nối ở bất cứ nơi đ&acirc;u tr&ecirc;n thế giới, d&ugrave; đ&oacute; c&oacute; l&agrave; Uganda hay l&agrave; h&ograve;n đảo Kerguelen xa x&ocirc;i. </span></div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (659, 40, 1, 'SONY ERICSSON T715', 'Thiết kế chắc chắn với dáng trượt thời trang\r\nKết nối 3G với tốc độ truyền tải dữ lliệu HSDPA\r\nCamera 3.15MP, hỗ trợ quay phim và thực hiện videocall\r\nMáy nghe nhạc, xem phim MP3/eAAC+/WMA, MP4/WMV\r\nĐài FM tích hợp\r\nKết nối Bluetooth, USB...\r\nỨng dụng You', '0000-00-00', 4589000, 'Sony Ericsson T715_1.jpg', '<p class="Title" style="color: #810004; padding-left: 10px; font: bold 20px Arial;">Sony Ericsson T715 thời trang</p>\r\n<p class="Lead" style="margin-left: 10px; color: #343434; font: bold 12px Arial;">H&ocirc;m qua, Sony Ericsson ra mắt T715, chiếc di động với thiết kế chắc gọn, d&aacute;ng trượt thời trang, kết nối 3G v&agrave; camera 3 Megapixel.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/04/D3/gsmarena_001.jpg" border="1" alt="Sony Ericsson T715 c&oacute; thiết kế đẹp. Ảnh: Gsmarena." width="480" height="510" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;">Sony Ericsson T715 c&oacute; thiết kế đẹp. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>Gsmarena.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Sony Ericsson T715 l&agrave; chiếc di động ấn tượng bởi thiết kế bề ngo&agrave;i. Mặt sau của m&aacute;y được l&agrave;m từ lớp vỏ nh&ocirc;m, chải b&oacute;ng. Model n&agrave;y c&oacute; c&aacute;c m&agrave;u như bạc, hồng. M&aacute;y c&oacute; k&iacute;ch thước nhỏ gọn, 91,5 x 48,0 x 14,9 mm v&agrave; nặng chỉ 96 gram, d&aacute;ng trượt.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">T715 được trang bị camera 3,15 Megapixel với t&iacute;nh năng Cell-ID geotagging, đ&egrave;n flash LED. C&oacute; t&iacute;nh năng quay video, tuy nhi&ecirc;n độ ph&acirc;n giải lại kh&ocirc;ng cao.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Mặt trước của m&aacute;y, m&agrave;n h&igrave;nh 2,2 inch kh&aacute; lớn, độ ph&acirc;n giải QVGA, hỗ trợ hiển thị tốt dưới &aacute;nh s&aacute;ng mặt trời. T715 trang bị c&aacute;c t&iacute;nh năng chơi nhạc như TrackID, PlayNow v&agrave; Bluetooth 2.0 với A2DP, nghe đ&agrave;i FM với RDS.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Đ&acirc;y l&agrave; chiếc di động hỗ trợ kết nối tốt, với HSPA, T715 cho ph&eacute;p lướt web th&ocirc;ng qua tr&igrave;nh duyệt NetFront, xem video trực tuyến tr&ecirc;n YouTube th&ocirc;ng qua một chương tr&igrave;nh c&agrave;i đặt sẵn. Ngo&agrave;i ra, kết nối tốc độ cao c&ograve;n hỗ trợ người d&ugrave;ng gọi điện bằng video.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">M&aacute;y hỗ trợ một v&agrave;i chương tr&igrave;nh Google như Google Maps, Google Search. Một trong những trang bị đ&aacute;ng ch&uacute; &yacute; l&agrave; được h&atilde;ng gọi l&agrave; "smart desktop feature" hỗ trợ nhắc việc, sinh nhật, c&aacute;c sự kiện tr&ecirc;n to&agrave;n cầu.</p>\r\n<table border="1" cellspacing="2" cellpadding="2" width="80%" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;" align="center"><a class="BoxLink" style="text-decoration: none; color: #000000; margin: 0px auto; font-size: 12px;" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2009/06/3B9B04D3/page_2.aspx"><strong><span style="color: #0000ff;">Video demo Sony Ericsson T715</span></strong></a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Sony Ericsson T715 sẽ c&oacute; mặt tr&ecirc;n thị trường v&agrave;o qu&yacute; III năm nay, chưa c&oacute; th&ocirc;ng tin về mức gi&aacute;.</p>\r\n<table border="0" cellspacing="2" cellpadding="2" width="80%" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/04/D3/gsmarena_002.jpg" border="1" alt="" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/04/D3/gsmarena_003.jpg" border="1" alt="" width="480" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/04/D3/gsmarena_006.jpg" border="1" alt="" width="480" height="469" /></td>\r\n</tr>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/04/D3/gsmarena_008.jpg" border="1" alt="" width="480" height="253" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><br class="Apple-interchange-newline" /></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (660, 40, 1, 'SONY ERICSSON W508', 'Thiết kế thời trang trẻ trung\r\nMàn hình cảm biến tự động xoay màn hình, bề mặt chống xước\r\nCamera 3.2 Mp, hỗ trợ quay phim QVGA @ 15 fps\r\nMáy nghe nhạc chuyên dụng với những phím chơi nhạc bên ngoài sành điệu\r\nĐài FM tích hợp\r\nKết nối dữ liệu nhanh với mạ', '0000-00-00', 4289000, '0Sony Ericsson W508_1.jpg', '<p class="Title" style="color: #810004; padding-left: 10px; font: bold 20px Arial;">Sony Ericsson tr&igrave;nh l&agrave;ng W508 v&agrave; C510</p>\r\n<p class="Lead" style="margin-left: 10px; color: #343434; font: bold 12px Arial;">Sony Ericsson vừa tr&igrave;nh l&agrave;ng bộ đ&ocirc;i điện thoại mới tại CES 2009 gồm chiếc Walkam nắp gập W508 v&agrave; di động chụp ảnh Cyber shot C510.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FA/C5/W508_open_a_grey.jpg" border="1" alt="Sony Ericsson W580 c&oacute; thiết kế nắp gập. Ảnh: Mobileburn." width="400" height="333" align="center" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;">Sony Ericsson W508 c&oacute; thiết kế nắp gập. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>Mobileburn.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Sony Ericsson W508 l&agrave; chiếc điện thoại mới nhất trong d&ograve;ng Walkman của h&atilde;ng, m&aacute;y được trang bị c&aacute;c t&iacute;nh năng giống như c&aacute;c model trong d&ograve;ng nghe nhạc như ph&iacute;m bấm chuy&ecirc;n dụng, điều khiển cử động.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">T&iacute;nh năng điều khiển cử động cho ph&eacute;p chỉnh nhạc, hẹn giờ. Trong đ&oacute;, người d&ugrave;ng c&oacute; thể giật điện thoại l&ecirc;n để tăng &acirc;m lượng, lật m&aacute;y để ngắt chu&ocirc;ng.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">W508 được trang bị camera 3,2 Megapixel, kết nối HSDPA, EDGE, Bluetooth. Điện thoại b&aacute;n ra sẽ được tăng k&egrave;m thẻ nhớ M2 dung lượng 1 GB. Model n&agrave;y sẽ xuất hiện tr&ecirc;n thị trường qu&yacute; II năm nay với hai m&agrave;u x&aacute;m v&agrave; trắng, chưa c&oacute; th&ocirc;ng tin gi&aacute; b&aacute;n.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (661, 48, 1, ' Vertu Constellation Satin brown', ' Vertu Constellation Satin brown  \r\n- Ứng dụng Java MIDP 2.0\r\n- Thiết kế độc đáo, được trau chuốt kỹ lưỡng\r\n- Đồng bộ máy tính và làm Modem tốc độ cao.\r\n- Chức năng báo thức\r\n- Tích hợp sổ tay-lịch cao cấp\r\n- Bộ đóan trước ký tự đa ngôn ngữ\r\n- Hỗ trợ đa n', '0000-00-00', 85236000, 'bConstellationSatinbrown.jpg', '<h1 class="Title">Di động Vertu mạ v&agrave;ng 4 m&ugrave;a</h1>\r\n<p class="Lead">Vertu vừa tr&igrave;nh l&agrave;ng tại Nhật Bản bộ 4 chiếc di động mạ  v&agrave;ng, c&oacute; m&agrave;u sắc kh&aacute;c nhau đại diện cho 4 m&ugrave;a trong năm.</p>\r\n<p class="Normal">Đ&acirc;y l&agrave; những model kỷ niệm tr&ograve;n một năm h&atilde;ng mở cửa  h&atilde;ng b&aacute;n lẻ tại Tokyo. Bốn model n&agrave;y được sản xuất với số lượng hạn chế,  thiết kế tinh xảo với c&aacute;c hoa văn, họa tiết l&agrave;m ho&agrave;n to&agrave;n bằng tay.</p>\r\n<p class="Normal">Bộ tứ di động của Vertu c&oacute; t&ecirc;n l&agrave; Vertu Kinko, Vertu  Kikusui, Vertu Nanten v&agrave; Vertu Daigo, gi&aacute; b&aacute;n mỗi model l&ecirc;n tới gần 215  ngh&igrave;n USD.</p>\r\n<table border="0" cellspacing="2" cellpadding="1" width="77%" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/1F/6F/1.jpg" border="1" alt="" width="480" height="517" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">C&aacute;c model c&oacute; thiết kế tinh xảo.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/1F/6F/2.jpg" border="1" alt="" width="480" height="517" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">Hoa văn, họa tiết được vẽ bằng tay.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/1F/6F/3.jpg" border="1" alt="" width="480" height="525" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">M&aacute;y chỉ b&aacute;n ra tại Nhật...</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/1F/6F/4.jpg" border="1" alt="" width="480" height="504" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink">... với số lượng hạn chế.</td>\r\n</tr>\r\n</tbody>\r\n</table>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (662, 40, 1, 'SONY ERICSSON W890I', 'Cho ai yêu thích âm nhạc\r\nThiết kế thanh mảnh thời trang\r\nChức năng điều khiển máy bằng chuyển động\r\nCamera 3.15 MP, camera phụ thực hiện videocall\r\nMáy phát nhạc Walkman\r\nChức năng nhận diện thông tin bài hát\r\nĐài FM', '0000-00-00', 3989000, 'Sony Ericsson W890i_1.jpg', '<h2 style="color: #ff9501; padding: 5px 0px 3px 10px; margin: 0px; font-weight: bolder;">B&agrave;i viết về sản phẩm Sony Ericsson W890i</h2>\r\n<div class="news_description" style="color: #606060; padding: 0px 0px 0px 10px; margin-bottom: 10px;">B&agrave;i viết về sản phẩm Sony Ericsson W890i</div>\r\n<div class="new_pro_detail_contet" style="padding: 5px 3px 5px 10px;"><span class="Article_Detail_Content" style="font-size: 11px;"><strong><span style="font-family: Verdana; font-size: x-small;">Với thiết kế candybar quen thuộc, W890 c&oacute; k&iacute;ch thước 104 x 46,5 x 9,9 mm, kh&aacute; nhẹ nh&agrave;ng với trọng lượng chỉ c&oacute; 78 gram. W890 c&oacute; khả năng kết nối mạng GSM 4 băng tần cũng như HSDPA cho tốc độ truyền tải dữ liệu nhanh hơn.</span></strong><br /><br /></span>\r\n<div><span class="Article_Detail_Content" style="font-size: 11px;"><br /></span></div>\r\n<span class="Article_Detail_Content" style="font-size: 11px;"><br /><span style="font-family: Verdana; font-size: x-small;">Một trong những đặc điểm nổi bật của W890 l&agrave; thẻ nhớ 2 GB, m&agrave;n h&igrave;nh TFT 2 inch với 256K m&agrave;u v&agrave; độ ph&acirc;n giải l&agrave; 240 x 320 pixel, trang bị phần mềm Walkman Player 3.0, SensMe, TrackID, FM radio với RDS, megabass, Bluetooth 2.0 với A2DP, email, Instant Messaging, tr&igrave;nh duyệt NetFront 3.4, v&agrave; RSS.</span><br /><br /><span style="font-family: Verdana; font-size: x-small;">Thời gian thoại của W890 l&agrave; 9 giờ v&agrave; thời gian chờ l&ecirc;n tới 360 giờ.</span><br /><br /><span style="font-family: Verdana; font-size: x-small;">Ngo&agrave;i ra, chiếc camera 3.15 megapixel với khả năng quay video sẽ l&agrave; c&ocirc;ng cụ hữu &iacute;ch cho ph&eacute;p người d&ugrave;ng thoải m&aacute;i ghi lại những khoảnh khắc đ&aacute;ng nhớ với chất lượng kh&aacute; ổn. Tuy nhi&ecirc;n, chức năng chụp ảnh của m&aacute;y kh&ocirc;ng được hỗ trợ, bởi t&iacute;nh năng tự động điều chỉnh ti&ecirc;u cự hay đ&egrave;n flash. Nhưng n&ecirc;n nhớ rằng W890 l&agrave; một ch&uacute; dế Walkman - d&agrave;nh cho c&aacute;c fan y&ecirc;u nhạc chứ kh&ocirc;ng phải l&agrave; một ch&uacute; dế Cyber-shot - d&agrave;nh cho những người m&ecirc; nhiếp ảnh.</span></span></div>\r\n<p><br class="Apple-interchange-newline" /></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (663, 40, 1, 'SONY ERICSSON T707', 'Thiết kế nắp gập thời trang sang trọng\r\nCamera 3.2 MP, hỗ trợ quay phim\r\nMáy nghe nhạc nhiều định dạng\r\nChức năng calling Video\r\nKết nối 3G, Bluetooth, USB\r\nĐài FM', '0000-00-00', 3989000, 'SE_T707_b.jpg', '<p class="Title" style="color: #810004; padding-left: 10px; font: bold 20px Arial;">Sony Ericsson T707 tr&igrave;nh diễn</p>\r\n<p class="Lead" style="margin-left: 10px; color: #343434; font: bold 12px Arial;">Di động thời trang Sony Ericsson nổi bật với kiểu d&aacute;ng gọn, b&oacute;ng mượt, mặt ngo&agrave;i với m&agrave;n h&igrave;nh đơn sắc ẩn hiện.</p>\r\n<table border="0" cellspacing="2" cellpadding="2" width="80%" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/0.jpg" border="1" alt="" width="480" height="287" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">T707 l&agrave; chiếc di động duy nhất m&agrave; Sony Ericsson mang ra trưng b&agrave;y tại triển l&atilde;m kh&ocirc;ng d&acirc;y CTIA 2009.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/2.jpg" border="1" alt="" width="480" height="511" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">Thiết kế d&aacute;ng gập...</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/3.jpg" border="1" alt="" width="480" height="313" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">... b&ecirc;n dưới l&agrave; b&agrave;n ph&iacute;m với c&aacute;c n&uacute;t bấm rộng, phẳng. N&uacute;t điều hướng được thiết kế như một v&ograve;ng tr&ograve;n tỏa s&oacute;ng.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/5.jpg" border="1" alt="" width="480" height="514" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">Nh&igrave;n từ cạnh phải...</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/8.jpg" border="1" alt="" width="480" height="452" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">... mặt sau.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/10.jpg" border="1" alt="" width="480" height="468" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">M&aacute;y c&oacute; m&agrave;n h&igrave;nh QVGA 2,2 inch.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/12.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/13.jpg" border="1" alt="" width="480" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">... b&ecirc;n ngo&agrave;i l&agrave; m&agrave;n h&igrave;nh đơn sắc, nằm ẩn hiện tr&ecirc;n lớp vỏ ngo&agrave;i. G&oacute;c tr&ecirc;n l&agrave; camera 3,2 Megapixel.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/15.jpg" border="1" alt="" width="480" height="400" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/16.jpg" border="1" alt="" width="480" height="369" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">Tr&ocirc;ng T707 kh&aacute; gọn g&agrave;ng..</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/17.jpg" border="1" alt="" width="480" height="262" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">... v&agrave; lọt thỏm trong b&agrave;n tay.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FE/3E/18.jpg" border="1" alt="" width="480" height="383" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" style="vertical-align: top; margin: 0px auto;">Sony Ericsson vẫn bảo thủ khi trang bị cổng giao tiếp ri&ecirc;ng, giắc cắm tai nghe kh&ocirc;ng phải 3,5 mm.</td>\r\n</tr>\r\n</tbody>\r\n</table>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (664, 48, 1, 'Vertu Signature S ', ' Vertu Signature S  \r\n- Kiểu dáng sành điệu\r\n- Bộ nhớ trong 4GB\r\n- Màn hình bằng kính sapphire\r\n- 4.75 carats ruby bearings được tích hợp trên phím\r\n- Hỗ trợ wifi, mạng 3G, Bluetooth', '0000-00-00', 164258000, 'Vertu_SignS_b.jpg', '<p class="Title">Vertu - điện thoại đắt hơn &ocirc;t&ocirc;</p>\r\n<p class="Lead">Trong bộ sưu tập điện thoại Vertu m&agrave; C&ocirc;ng ty TNHH Ph&acirc;n  phối FPT (FDC), vừa giới thiệu tại Việt Nam, d&ograve;ng Signature c&oacute; mức gi&aacute;  đỉnh điểm l&ecirc;n đến 23.000 USD, tức l&agrave; đắt hơn cả một chiếc xe Daewoo  Gentra mới được tr&igrave;nh l&agrave;ng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/DC/B0/ve2.jpg" border="1" alt="Vertu Signature. (	 Economy.enorth.com.cn)" width="225" height="300" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vertu Signature. (<em> Economy.enorth.com.cn</em>)</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ng&agrave;y 19/1, C&ocirc;ng ty FDC đ&atilde; k&yacute; kết với h&atilde;ng điện thoại  cao cấp Vertu để cung cấp tại Việt Nam hai d&ograve;ng sản phẩm Signature v&agrave;  Ascent, tại 88 Đồng khởi, Quận1, TP HCM.</p>\r\n<p class="Normal">Vertu Signature đầu ti&ecirc;n được đưa ra thị trường v&agrave;o  th&aacute;ng 1/2002. Điện thoại được cấu th&agrave;nh từ tr&ecirc;n 400 bộ phận kh&aacute;c nhau,  sử dụng c&aacute;c chất liệu như th&eacute;p kh&ocirc;ng gỉ, v&agrave;ng trắng 18 carat, v&agrave;ng 18  carat v&agrave; platin. D&ograve;ng sản phẩm n&agrave;y bao gồm Signature 18 Carat Yellow  Gold, Signature 18 Carat White Gold với c&aacute;c hạt kim cương được nạm b&aacute;n  phần v&agrave; to&agrave;n phần, c&ugrave;ng điện thoại Signature Platinum nạm một vi&ecirc;n kim  cương c&oacute; khối lượng 0,25 carat. C&aacute;c nghệ nh&acirc;n thủ c&ocirc;ng đ&atilde; phải mất đến 7  ng&agrave;y để nạm bằng tay trung b&igrave;nh 700 vi&ecirc;n kim cương.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/DC/B0/ve3.jpg" border="1" alt="Vertu Ascent. (Tourneau)" width="201" height="300" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vertu Ascent. (<em>Tourneau</em>)</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Vertu Ascent c&oacute; 3 m&agrave;u đỏ, v&agrave;ng, xanh, thể hiện m&agrave;u sắc  cổ điển của những chiếc xe nổi tiếng: Ferrari (đỏ), Lamborghini (v&agrave;ng)  v&agrave; Bugatti (xanh). Gi&aacute;m đốc S&aacute;ng tạo của Vertu, &ocirc;ng Frank Nuovo, tiết  lộ, d&ograve;ng Ascent c&oacute; đường n&eacute;t thanh lịch của những chiếc xe sang trọng,  đặc biệt điện thoại c&ograve;n được bổ sung những điểm nhấn với chất liệu da  thuộc.</p>\r\n<table border="0" cellspacing="7" cellpadding="7" width="209" align="right">\r\n<tbody>\r\n<tr>\r\n<td class="Image"><a class="Image" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2005/12/3B9ADA93/"><strong><span style="color: #595900;">*Vertu: Xe đ&egrave; kh&ocirc;ng  chết</span></strong></a>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><a class="Image" onclick="return openMeExt(this,1,1,1,1,1,0,1,1,300,400,0,0,0)" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2005/10/3B9AD821/"><strong><span style="color: #555500;">*Di động Samsung ''học đ&ograve;i''  l&agrave;m qu&yacute; tộc</span></strong></a></td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><a class="Image" onclick="return openMeExt(this,1,1,1,1,1,0,1,1,300,400,0,0,0)" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2005/06/3B9AD257/"><strong><span style="color: #555500;">*Chơi điện thoại qu&yacute; tộc</span></strong></a></td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><a class="Image" onclick="return openMeExt(this,1,1,1,1,1,0,1,1,300,400,0,0,0)" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2004/12/3B9ACBDA/"><strong><span style="color: #555500;">*Mobiado - di động qu&yacute; tộc</span></strong></a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Vertu Ascent l&agrave;m nổi bật sự tương phản giữa cảm nhận  da thuộc kh&acirc;u tay mềm mại với hợp kim sử dụng trong chế tạo t&agrave;u con thoi  của Mỹ, mang lại cho người d&ugrave;ng cảm gi&aacute;c của sự vững bền. Đặc biệt, b&agrave;n  ph&iacute;m Vertu Ascent được sản xuất kh&aacute; phức tạp. H&atilde;ng Vertu đ&atilde; bơm hỗn hợp  th&eacute;p kh&ocirc;ng gỉ ở nhiệt độ cao v&agrave; &eacute;p v&agrave;o một khu&ocirc;ng c&oacute; k&iacute;ch thước lớn  hơn. Khi nguội đi, ph&iacute;m sẽ co lại 14% để tạo ra thiết kế b&agrave;n ph&iacute;m c&oacute; g&oacute;c  xi&ecirc;n. Sau đ&oacute; từng ph&iacute;m được đặt tr&ecirc;n hai v&ograve;ng đệm bằng đồ trang sức,  tạo ra t&aacute;c động xoay độc đ&aacute;o v&agrave; bảo đảm đ&uacute;ng &acirc;m thanh vang l&ecirc;n khi nhấn  ph&iacute;m.</p>\r\n<p class="Normal">Những chiếc điện thoại n&agrave;y sẽ c&oacute; mặt Việt Nam v&agrave;o cuối  th&aacute;ng 1. Gi&aacute; b&aacute;n dự kiến của d&ograve;ng Ascent l&agrave; từ 5.000 USD đến 5.600 USD;  d&ograve;ng Signature l&agrave; từ 7.500 USD đến 23.000 USD. Ngo&agrave;i Signature v&agrave;  Ascent, Vertu c&ograve;n c&oacute; d&ograve;ng sản phẩm Diamond được sản xuất theo thiết kế  ri&ecirc;ng của Vertu, mỗi m&aacute;y gồm 388 bộ phận cơ.&nbsp;</p>\r\n<p class="Normal">Vertu l&agrave; nh&agrave; sản xuất điện thoại thủ c&ocirc;ng cao cấp h&agrave;ng  đầu tr&ecirc;n thế giới. Điện thoại của h&atilde;ng c&oacute; kiểu d&aacute;ng thiết kế tương tự  c&aacute;c d&ograve;ng đồng hồ sang trọng v&agrave; sử dụng chất liệu của xe hơi cao cấp v&agrave;  th&eacute;p kh&ocirc;ng gỉ, da thuộc, v&agrave;ng platin v&agrave; kim cương. Mặt k&iacute;nh của m&aacute;y l&agrave;  mặt đ&aacute; ngọc b&iacute;ch. Đặc biệt, tất cả c&aacute;c m&aacute;y của Vertu đều được chế t&aacute;c  bằng tay bởi những chuy&ecirc;n gia d&agrave;y dạn kinh nghiệm.</p>\r\n<p class="Normal">Trong qu&aacute; tr&igrave;nh sản xuất điện thoại Vertu, h&atilde;ng sản  xuất đ&atilde; được trao 74 bằng s&aacute;ng chế, điều đặc biệt chưa từng c&oacute; trong  ng&agrave;nh điện thoại di động. Ngo&agrave;i ra, Vertu c&ograve;n c&oacute; dịch vụ Concierge ri&ecirc;ng  của h&atilde;ng: Chỉ cần một lần nhấn n&uacute;t người d&ugrave;ng sẽ được kết nối với mạng  hỗ trợ to&agrave;n cầu suốt 24 giờ. Do đ&oacute;, điện thoại Vertu được coi l&agrave; t&acirc;m  điểm truyền th&ocirc;ng, cũng như l&agrave; một chiếc v&eacute; danh dự để bước v&agrave;o những  kh&aacute;ch sạn, rạp h&aacute;t tốt nhất tr&ecirc;n thế giới.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (665, 40, 1, 'SONY ERICSSON W760', 'Thiết kế nắp trượt sành điệu\r\nĐịnh vị GPS\r\nCamera 3.2 MP\r\nHỗ trợ thẻ nhớ ngoài\r\nNghe nhạc nhiều định dạng\r\nĐài FM\r\nLoa thoại ngoài tích hợp', '0000-00-00', 3590000, 'W760_b.jpg', '<p class="Title" style="color: #810004; padding-left: 10px; font: bold 20px Arial;">Sony Ericsson W760 phi&ecirc;n bản MTV</p>\r\n<p class="Lead" style="margin-left: 10px; color: #343434; font: bold 12px Arial;"><a class="Lead" style="text-decoration: none; color: #343434; font: bold 12px Arial;" href="http://sohoa.vnexpress.net/Topic/?ID=211">Sony Ericsson</a><span class="Apple-converted-space">&nbsp;</span>kết hợp với MTV ch&acirc;u &Acirc;u ra mắt phi&ecirc;n bản chiếc điện thoại W760 với m&agrave;u sắc sặc sở, m&aacute;y được hỗ trợ nội dung từ k&ecirc;nh truyền h&igrave;nh &acirc;m nhạc MTV.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;">\r\n<p><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F3/C5/sony-eric.jpg" border="1" alt="Sony Ericsson W760 MTV c&oacute; m&agrave;u sắc trẻ trung. Ảnh: Softpedia." width="400" height="295" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;" align="center">Sony Ericsson W760 MTV c&oacute; m&agrave;u sắc trẻ trung. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>Softpedia.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Sony Ericsson W760 MTV Edition sẽ c&oacute; mặt trong hai tuần nữa, đầu ti&ecirc;n l&agrave; Italy v&agrave; sau đ&oacute; l&agrave; c&aacute;c quốc gia ch&acirc;u &Acirc;u kh&aacute;c với gi&aacute; b&aacute;n l&agrave; 460 USD.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">W760 phi&ecirc;n bản MTV sở hữu lớp vỏ ngo&agrave;i trẻ trung, rực rỡ, m&aacute;y cho ph&eacute;p truy cập nhanh c&aacute;c chương tr&igrave;nh video, nhạc v&agrave; nội dung di động của MTV.</p>\r\n<p>&nbsp;</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Ngo&agrave;i ra, thiết bị kh&ocirc;ng kh&aacute;c nhiều so với<span class="Apple-converted-space">&nbsp;</span><a class="Normal" style="text-decoration: none; color: #1f1f1f; font: 12px Arial; padding: 0px; margin: 0px;" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2008/01/3B9AF037/">phi&ecirc;n bản nguy&ecirc;n gốc</a><span class="Apple-converted-space">&nbsp;</span>với thiết kế trượt v&agrave; chương tr&igrave;nh chơi nhạc Walkman 3.0, loa ngo&agrave;i lớn, Bluetooth 2.0, FM v&agrave; c&aacute;c t&iacute;nh năng hỗ trợ chơi nhạc hay.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">W760 sở hữu m&agrave;n h&igrave;nh 2,2 inch, kết nối tốc độ cao HSDPA hỗ trợ duyệt web. Chiếc Walkman phone n&agrave;y cũng t&iacute;ch hợp GPS, bản đồ Google c&ugrave;ng camera 3,2 Megapixel.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (666, 48, 1, 'Vertu Constellation Ayxta ', ' Vertu Constellation Ayxta  \r\n- Thiết kế nắp gập thời trang\r\n- Máy ảnh 3.15 MP, hỗ trợ Flash\r\n- Máy nghe nhạc, xem phim MP3, MP4, AAC+\r\n- Chức năng định vị GPS, hỗ trợ google map', '0000-00-00', 100000000, 'Vertu_Ayxta_b.jpg', '<div class="new_pro_detail_contet"><span class="Content"> <span id="ctl00_ContentPlaceHolder1_ctl00_lblNews_Content" class="Content">\r\n<p><strong>Đ&atilde; từng ra tay s&aacute;ng chế kh&ocirc;ng biết bao nhi&ecirc;u di động &ldquo;khủng long&rdquo; nhưng m&agrave; tới nay Vertu mới chịu &ldquo;x&eacute; r&agrave;o&rdquo;, dồn sức chuẩn bị m&ocirc; h&igrave;nh điện thoại nắp gập đầu ti&ecirc;n trong lịch sử hoạt động của m&igrave;nh. </strong></p>\r\n<p>Si&ecirc;u dế mới thuộc d&ograve;ng Constellation chỉ vừa ra mắt  tại London c&aacute;ch đ&acirc;y &iacute;t h&ocirc;m, <span style="font-weight: bold;">Ayxta </span>nhằm v&agrave;o  những c&ocirc;ng d&acirc;n s&agrave;nh điệu to&agrave;n cầu đang t&igrave;m kiếm một sản phẩm chất lượng cao để gi&uacute;p duy tr&igrave; cuộc sống đặc biệt ở bất kỳ đ&acirc;u tr&ecirc;n thế giới. Mobile &ldquo;tự h&agrave;o&rdquo; dịch vụ <span style="font-style: italic;">Vertu  Select City Brief</span> cho ph&eacute;p cập nhật đầy đủ c&aacute;c th&ocirc;ng tin chi tiết  về th&agrave;nh phố nơi m&agrave; em dế gh&eacute; thăm.</p>\r\n<p><img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2009/09/25/090925a4b2.jpg" alt="" /></p>\r\n<p>Đặc biệt hơn, <span style="font-weight: bold;">Constellation  Ayxta</span> c&ograve;n được Vertu sắm sửa nhạc chu&ocirc;ng độc quyền do một ban nhạc đến từ nước Anh mang t&ecirc;n Zero 7 bi&ecirc;n soạn. C&ograve;n lại, di động vẫn sẽ trang bị bộ khung bằng th&eacute;p kh&ocirc;ng gỉ, một số chi tiết bằng nh&ocirc;m hoặc gốm cao cấp v&agrave; m&agrave;n h&igrave;nh sa-phia chống xước ho&agrave;n hảo.</p>\r\n<div>B&ecirc;n cạnh m&aacute;y ảnh 3 chấm c&oacute; đ&egrave;n flash k&eacute;p, <span style="font-weight: bold;">Constellation Ayxta</span> cũng chơi cả khe cắm thẻ nhớ microSD tối đa 8GB, kết nối 3G c&ugrave;ng khả năng ghi ch&uacute; si&ecirc;u việt. Vertu cho biết, sản phẩm sẽ được b&aacute;n ra với mức gi&aacute; v&agrave;o khoảng 7.100 đến 9.500 USD (tương đương 128 đến 171 triệu đồng).</div>\r\n<div><br /><img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2009/09/25/090925a2b2.jpg" alt="" /><br /><br /><img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2009/09/25/090925a3b2.jpg" alt="" /></div>\r\n<div>&nbsp;</div>\r\n<div><img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2009/09/25/090925a1b2.jpg" alt="" /></div>\r\n</span> </span></div>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (667, 40, 1, 'SONY ERICSSON W595', 'Kiểu dáng nắp trượt sành điệu\r\nCamera 3.2 MP, hỗ trợ xem phim\r\nMáy nghe nhạc Walkman\r\nĐài FM, Bluetooth, 3G', '0000-00-00', 3589000, 'bSE-W595.jpg', '<p class="Title" style="color: #810004; padding-left: 10px; font: bold 20px Arial;">Sony Ericsson W595</p>\r\n<p class="Lead" style="margin-left: 10px; color: #343434; font: bold 12px Arial;">Walkman chơi nhạc mới W595i c&oacute; kiểu d&aacute;ng chắc gọn, ấn tượng với hai loa ph&aacute;t nhạc, kế thừa<span class="Apple-converted-space">&nbsp;</span><a class="Lead" style="text-decoration: none; color: #343434; font: bold 12px Arial;" href="http://sohoa.vnexpress.net/News/Dien-thoai/2007/10/3B9AEE02/">W580i</a>, m&aacute;y c&ograve;n n&acirc;ng cấp với khả năng nghe nhạc hay, kết nối 3G, chụp ảnh 3,2 Megapixel.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Chiếc Walkman phone mới W595 vừa xuất hiện tr&ecirc;n thị trường đặc biệt với hai loa tr&ecirc;n v&agrave; dưới. M&aacute;y kế thừa phi&ecirc;n bản W580i đ&atilde; được đ&oacute;n nhận trước đ&acirc;y.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F9/A9/sc001.jpg" border="1" alt="Thiết kế của W595 gọn g&agrave;ng v&agrave; hai loa tr&ecirc;n v&agrave; dưới. Ảnh: Cnet." width="400" height="300" align="center" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;">Thiết kế của W595 gọn g&agrave;ng v&agrave; hai loa tr&ecirc;n v&agrave; dưới. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>Cnet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;"><strong><span style="color: #2f2f2f;">So với c&aacute;c mẫu Walkman chơi nhạc m&agrave;u cam trước đ&acirc;y, d&ograve;ng di động mới của Sony Ericsson hiện tại đ&atilde; thanh tho&aacute;t, chắc gọn hơn.</span></strong><span class="Apple-converted-space">&nbsp;</span>W910i hay W760i l&agrave; những v&iacute; dụ. Điện thoại mới W595 sở hữu kiểu d&aacute;ng trượt, tuy nhi&ecirc;n, th&acirc;n h&igrave;nh kh&aacute; nhỏ nhắn với k&iacute;ch thước 100 x 47 x 14 mm v&agrave; nặng 104 gram.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Sony Ericsson W595 kh&ocirc;ng c&oacute; nhiều kh&aacute;c biệt về thiết kế so với W580i. M&aacute;y c&oacute; c&aacute;c đường cong quyến rũ, diện t&iacute;ch mặt trước b&eacute; v&agrave; mặt sau lớn, cạnh tranh v&agrave; cạnh dưới được v&aacute;t rộng ra ph&iacute;a sau v&agrave; l&agrave; nơi để hai loa ngo&agrave;i. Thiết kế n&agrave;y l&agrave;m cho điện thoại ph&aacute;t ra &acirc;m thanh dễ hơn.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Một v&agrave;i điểm kh&aacute;c so với W580i như camera đ&atilde; thay đổi vị tr&iacute;, cổng USB được di chuyển. C&ograve;n lại, W595 cũng kh&ocirc;ng c&oacute; giắc cắm tai nghe 3,5 mm như nhiều di động d&ograve;ng Walkman kh&aacute;c.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">M&aacute;y c&oacute; m&agrave;n h&igrave;nh rộng 2,2 inch, độ ph&acirc;n giải 240 x 320 pixel, h&igrave;nh ảnh hiển thị sắc n&eacute;t v&agrave; cho ph&eacute;p xem phim, đọc c&aacute;c văn bản hiển thị chứ b&eacute; dễ d&agrave;ng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F9/A9/sc003.jpg" border="1" alt="M&aacute;y trang bị camera 3,2 Megapixel. Ảnh: Cnet." width="400" height="323" align="center" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;">M&aacute;y trang bị camera 3,2 Megapixel. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>Cnet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;"><strong>So với c&aacute;c di động chơi nhạc kh&aacute;c, c&aacute;c t&iacute;nh năng của W595 nằm trong nh&oacute;m tầm trung.</strong></p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Cải tiến nổi bật dễ nhận thấy của W595 l&agrave; camera đ&atilde; được tăng l&ecirc;n từ 2 l&ecirc;n 3,2 Megapixel. Tuy nhi&ecirc;n, m&aacute;y kh&ocirc;ng trang bị đ&egrave;n flash, v&igrave; thế người d&ugrave;ng chỉ c&oacute; thể chụp c&aacute;c bức ảnh tốt trong điều kiện trời s&aacute;ng.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Đi k&egrave;m với m&aacute;y l&agrave; thẻ nhớ M2 dung lượng 2 GB, model hỗ trợ mở rộng tối đa dung lượng l&ecirc;n 8 GB. Tuy vậy, điều khiến người d&ugrave;ng kh&ocirc;ng h&agrave;i l&ograve;ng ch&iacute;nh l&agrave; khe cắm thẻ nhớ được đặt ph&iacute;a sau pin, mỗi lần thay hoặc th&aacute;o ra lại phải tắt m&aacute;y.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">C&aacute;c ph&iacute;m bấm chuy&ecirc;n dụng nghe nhạc tr&ecirc;n Sony Ericsson W595 được bố tr&iacute; đơn giản, tương đối thuận lợi cho người d&ugrave;ng trong qu&aacute; tr&igrave;nh sử dụng. Điện thoại hỗ trợ c&aacute;c định dạng &acirc;m thanh cơ bản như MP3, AAC, WMA v&agrave; WAV v&agrave; WMA. Loa ngo&agrave;i được bố tr&iacute; hai đầu m&aacute;y, &acirc;m thanh lớn v&agrave; mạnh mẽ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/F9/A9/sc005.jpg" border="1" alt="Model dạng trượt n&agrave;y c&oacute; &acirc;m thanh lớn, kết nối 3G. Ảnh: Cnet." width="400" height="300" align="center" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;">Model dạng trượt n&agrave;y c&oacute; &acirc;m thanh lớn, kết nối 3G. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>Cnet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Tuy nhi&ecirc;n, xem phim tr&ecirc;n m&aacute;y kh&ocirc;ng thuận lợi. W595 hỗ trợ định dạng MPEG-4, tuy nhi&ecirc;n kh&ocirc;ng c&oacute; phần mềm hỗ trợ đi k&egrave;m, Sony Ericsson cũng kh&ocirc;ng hỗ trợ ứng dụng đổi định dạng để xem video. Một t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute; l&agrave; m&aacute;y hỗ trợ YouTube, cho ph&eacute;p người d&ugrave;ng xem v&agrave; tải trực tiếp c&aacute;c đoạn video l&ecirc;n trang chia sẻ n&agrave;y.</p>\r\n<p>&nbsp;</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">M&aacute;y chạy 4 băng tần GSM, hỗ trợ người d&ugrave;ng c&oacute; thể chuyển mạng, roaming dễ d&agrave;ng tại nhiều quốc gi&aacute; kh&aacute;c nhau. W595 t&iacute;ch hợp 3G, kết nối HSDPA b&ecirc;n cạnh Bluetooth.</p>\r\n<p>&nbsp;</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Giống như phần lớn c&aacute;c mẫu Walkman kh&aacute;c, W595 l&agrave; một phi&ecirc;n bản n&acirc;ng cấp, c&aacute;c t&iacute;nh năng đơn giản l&agrave; nghe nhạc với chất lượng &acirc;m thanh tốt, nghe bằng tai nghe hay hơn. So s&aacute;nh với c&aacute;c mẫu di động chơi nhạc trong tầm gi&aacute;, chất lượng của m&aacute;y chấp nhận được. Tuy nhi&ecirc;n, nếu để b&ecirc;n cạnh c&aacute;c thiết bị chơi nhạc MP3, &acirc;m thanh của W595 vẫn c&ograve;n thua k&eacute;m.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Chất lượng đ&agrave;m thoại ổn định, kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng ph&agrave;n n&agrave;n. Pin tr&ecirc;n m&aacute;y cho ph&eacute;p đ&agrave;m thoại được 9 giờ, thời gian chờ l&agrave; 16 ng&agrave;y. Sử dụng b&igrave;nh thường với c&aacute;c t&iacute;nh năng nghe gọi, khoảng 4 đến 5 ng&agrave;y mới phải sạc lại.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Sony Ericsson W595 đ&atilde; c&oacute; mặt tr&ecirc;n thị trường. Gi&aacute; tham khảo: 5,8 triệu đồng.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (668, 40, 1, 'SONY ERICSSON T700', 'Thiết kế cổ điển thời trang với vỏ kim loại\r\nCamera 3.2 MP, hỗ trợ quay phim\r\nMáy nghe nhạc nhiều định dạng với walkman player 3.0\r\nKết nối 3G với tốc truyền dữ liệu 3.6 Mbps\r\nĐài FM, Bluetooth, USB, GPRS', '0000-00-00', 3089000, 'T700_b.jpg', '<p class="Title" style="color: #810004; padding-left: 10px; font: bold 20px Arial;">Sony Ericsson T700 đa t&agrave;i</p>\r\n<p class="Lead" style="margin-left: 10px; color: #343434; font: bold 12px Arial;">Chiếc m&aacute;y n&agrave;y kế thừa nhiều ưu điểm từ tiền th&acirc;n T650 với giao diện "mượn" của W980 v&agrave; mức gi&aacute; hợp l&yacute; 4,5 triệu đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/8B/son1.jpg" border="1" alt="Sony Ericsson T700 sẽ c&oacute; m&agrave;u đỏ. Ảnh: iTechnews." width="400" height="294" align="center" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;">Sony Ericsson T700 c&oacute; m&agrave;u đỏ. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>iTechnews.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">T700 c&oacute; k&iacute;ch thước 104 x 48 x 10 mm v&agrave; nặng chỉ 78 gram, th&ocirc;ng số kh&aacute; l&yacute; tưởng cho một chiếc điện thoại di động. M&agrave;n h&igrave;nh 2 inch thể hiện độ tương phản tốt v&agrave; c&oacute; thể nh&igrave;n r&otilde; ngay cả dưới &aacute;nh s&aacute;ng mặt trời. B&agrave;n ph&iacute;m nhỏ gọn tương tự như W980 nhưng được cải tiến để c&oacute; độ nhấn mềm mại v&agrave; dễ d&agrave;ng hơn. Ph&iacute;m điều hướng D-pad v&agrave; Call, End c&oacute; h&igrave;nh d&aacute;ng vu&ocirc;ng v&agrave; hơi nh&ocirc; l&ecirc;n để tr&aacute;nh việc ấn nhầm. Giao diện m&aacute;y kh&aacute; quen thuộc với người sử dụng v&agrave; kh&ocirc;ng thay đổi nhiều so với W980.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">M&aacute;y nổi bật với chức năng quản l&yacute; tin nhắn được lưu tại bộ nhớ trong hoặc thẻ nhớ ngo&agrave;i theo thứ tự ng&agrave;y, dung lượng v&agrave; số điện thoại. Được kế thừa c&ocirc;ng nghệ &acirc;m thanh tuyệt đỉnh Walkman của Sony, tr&igrave;nh nghe nhạc Music Player 3.0 c&oacute; giao diện Media tự động thay đổi theo chủ đề v&agrave; được thiết kế kh&aacute; đẹp. Chất lượng &acirc;m thanh sống động v&agrave; trong trẻo nổi trổi so với nhiều d&ograve;ng nghe nhạc kh&aacute;c nhờ bộ chỉnh tăng &acirc;m Megabass v&agrave; stereo mở rộng. M&aacute;y hỗ trợ nhiều định dạng tập tin, bao gồm MP3, AAC, AAC+, E-AAC+, WAV, WMA, M4A v&agrave; MIDI.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Camera đi k&egrave;m 3,2 megapixel c&ugrave;ng đ&egrave;n Flash LED cho h&igrave;nh ảnh đẹp. Nếu so c&ugrave;ng nhiều điện thoại tr&ecirc;n 3 Megapixel hiện nay, h&igrave;nh ảnh T700 tạo ra sắc n&eacute;t hơn rất nhiều. C&aacute;c t&iacute;nh năng nổi bật từ d&ograve;ng Cyber-shot đều được t&iacute;ch hợp trong d&ograve;ng m&aacute;y n&agrave;y với bộ c&ocirc;ng cụ tiện &iacute;ch cho việc kiểm so&aacute;t chế độ chụp, cảnh, k&iacute;ch thước ảnh&hellip; M&aacute;y c&ograve;n c&oacute; khả năng quay video ở tốc độ 15 khung h&igrave;nh mỗi gi&acirc;y với định dạng Mp4 độ ph&acirc;n giải QVGA (240 x 320 pixel).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="vertical-align: top;"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/FC/8B/son2.jpg" border="1" alt="Bản m&agrave;u đen. Ảnh: iTechnews." width="370" height="400" align="center" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" style="vertical-align: top; font: 11px Arial; color: #838383;">Bản m&agrave;u đen. Ảnh:<span class="Apple-converted-space">&nbsp;</span><em>iTechnews.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Ngo&agrave;i tr&igrave;nh duyệt quen thuộc OperaMini, m&aacute;y hỗ trợ Access NetFront 3.4 chuy&ecirc;n d&ugrave;ng cho m&aacute;y Sony với c&aacute;c t&iacute;nh năng: chế độ to&agrave;n m&agrave;n h&igrave;nh v&agrave; một số chế độ xem kh&aacute;c như: to&agrave;n cảnh, chỉ c&oacute; văn bản, Pan v&agrave; Zoom, ph&oacute;ng to trang.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Khả năng kết nối kh&aacute; mạnh với hỗ trợ đầy đủ GSM/GPRS/EDGE v&agrave; 3G. Ngo&agrave;i ra, Bluetooth c&oacute; hỗ trợ A2DP cho ph&eacute;p nghe nhạc với tai nghe stereo kh&ocirc;ng d&acirc;y. Kết nối USB 2.0 c&oacute; 4 chế độ: trao đổi dữ liệu, sử dụng điện thoại như một modem, đồng bộ h&oacute;a với Window Media Player v&agrave; m&aacute;y in.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Từ khi vừa được giới thiệu hồi th&aacute;ng 8/2008, T700 đ&atilde; tạo n&ecirc;n cơn sốt trong cộng đồng người sử dụng trong nước. Hầu như kh&ocirc;ng nhiều cửa h&agrave;ng c&oacute; đủ c&aacute;c mẫu cho m&aacute;y. C&aacute;c đại l&yacute; vẫn đang chờ đợt h&agrave;ng mới để cung cấp đủ cho kh&aacute;ch. Theo th&ocirc;ng tin từ nh&agrave; ph&acirc;n phối từ giữa th&aacute;ng 3, sẽ c&oacute; T700 vỏ m&agrave;u hồng trẻ trung v&agrave; thanh lịch.</p>\r\n<p class="Normal" style="font: 11pt ''Times New Roman''; color: #1f1f1f; padding-left: 10px;">Từ ng&agrave;y 2 đến 8/3, 100 kh&aacute;ch h&agrave;ng mua điện thoại T700 hay Sony Ericsson F305 sẽ được tặng một cặp v&eacute; xem phim &ldquo;Cuộc chiến n&agrave;ng d&acirc;u&rdquo; tại Megarstar v&agrave;o ng&agrave;y 8/3. M&aacute;y T700 của Sony Ericsson hiện đang c&oacute; gi&aacute; c&ocirc;ng bố khoảng 4,5 triệu tại thị trường Việt Nam qua nh&agrave; ph&acirc;n phối CMC Distribution.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (669, 48, 1, 'Vertu Constellation Polished black', ' Vertu Constellation Polished black  \r\n- Ứng dụng Java MIDP 2.0\r\n- Thiết kế độc đáo, được trau chuốt kỹ lưỡng\r\n- Đồng bộ máy tính và làm Modem tốc độ cao.\r\n- Chức năng báo thức\r\n- Tích hợp sổ tay-lịch cao cấp\r\n- Bộ đóan trước ký tự đa ngôn ngữ\r\n- Hỗ trợ đ', '0000-00-00', 98547000, 'bConstellationPolishedblack.jpg', '<p class="Title">Vertu - điện thoại đắt hơn &ocirc;t&ocirc;</p>\r\n<p class="Lead">Trong bộ sưu tập điện thoại Vertu m&agrave; C&ocirc;ng ty TNHH Ph&acirc;n  phối FPT (FDC), vừa giới thiệu tại Việt Nam, d&ograve;ng Signature c&oacute; mức gi&aacute;  đỉnh điểm l&ecirc;n đến 23.000 USD, tức l&agrave; đắt hơn cả một chiếc xe Daewoo  Gentra mới được tr&igrave;nh l&agrave;ng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/DC/B0/ve2.jpg" border="1" alt="Vertu Signature. (	 Economy.enorth.com.cn)" width="225" height="300" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vertu Signature. (<em> Economy.enorth.com.cn</em>)</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ng&agrave;y 19/1, C&ocirc;ng ty FDC đ&atilde; k&yacute; kết với h&atilde;ng điện thoại  cao cấp Vertu để cung cấp tại Việt Nam hai d&ograve;ng sản phẩm Signature v&agrave;  Ascent, tại 88 Đồng khởi, Quận1, TP HCM.</p>\r\n<p class="Normal">Vertu Signature đầu ti&ecirc;n được đưa ra thị trường v&agrave;o  th&aacute;ng 1/2002. Điện thoại được cấu th&agrave;nh từ tr&ecirc;n 400 bộ phận kh&aacute;c nhau,  sử dụng c&aacute;c chất liệu như th&eacute;p kh&ocirc;ng gỉ, v&agrave;ng trắng 18 carat, v&agrave;ng 18  carat v&agrave; platin. D&ograve;ng sản phẩm n&agrave;y bao gồm Signature 18 Carat Yellow  Gold, Signature 18 Carat White Gold với c&aacute;c hạt kim cương được nạm b&aacute;n  phần v&agrave; to&agrave;n phần, c&ugrave;ng điện thoại Signature Platinum nạm một vi&ecirc;n kim  cương c&oacute; khối lượng 0,25 carat. C&aacute;c nghệ nh&acirc;n thủ c&ocirc;ng đ&atilde; phải mất đến 7  ng&agrave;y để nạm bằng tay trung b&igrave;nh 700 vi&ecirc;n kim cương.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9A/DC/B0/ve3.jpg" border="1" alt="Vertu Ascent. (Tourneau)" width="201" height="300" align="center" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vertu Ascent. (<em>Tourneau</em>)</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Vertu Ascent c&oacute; 3 m&agrave;u đỏ, v&agrave;ng, xanh, thể hiện m&agrave;u sắc  cổ điển của những chiếc xe nổi tiếng: Ferrari (đỏ), Lamborghini (v&agrave;ng)  v&agrave; Bugatti (xanh). Gi&aacute;m đốc S&aacute;ng tạo của Vertu, &ocirc;ng Frank Nuovo, tiết  lộ, d&ograve;ng Ascent c&oacute; đường n&eacute;t thanh lịch của những chiếc xe sang trọng,  đặc biệt điện thoại c&ograve;n được bổ sung những điểm nhấn với chất liệu da  thuộc.</p>\r\n<table border="0" cellspacing="7" cellpadding="7" width="209" align="right">\r\n<tbody>\r\n<tr>\r\n<td class="Image"><a class="Image" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2005/12/3B9ADA93/"><strong><span style="color: #595900;">*Vertu: Xe đ&egrave; kh&ocirc;ng  chết</span></strong></a>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><a class="Image" onclick="return openMeExt(this,1,1,1,1,1,0,1,1,300,400,0,0,0)" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2005/10/3B9AD821/"><strong><span style="color: #555500;">*Di động Samsung ''học đ&ograve;i''  l&agrave;m qu&yacute; tộc</span></strong></a></td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><a class="Image" onclick="return openMeExt(this,1,1,1,1,1,0,1,1,300,400,0,0,0)" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2005/06/3B9AD257/"><strong><span style="color: #555500;">*Chơi điện thoại qu&yacute; tộc</span></strong></a></td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><a class="Image" onclick="return openMeExt(this,1,1,1,1,1,0,1,1,300,400,0,0,0)" href="http://sohoa.vnexpress.net/SH/Dien-thoai/2004/12/3B9ACBDA/"><strong><span style="color: #555500;">*Mobiado - di động qu&yacute; tộc</span></strong></a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Vertu Ascent l&agrave;m nổi bật sự tương phản giữa cảm nhận  da thuộc kh&acirc;u tay mềm mại với hợp kim sử dụng trong chế tạo t&agrave;u con thoi  của Mỹ, mang lại cho người d&ugrave;ng cảm gi&aacute;c của sự vững bền. Đặc biệt, b&agrave;n  ph&iacute;m Vertu Ascent được sản xuất kh&aacute; phức tạp. H&atilde;ng Vertu đ&atilde; bơm hỗn hợp  th&eacute;p kh&ocirc;ng gỉ ở nhiệt độ cao v&agrave; &eacute;p v&agrave;o một khu&ocirc;ng c&oacute; k&iacute;ch thước lớn  hơn. Khi nguội đi, ph&iacute;m sẽ co lại 14% để tạo ra thiết kế b&agrave;n ph&iacute;m c&oacute; g&oacute;c  xi&ecirc;n. Sau đ&oacute; từng ph&iacute;m được đặt tr&ecirc;n hai v&ograve;ng đệm bằng đồ trang sức,  tạo ra t&aacute;c động xoay độc đ&aacute;o v&agrave; bảo đảm đ&uacute;ng &acirc;m thanh vang l&ecirc;n khi nhấn  ph&iacute;m.</p>\r\n<p class="Normal">Những chiếc điện thoại n&agrave;y sẽ c&oacute; mặt Việt Nam v&agrave;o cuối  th&aacute;ng 1. Gi&aacute; b&aacute;n dự kiến của d&ograve;ng Ascent l&agrave; từ 5.000 USD đến 5.600 USD;  d&ograve;ng Signature l&agrave; từ 7.500 USD đến 23.000 USD. Ngo&agrave;i Signature v&agrave;  Ascent, Vertu c&ograve;n c&oacute; d&ograve;ng sản phẩm Diamond được sản xuất theo thiết kế  ri&ecirc;ng của Vertu, mỗi m&aacute;y gồm 388 bộ phận cơ.&nbsp;</p>\r\n<p class="Normal">Vertu l&agrave; nh&agrave; sản xuất điện thoại thủ c&ocirc;ng cao cấp h&agrave;ng  đầu tr&ecirc;n thế giới. Điện thoại của h&atilde;ng c&oacute; kiểu d&aacute;ng thiết kế tương tự  c&aacute;c d&ograve;ng đồng hồ sang trọng v&agrave; sử dụng chất liệu của xe hơi cao cấp v&agrave;  th&eacute;p kh&ocirc;ng gỉ, da thuộc, v&agrave;ng platin v&agrave; kim cương. Mặt k&iacute;nh của m&aacute;y l&agrave;  mặt đ&aacute; ngọc b&iacute;ch. Đặc biệt, tất cả c&aacute;c m&aacute;y của Vertu đều được chế t&aacute;c  bằng tay bởi những chuy&ecirc;n gia d&agrave;y dạn kinh nghiệm.</p>\r\n<p class="Normal">Trong qu&aacute; tr&igrave;nh sản xuất điện thoại Vertu, h&atilde;ng sản  xuất đ&atilde; được trao 74 bằng s&aacute;ng chế, điều đặc biệt chưa từng c&oacute; trong  ng&agrave;nh điện thoại di động. Ngo&agrave;i ra, Vertu c&ograve;n c&oacute; dịch vụ Concierge ri&ecirc;ng  của h&atilde;ng: Chỉ cần một lần nhấn n&uacute;t người d&ugrave;ng sẽ được kết nối với mạng  hỗ trợ to&agrave;n cầu suốt 24 giờ. Do đ&oacute;, điện thoại Vertu được coi l&agrave; t&acirc;m  điểm truyền th&ocirc;ng, cũng như l&agrave; một chiếc v&eacute; danh dự để bước v&agrave;o những  kh&aacute;ch sạn, rạp h&aacute;t tốt nhất tr&ecirc;n thế giới.</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (670, 49, 1, 'Mobell M770      ', 'Mobell M770  \r\n\r\n    * Màn hình cảm ứng kéo thả độc đáo\r\n    * Hỗ trợ 2 Sim 2 Sóng online\r\n    * Máy ảnh 3.2 MP, hỗ trợ quay phim\r\n    * Đài FM tích hợp, jack tai nghe 3.5mm\r\n    * Chức năng backup danh bạ vào thẻ nhớ\r\n    * Lắc tay đổi nhạc, video tắt và', '0000-00-00', 1789000, 'Mobell_M770_b.jpg', '<h2>B&agrave;i viết về sản phẩm Mobell M770</h2>\r\n<div class="news_description">B&agrave;i viết về sản phẩm Mobell M770</div>\r\n<p>Tuần qua, Mobell đ&atilde; ch&iacute;nh thức giới  thiệu kh&aacute;c h&agrave;ng của m&igrave;nh sản phẩm cao cấp M770. Đ&acirc;y sẽ l&agrave; model hứa hẹn  đem sẽ đem lại nhiều th&uacute; vị cho người sử dụng n&oacute; v&agrave; tạo cơn sốt cho d&ograve;ng  m&aacute;y cảm ứng của Mobell.<br /><br />Mobell M770 với m&agrave;n h&igrave;nh cảm ứng k&eacute;o thả  rộng 3.2 inches v&agrave; camera 3.2 MP, đ&acirc;y l&agrave; sự bức ph&aacute; lớn của h&atilde;ng Mobell  trong việc chinh phục kh&aacute;ch h&agrave;ng.<br /><br /><img src="http://www.thegioididong.com/dtdd/news/BaivietSP/Mobell/M770.jpg" alt="Điện thoại di động: Mobell M770" /></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (671, 49, 1, 'Mobell M760 ', 'Mobell M760  \r\n\r\n    * Kiểu dáng sang trọng, mạnh mẽ\r\n    * Màn hình cảm ứng rộng 3.2 inches\r\n    * Hỗ trợ 2 Sim 2 Sóng online\r\n    * Máy ảnh 2.0 MP, hỗ trợ quay phim\r\n    * Chặn cuộc gọi, Ebuddy,Opera, Facebook, MSN ', '0000-00-00', 1589000, 'M760_b.jpg', '<p><strong>Th&ocirc;ng tin về sản phẩm</strong>:<br /><br /> &nbsp;         <br /></p>\r\n<p><strong>H&agrave;ng ch&iacute;nh h&atilde;ng của </strong><strong>Mobell</strong><strong> Việt Nam. Bảo h&agrave;nh 12 th&aacute;ng tr&ecirc;n to&agrave;n quốc.</strong><strong><br /><br /><br /><strong>T&iacute;nh năng nổi bật:</strong></strong></p>\r\n<p>- Kiểu d&aacute;ng sang trọng, mạnh mẽ<br />- M&agrave;n h&igrave;nh cảm ứng rộng 3.2 inches<br />- Hỗ trợ 2 Sim 2 S&oacute;ng online<br />- M&aacute;y ảnh 2.0 MP, hỗ trợ quay phim<br />- Chặn cuộc gọi, Ebuddy,Opera, Facebook, MSN</p>\r\n<p><strong>Th&ocirc;ng tin chi tiết:</strong></p>\r\n<p>+ Băng tần GSM 900/1800 <br /> + Hỗ trợ mạng tại Việt Nam MobiFone, VinaPhone, Viettel, Vietnamobile <br /> + K&iacute;ch thước&nbsp; <br /> + Trọng lượng g <br /> + Thời gian đ&agrave;m thoại 4 (giờ) <br /> + Thời gian chờ 250 (giờ)<br /> + Chụp h&igrave;nh 2 MP, 1600x1200 pixels<br /> + Quay phim T&ugrave;y thuộc bộ nhớ <br /> + Video call Kh&ocirc;ng <br /> + Nghe nhạc MP3&nbsp;<br /> + Xem phim 3Gp, MP4 <br /> + Đ&agrave;i FM C&oacute;<br /> + Ghi &acirc;m Kh&ocirc;ng <br /> + Ghi &acirc;m cuộc gọi Kh&ocirc;ng<br /> + Xem Tivi Kh&ocirc;ng<br /> + Bộ nhớ trong <strong>58MB</strong> <br /> + Thẻ nhớ ngo&agrave;i: <strong></strong><strong>TranFlash  (Micro SD) - Hỗ trợ đến 8GB</strong><strong></strong><br /> + Kết nối USB Kh&ocirc;ng <br /> + Bluetooth Kh&ocirc;ng <br /> + Hồng ngoại Kh&ocirc;ng <br /> + Tr&igrave;nh duyệt Wap C&oacute; &nbsp;<br /> + Java Kh&ocirc;ng <br /> + GPRS kh&ocirc;ng <br /> + HSCSD Kh&ocirc;ng <br /> + EDGE Kh&ocirc;ng <br /> + 3G Kh&ocirc;ng<br /> + UMTS Kh&ocirc;ng<br /> + WCDMA Kh&ocirc;ng<br /> + Wifi Kh&ocirc;ng<br /> + GPS Kh&ocirc;ng<br /> + Loại m&agrave;n h&igrave;nh TFT 65.536 m&agrave;u <br /> + K&iacute;ch thước m&agrave;n h&igrave;nh 3.2 inches 320 x 480 Pixels <br /> + Nhạc chu&ocirc;ng 64 &acirc;m săc MIDI, MP3 <br /> + B&aacute;o rung C&oacute; <br /> + Tin nhắn SMS <br /> + Danh bạ 300 s&ocirc; <br /> + Loa ngo&agrave;i C&oacute; <br /> + Ứng dụng văn ph&ograve;ng Kh&ocirc;ng <br /> + Hệ điều h&agrave;nh --- <br /> + Tr&ograve; chơi C&agrave;i sẵn trong m&aacute;y <br /> + Đồng hồ C&oacute; <br /> + B&aacute;o thức C&oacute; <br /> + Ng&ocirc;n ngữ Tiếng Việt<br /> + Kiểu d&aacute;ng Thanh<br /> + M&agrave;u sắc: <strong>Đen</strong></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (672, 49, 1, 'Mobell M750', '    * Màn hình cảm ứng với chức năng kéo thả\r\n    * Hỗ trợ 2 Sim 2 Sóng\r\n    * Máy ảnh VGA, hỗ trợ quay phim\r\n    * Máy nghe nhạc MP3, xem phim MP4, 3GP, AVI\r\n    * Đài FM tích hợp\r\n    * Chức năng gửi và nhận mail ', '0000-00-00', 1489000, 'Mobell_M750_b.jpg', '<p><span class="Promotion_Item">Điện thoại 2 sim 2 s&oacute;ng - M&agrave;n hnh cảm ứng với chức năng k&eacute;o thả - Camera chụp ảnh v&agrave; quay phim - FM Radio - Kết nối Bluetooth - Thẻ nhớ MicroSd 2 Gb mở rộng - Tin nhắn E-mail - Ghi &acirc;m/ Ghi &acirc;m cuộc gọi - Lịch - Sắp xếp - B&aacute;o thức</span></p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (673, 49, 1, 'Mobell M116 ', 'Mobell M116  \r\n\r\n    * Thiết kế dạng thanh với bàn phím Qwerty\r\n    * Hỗ trợ 2 Sim 2 sóng online\r\n    * Máy ảnh VGA (480 x 640 pixcels)\r\n    * Đài FM tích hợp\r\n    * Nghe nhạc: MP3\r\n    * Xem Phim: 3GP\r\n    * Kết nối Bluetooth V2.0 với A2DP ', '0000-00-00', 1290000, 'mobell_M116_b.jpg', '<p class="Title">Điện thoại 2 SIM, 2 s&oacute;ng đang hot</p>\r\n<p class="Lead">Trong số những mẫu 2 SIM, 2 s&oacute;ng đang c&oacute; tr&ecirc;n thị  trường, phần nhiều l&agrave; sản phẩm của Q-Mobile, c&ograve;n lại đến từ Philips,  Samsung hay Mobell.</p>\r\n<p class="Normal">Thị trường xuất hiện nhiều sản phẩm sử dụng 2 SIM, 2  s&oacute;ng. Dưới đ&acirc;y l&agrave; những so s&aacute;nh về t&iacute;nh năng kỹ thuật v&agrave; chất lượng của  những sản phẩm được cho l&agrave; ti&ecirc;u biểu trong d&ograve;ng điện thoại n&agrave;y.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="407" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/14/3B/philips-xenium-x710-.jpg" border="1" alt="" width="400" height="253" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Philips X710 c&oacute; gi&aacute; tham khảo 3,9 triệu đồng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>Philips X710</strong> c&oacute; năng nhận cả 2 cuộc  gọi ở cả 2 SIM kh&aacute;c nhau c&ugrave;ng một thời điểm. M&aacute;y c&oacute; thiết kế dạng thanh  truyền thống, m&agrave;u đen chắc khoẻ sang trọng. Sản phẩm hỗ trợ ba băng tần  GSM c&ugrave;ng c&aacute;c kết nối GPRS/EDGE. M&aacute;y được trang bị m&agrave;n h&igrave;nh TFT rộng 2,4  inch, độ ph&acirc;n giải 240 x 320 pixel, c&oacute; khả năng hiển thị 262.144 m&agrave;u.</p>\r\n<p class="Normal">Philips Xenium X710 c&ograve;n sở hữu camera chế độ autofocus  3,15 Megapixel (2.048 x 1.536 pixel) cho chất lượng ảnh chụp sắc n&eacute;t.  Ch&uacute; "dế" phổ th&ocirc;ng n&agrave;y cũng c&oacute; khả năng bắt s&oacute;ng radio FM, chơi nhạc MP3  với tai nghe k&egrave;m theo m&aacute;y c&oacute; chất lượng rất tốt, kết nối Bluetooth,  USB, WAP 2.0 v&agrave; hỗ trợ gửi/nhận email. M&aacute;y được trang bị bộ nhớ trong 20  MB, hỗ trợ thẻ nhớ mở rộng microSD dung lượng tối đa 8 GB. M&aacute;y hỗ trợ  Bletooth V2.0 cho chuyển dữ liệu, tai nghe stereo, kết nối với điện  thoại v&agrave; c&aacute;c thiết bị di động kh&aacute;c.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="410" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/14/3B/samsung-c3212-.jpg" border="1" alt="" width="400" height="267" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Samsung C3212 c&oacute; gi&aacute; tham khảo 2.150.000 đồng</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>Samsung C3212</strong> c&oacute; thiết kế dạng thanh  mỏng v&agrave; nhỏ gọn (111,6 x 47 x 14,2 mm). M&agrave;n h&igrave;nh 2 inch trang bị QQVGA  65K m&agrave;u TFT gi&uacute;p hiển thị r&otilde; nội dung. C3212 cung cấp c&aacute;c t&iacute;nh năng bảo  mật, như cuộc gọi ảo v&agrave; tin nhắn khẩn cấp.</p>\r\n<p class="Normal">Tr&igrave;nh chơi nhạc đa định dạng  MP2/AAC/AAC+/eAAC+/WMA/AMR/MIDI/SP-MIDI/i-melody/WAV với khả năng chơi  nhạc nền tiếp tục chơi nhạc trong khi người d&ugrave;ng truy cập c&aacute;c ứng dụng  kh&aacute;c tr&ecirc;n điện thoại. FM với ghi &acirc;m AAC cho ph&eacute;p bạn đặt b&aacute;o thức v&agrave;  nhạc chu&ocirc;ng FM.</p>\r\n<p class="Normal">M&aacute;y ảnh VGA Bluetooth 2.1 mang đến kết nối kh&ocirc;ng d&acirc;y  cho chuyển dữ liệu, tai nghe stereo, kết nối với c&aacute;c điện thoại v&agrave; c&aacute;c  thiết bị di động kh&aacute;c. Tin nhắn Bluetooth gi&uacute;p người d&ugrave;ng gửi tin nhắn  tức th&igrave; đến người kh&aacute;c m&agrave; kh&ocirc;ng mất ph&iacute; ở khoảng c&aacute;ch gần.</p>\r\n<p class="Normal">USB 2.0 Tốc độ cao cho ph&eacute;p truyền tải file một c&aacute;ch  nhanh ch&oacute;ng, dễ d&agrave;ng v&agrave; kết nối với m&aacute;y in kh&ocirc;ng d&acirc;y. Danh bạ 1.000 số  (10 trường) gi&uacute;p người d&ugrave;ng giữ được tất cả số li&ecirc;n lạc ở một nơi thuận  tiện. Sản phẩm c&oacute; năng nhận cả hai cuộc gọi ở cả 2 SIM kh&aacute;c nhau c&ugrave;ng  một thời điểm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="409" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/14/3B/m520.jpg" border="1" alt="" width="400" height="267" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mobell M520 c&oacute; gi&aacute; b&aacute;n lẻ tham khảo l&agrave; 910.000 đồng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>Mobell M520</strong> c&oacute; thiết kế kiểu d&aacute;ng m&aacute;y  kh&aacute; nhỏ gọn, chắc chắn, hỗ trợ ch&acirc;n cắm tai nghe 3,5 mm phổ biến, cho  chất lượng nghe nhạc MP3 trung thực. M&aacute;y hỗ trợ chức năng chặn cuộc gọi  v&agrave; giả m&ocirc;i trường &acirc;m thanh cuộc gọi. &Acirc;m thanh cuộc gọi to v&agrave; r&otilde;. Việc  chuyển đổi c&aacute;c danh mục menu cực nhanh, cho cảm gi&aacute;c m&aacute;y chạy rất  "nuột".</p>\r\n<p class="Normal">Dung lượng pin 1.800 mAh n&ecirc;n độ bền của pin kh&aacute; cao.  Sử dụng b&igrave;nh thường được từ 5 đến 15 ng&agrave;y. Tuy nhi&ecirc;n, pin vẫn c&oacute; thể hết  ngay trong ng&agrave;y v&igrave; thời gian gọi li&ecirc;n tục chỉ ở khoảng 10 giờ.</p>\r\n<p class="Normal">Nhược điểm, kh&ocirc;ng nhận được cả hai cuộc gọi ở cả 2 SIM kh&aacute;c nhau c&ugrave;ng một thời điểm gọi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="402" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="Image"><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/14/3B/Q218.jpg" border="1" alt="" width="400" height="266" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Q-mobile Q218 c&oacute; gi&aacute; 910.000 đồng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>Q-mobile Q218</strong> c&oacute; ki&ecirc;̉u dáng thời  trang, phong cách là &acirc;́n tượng. Máy có 3 m&agrave;u đỏ, vàng và đen.  Toàn th&acirc;n được bảo v&ecirc;̣ bởi lớp vỏ chắc chắn, Q218 cho cảm giác  c&acirc;̀m đ&acirc;̀m tay nhưng khá nhẹ. Mặt trước của máy c&oacute; màn hình QVGA  r&ocirc;̣ng 1,8 inch nằm gọn dưới b&ecirc;̀ mặt tr&aacute;ng gương sáng bóng.</p>\r\n<p class="Normal">Q218 h&ocirc;̃ trợ đ&acirc;̀y đủ các tính năng giải trí v&agrave;  giao tiếp cơ bản. Chương trình nghe nhạc và xem video tr&ecirc;n máy khá  hoàn chỉnh. Máy cho phép truy nhập nhanh v&agrave;o trình nghe nhạc  th&ocirc;ng qua m&ocirc;̣t phím b&acirc;́m, tùy chọn hi&ecirc;̣u ứng &acirc;m thanh, cài đặt  danh sách, ch&ecirc;́ đ&ocirc;̣ chơi nhạc. Chương trình bắt sóng đài FM tr&ecirc;n  Q218 khá giản ti&ecirc;̣n nhưng đ&acirc;̀y đủ các tính năng. Máy h&ocirc;̃ trợ dò  tìm các k&ecirc;nh phát thanh, cài đặt mặc định cũng như khả năng lưu  và hẹn giờ lưu các chương trình y&ecirc;u thích.</p>\r\n<p class="Normal">M&ocirc;̣t trong những trang bị đáng giá của Qmobile  Q218 là pin của máy có dung lượng tới 1.800 mAh. Thử nghi&ecirc;̣m  thực t&ecirc;́ trong 3 ngày, 3 c&ocirc;̣t pin tr&ecirc;n máy chưa h&ecirc;̀ bị hao hụt,  model này hứa hẹn cho người dùng có th&ecirc;̉ gọi đi&ecirc;̣n, sử dụng  được hơn m&ocirc;̣t tu&acirc;̀n.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, Q218 kh&ocirc;ng c&oacute; khả năng nhận được cả 2 cuộc gọi ở cả 2 SIM kh&aacute;c nhau c&ugrave;ng một thời điểm gọi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="404" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/14/3B/M218.jpg" border="1" alt="" width="400" height="267" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mobell M218 c&oacute; gi&aacute; b&aacute;n lẻ tham khảo l&agrave; 500.000 đồng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>Mobell M218</strong> hiện l&agrave; mẫu  gi&aacute; rẻ nhất  thị trường di động 2 SIM, 2 s&oacute;ng. M218 được thiết kế dạng thanh nhỏ gọn.  M&agrave;n h&igrave;nh m&agrave;u TFT 65.536 sắc n&eacute;t.</p>\r\n<p class="Normal">M&aacute;y hỗ trợ radio FM, nghe nhạc MP3 v&agrave; c&oacute; đ&egrave;n pin với  n&uacute;t bật tắt tiện lợi. M218 kh&ocirc;ng mang camera chụp ảnh. Pin dung lượng  cao l&ecirc;n tới 1.000 mAh. M218 c&oacute; thể chặn cuộc gọi.</p>\r\n<p class="Normal">Nhược điểm: Kh&ocirc;ng nhận được cả hai cuộc gọi ở cả hai SIM kh&aacute;c nhau c&ugrave;ng một thời điểm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="409" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://sohoa.vnexpress.net/Files/Subject/3B/9B/14/3B/Q-mobile_ME01.jpg" border="1" alt="" width="400" height="267" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Q-mobile ME01 c&oacute; gi&aacute; b&aacute;n lẻ tham khảo 990.000 đồng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>Q-mobile ME01</strong> thiết kế dạng bar với vỏ ngo&agrave;i bằng th&eacute;p kh&ocirc;ng rỉ v&agrave; nhiều m&agrave;u sắc.</p>\r\n<p class="Normal">Về t&iacute;nh năng, ME01 c&oacute; thể chat qua Yahoo, Facebook với  sự hỗ kết nối qua GPRS, b&ecirc;n cạnh chức năng nghe nhạc MP3/MP4 v&agrave; đ&agrave;i  radio FM &acirc;m thanh nổi. M&aacute;y c&oacute; b&agrave;n ph&iacute;m QWERTY rất dễ sử dụng, chức năng  đọc số cuộc gọi đến v&agrave; đi tiện lợi.</p>\r\n<p class="Normal">ME01 cũng kh&ocirc;ng nhận được cả hai cuộc gọi ở hai SIM kh&aacute;c nhau c&ugrave;ng một thời điểm.</p>\r\n<p class="Normal">Những sản phẩm tr&ecirc;n đang b&aacute;n tại c&ocirc;ng ty TNHH Điểm  S&aacute;ng Việt 581 Kim M&atilde;, Ba Đ&igrave;nh, H&agrave; Nội) - Tel: (04) 3 766.5789 / (04) 2  215.6839.</p>\r\n<p class="Normal">Từ ng&agrave;y 25/11 đến 31/12, Điểm S&aacute;ng Việt &aacute;p dụng chương  tr&igrave;nh khuyến m&atilde;i cho Philips Xenium X710 (tặng thẻ nhớ dung lượng 2 GB)  v&agrave; Samsung C3212 (tặng thẻ dịch vụ data GPRS/EDGE ưu đ&atilde;i trong v&ograve;ng 6  th&aacute;ng kể từ khi k&iacute;ch hoạt, miễn ph&iacute; thu&ecirc; bao 50 ngh&igrave;n mỗi th&aacute;ng, miễn  ph&iacute; 300 MB một th&aacute;ng). Miễn ph&iacute; cước vận chuyển b&aacute;n h&agrave;ng to&agrave;n quốc.  Th&ocirc;ng tin chi tiết xem tại: <a class="Normal" href="http://www.diemsangviet.net/">www.diemsangviet.net</a>.</p>\r\n<p class="Normal">(Nguồn: <em>Điểm S&aacute;ng Việt</em>)</p>\r\n<div class="cmt-header"><a name="aComment">&Yacute; kiến độc giả</a> (<label style="color: #0033ff;">8</label>)</div>\r\n<div class="comment_ct">\r\n<p class="Title">Philips X710</p>\r\n<p class="Normal">Kh&ocirc;ng biết c&oacute; sự nhầm lẫn ở đ&acirc;y kh&ocirc;ng nhưng theo m&igrave;nh  biết Phil&iacute;p X710 c&oacute; 2 khe cắm thẻ sim nhưng chỉ được chọn 1 trong 2 hoặc  l&agrave; A hoặc l&agrave; B giống như sim gh&eacute;p hồi xưa khỏi mắc c&ocirc;ng thay sim th&ocirc;i  chứ kh&ocirc;ng nhận được hai cuộc gọi một l&uacute;c như Điểm S&aacute;ng Việt n&oacute;i v&agrave; n&oacute;  chỉ c&oacute; một cột s&oacute;ng th&ocirc;i. M&igrave;nh đ&atilde; gh&eacute; Viễn Th&ocirc;ngA t&iacute;nh mua nhưng thử m&aacute;y  kh&ocirc;ng như Điểm S&aacute;ng Việt n&oacute;i n&ecirc;n th&ocirc;i kh&ocirc;ng mua nữa!</p>\r\n<p class="Normal"><em>( Minh map - nguyenngocminh2003@yahoo.com )</em></p>\r\n</div>\r\n<hr />\r\n<p class="Title">X710 nhận được 2 cuộc gọi c&ugrave;ng l&uacute;c</p>\r\n<p class="Normal">M&igrave;nh đang d&ugrave;ng 1 ch&uacute; X710 pin rất t&ocirc;t, M&aacute;y cắm được 2  sim tr&ecirc;n 2 khe cắm hiện thị 2 cột s&oacute;ng v&agrave; thực hiện 2 cuộc gọi c&ugrave;ng l&uacute;c,  trước đ&acirc;y m&igrave;nh d&ugrave;ng một m&aacute;y 2 sim nhưng tại một thời điểm chỉ nhận được  1 cuộc gọi c&ograve;n cuộc gọi đến Sim 2 bị b&aacute;o tắt m&aacute;y hoặc kh&ocirc;ng li&ecirc;n lạc  được n&ecirc;n giờ đổi sang d&ugrave;ng Philips X710 pin bền s&oacute;ng khoẻ t&iacute;nh năng  camera v&agrave; đồng bộ t&ocirc;t với m&aacute;y t&iacute;nh</p>', 0, 200, NULL, 0, 1);
INSERT INTO `sanpham` VALUES (674, 49, 1, 'Mobell M390 red ', 'Mobell M390 red  \r\nMobell M390 - Nắp gập thời trang\r\n- Camera VGA\r\n- Hỗ trợ thẻ nhớ ngoài\r\n- Nghe nhạc nhiều định dạng\r\n- Ghi âm cuộc gọi và môi trường\r\n- Loa thoại rảnh tay ', '0000-00-00', 2500000, 'bMobell-M3902.jpg', '<div class="img_details_content">\r\n<div id="photos" class="galleryview">\r\n<div style="width: 623px; height: auto; overflow: hidden;">\r\n<div class="panel"><img id="imgpanel" src="http://thumbnail2.xalo.vn/Image/?url=http://news.ictexpress.com/wp-content/uploads/2009/10/ICT-Express-Mobell-M390.jpg&amp;w=623&amp;v=9ae5c4f61ec41caf7bfcb2f31c286065" border="0" alt="ICT Express &ndash; MOBELL M390 l&agrave; " /></div>\r\n</div>\r\n</div>\r\n<div class="img_details_title2">\r\n<h4 style="margin: 10px 0pt 0pt; padding: 0pt; text-align: center; font-size: 12px;">sản phẩm rao vặt cần mua b&aacute;n</h4>\r\n<span id="wh" style="margin: 0pt; padding: 0pt; font-style: italic; color: #00f8d8;">K&iacute;ch thước: 400 x 400</span></div>\r\n</div>\r\n<div class="notice">Ảnh <strong>"<span class="img_name">ICT Express &ndash; MOBELL M390 l&agrave; </span>"</strong>được m&aacute;y t&igrave;m kiếm Xa Lộ tự động qu&eacute;t tr&ecirc;n mạng 		Nếu thấy ảnh c&oacute; nội dung xấu, xin bạn vui l&ograve;ng bấm chọn th&ocirc;ng b&aacute;o ảnh vi phạm.</div>', 0, 200, NULL, 0, 1);
